// Artifact Logger - Logs important artifacts and governance decisions with persistence
const fs = require('node:fs').promises;
const path = require('node:path');
const crypto = require('node:crypto');

class ArtifactLogger {
  constructor() {
    this.artifacts = [];
    this.artifactsDir = path.join(process.cwd(), 'artifacts', 'logs');
    this.indexFile = path.join(this.artifactsDir, 'index.json');
    this.loadIndex();
  }

  /**
   * Load artifact index from disk
   */
  async loadIndex() {
    try {
      const data = await fs.readFile(this.indexFile, 'utf8');
      this.artifacts = JSON.parse(data);
    } catch (err) {
      console.log('Artifact index not found, creating new:', err.message);
      this.artifacts = [];
    }
  }

  /**
   * Save artifact index to disk
   */
  async saveIndex() {
    try {
      await fs.mkdir(this.artifactsDir, { recursive: true });
      await fs.writeFile(this.indexFile, JSON.stringify(this.artifacts, null, 2));
    } catch (err) {
      console.error('Failed to save artifact index:', err);
    }
  }

  /**
   * Log an artifact
   */
  async log(type, data, metadata = {}) {
    const id = crypto.randomBytes(16).toString('hex');
    const artifact = {
      id,
      type,
      data,
      metadata,
      timestamp: new Date().toISOString(),
      version: this.artifacts.length + 1
    };
    
    this.artifacts.push(artifact);
    
    // Write individual artifact file
    const artifactFile = path.join(this.artifactsDir, `${id}.json`);
    await fs.mkdir(this.artifactsDir, { recursive: true });
    await fs.writeFile(artifactFile, JSON.stringify(artifact, null, 2));
    
    // Update index
    await this.saveIndex();
    
    console.log(`[ArtifactLogger] Logged: ${type} (${id})`);
    
    return artifact;
  }

  /**
   * Get all artifacts of a specific type
   */
  getByType(type) {
    return this.artifacts.filter(a => a.type === type);
  }

  /**
   * Get all artifacts
   */
  getAll() {
    return this.artifacts;
  }

  /**
   * Get artifact by ID
   */
  getById(id) {
    return this.artifacts.find(a => a.id === id) || null;
  }

  /**
   * Get recent artifacts
   */
  getRecent(limit = 10) {
    return this.artifacts.slice(-limit).reverse();
  }

  /**
   * Clear all artifacts (use with caution)
   */
  clear() {
    this.artifacts = [];
    console.log('[ArtifactLogger] All artifacts cleared');
  }
}

module.exports = new ArtifactLogger();

// Governance Notary - Immutable audit trail for governance decisions
const crypto = require('node:crypto');
const fs = require('node:fs').promises;
const path = require('node:path');

class GovernanceNotary {
  constructor() {
    this.ledger = [];
    this.version = 0;
    this.ledgerFile = path.join(process.cwd(), 'artifacts', 'governance-ledger.json');
    this.loadLedger();
  }

  /**
   * Load ledger from disk
   */
  async loadLedger() {
    try {
      const data = await fs.readFile(this.ledgerFile, 'utf8');
      const saved = JSON.parse(data);
      this.ledger = saved.ledger || [];
      this.version = saved.version || 0;
    } catch (err) {
      // File doesn't exist yet, start fresh
      this.ledger = [];
      this.version = 0;
    }
  }

  /**
   * Save ledger to disk
   */
  async saveLedger() {
    try {
      await fs.mkdir(path.dirname(this.ledgerFile), { recursive: true });
      await fs.writeFile(this.ledgerFile, JSON.stringify({
        version: this.version,
        ledger: this.ledger,
        lastUpdated: new Date().toISOString()
      }, null, 2));
    } catch (err) {
      console.error('Failed to save governance ledger:', err);
    }
  }

  /**
   * Inscribe a governance action
   */
  inscribe(action, actor, justification) {
    this.version++;
    const prevHash = this.ledger.length > 0 ? this.ledger[this.ledger.length - 1].hash : '0';
    
    const entry = {
      version: this.version,
      timestamp: new Date().toISOString(),
      action,
      actor,
      justification,
      prevHash,
      hash: this.generateHash(this.version, action, actor, justification, prevHash)
    };
    
    this.ledger.push(entry);
    this.saveLedger(); // Fire and forget
    return entry;
  }

  /**
   * Get full ledger
   */
  getLedger() {
    return this.ledger;
  }

  /**
   * Verify ledger integrity
   */
  verifyIntegrity() {
    for (let i = 0; i < this.ledger.length; i++) {
      const entry = this.ledger[i];
      const prevHash = i > 0 ? this.ledger[i - 1].hash : '0';
      const expectedHash = this.generateHash(
        entry.version,
        entry.action,
        entry.actor,
        entry.justification,
        prevHash
      );
      
      if (entry.hash !== expectedHash) {
        return { valid: false, tampered: i };
      }
    }
    return { valid: true };
  }

  /**
   * Generate SHA256 hash
   */
  generateHash(version, action, actor, justification, prevHash) {
    const data = `${version}:${action}:${actor}:${justification}:${prevHash}`;
    return crypto.createHash('sha256').update(data).digest('hex');
  }
}

module.exports = new GovernanceNotary();

const fs = require('fs');

const data = JSON.parse(fs.readFileSync('eslint-results.json', 'utf8'));
let totalErrors = 0;
let totalWarnings = 0;
let filesWithProblems = [];
let allProblems = [];

data.forEach(f => {
  const errors = f.errorCount || 0;
  const warnings = f.warningCount || 0;
  totalErrors += errors;
  totalWarnings += warnings;

  if (errors > 0 || warnings > 0) {
    const shortPath = f.filePath.replace(/^.*FORTHEWEEBS[\\/]/, '');
    filesWithProblems.push({
      path: shortPath,
      errors,
      warnings,
      messages: f.messages || []
    });

    // Collect all problem details
    (f.messages || []).forEach(msg => {
      allProblems.push({
        file: shortPath,
        line: msg.line,
        column: msg.column,
        severity: msg.severity === 2 ? 'error' : 'warning',
        message: msg.message,
        ruleId: msg.ruleId
      });
    });
  }
});

console.log('=== ESLINT ANALYSIS SUMMARY ===\n');
console.log('Total Errors:', totalErrors);
console.log('Total Warnings:', totalWarnings);
console.log('Total Problems:', totalErrors + totalWarnings);
console.log('Files with problems:', filesWithProblems.length);

console.log('\n=== TOP 30 FILES WITH MOST PROBLEMS ===\n');
filesWithProblems
  .sort((a, b) => (b.errors + b.warnings) - (a.errors + a.warnings))
  .slice(0, 30)
  .forEach((f, idx) => {
    console.log(`${idx + 1}. ${f.path}`);
    console.log(`   Errors: ${f.errors}, Warnings: ${f.warnings}, Total: ${f.errors + f.warnings}`);
  });

console.log('\n=== MOST COMMON ISSUES (Top 20) ===\n');
const ruleCounts = {};
allProblems.forEach(p => {
  if (p.ruleId) {
    ruleCounts[p.ruleId] = (ruleCounts[p.ruleId] || 0) + 1;
  }
});

Object.entries(ruleCounts)
  .sort((a, b) => b[1] - a[1])
  .slice(0, 20)
  .forEach(([rule, count], idx) => {
    console.log(`${idx + 1}. ${rule}: ${count} occurrences`);
  });

// Save detailed report
fs.writeFileSync('eslint-detailed-report.json', JSON.stringify({
  summary: {
    totalErrors,
    totalWarnings,
    totalProblems: totalErrors + totalWarnings,
    filesWithProblems: filesWithProblems.length
  },
  filesWithProblems: filesWithProblems.slice(0, 50),
  commonIssues: Object.entries(ruleCounts).sort((a, b) => b[1] - a[1]).slice(0, 30)
}, null, 2));

console.log('\n=== Detailed report saved to eslint-detailed-report.json ===');

/**
 * Artifact Logger: Sovereign action tracking and immortalized event logging
 * Every significant action becomes a queryable artifact
 */

import { createClient, SupabaseClient } from '@supabase/supabase-js';

const supabase: SupabaseClient = createClient(
  process.env.SUPABASE_URL || '',
  process.env.SUPABASE_SERVICE_KEY || ''
);

export type AgentType = 'mico' | 'policy_engine' | 'moderation' | 'compose' | 'entitlements' | 'system' | 'moderation_sentinel' | 'content_companion' | 'automation_clerk' | 'profile_architect' | 'legacy_archivist';
export type AuthorityLevel = 'user' | 'creator' | 'moderator' | 'admin' | 'owner' | 'sovereign' | 'suggest' | 'act' | 'read' | 'enforce';
export type ArtifactSeverity = 'info' | 'warning' | 'error' | 'critical';

export interface ArtifactLog {
  timestamp?: Date;
  agentType?: AgentType;
  agent?: AgentType;
  authority?: AuthorityLevel;
  authorityLevel?: AuthorityLevel;
  action: string;
  entityType?: string;
  entityId?: string;
  context?: Record<string, any>;
  result?: Record<string, any>;
  details?: Record<string, any>;
  severity?: ArtifactSeverity;
  userId?: string;
}

/**
 * Log an artifact to the immortalized event log
 */
export async function logArtifact(artifact: ArtifactLog): Promise<void> {
  try {
    if (!process.env.SUPABASE_URL || !process.env.SUPABASE_SERVICE_KEY) {
      console.warn('‚ö†Ô∏è Artifact logging disabled - Supabase not configured');
      return;
    }

    const { error } = await supabase.from('artifacts').insert({
      timestamp: (artifact.timestamp || new Date()).toISOString(),
      agent: artifact.agentType || artifact.agent,
      authority: artifact.authorityLevel || artifact.authority,
      action: artifact.action,
      entity_type: artifact.entityType,
      entity_id: artifact.entityId,
      details: artifact.details || artifact.context || artifact.result,
      severity: artifact.severity || 'info',
      user_id: artifact.userId,
    });

    if (error) {
      console.error('Failed to log artifact:', error);
    }
  } catch (err) {
    console.error('Artifact logging error:', err);
  }
}

/**
 * Query artifacts by filters
 */
export async function queryArtifacts(filters: {
  agent?: AgentType;
  authority?: AuthorityLevel;
  severity?: ArtifactSeverity;
  userId?: string;
  startTime?: Date;
  endTime?: Date;
  limit?: number;
}): Promise<ArtifactLog[]> {
  try {
    let query = supabase.from('artifacts').select('*');

    if (filters.agent) query = query.eq('agent', filters.agent);
    if (filters.authority) query = query.eq('authority', filters.authority);
    if (filters.severity) query = query.eq('severity', filters.severity);
    if (filters.userId) query = query.eq('user_id', filters.userId);
    if (filters.startTime) query = query.gte('timestamp', filters.startTime.toISOString());
    if (filters.endTime) query = query.lte('timestamp', filters.endTime.toISOString());

    query = query.order('timestamp', { ascending: false });
    query = query.limit(filters.limit || 100);

    const { data, error } = await query;

    if (error) throw error;

    return (data || []).map((row: any) => ({
      timestamp: new Date(row.timestamp),
      agent: row.agent,
      authority: row.authority,
      action: row.action,
      entityType: row.entity_type,
      entityId: row.entity_id,
      details: row.details,
      severity: row.severity,
      userId: row.user_id,
    }));
  } catch (err) {
    console.error('Failed to query artifacts:', err);
    return [];
  }
}

/**
 * Get artifact count by agent
 */
export async function getArtifactStats(): Promise<Record<AgentType, number>> {
  try {
    const { data, error } = await supabase
      .from('artifacts')
      .select('agent')
      .order('timestamp', { ascending: false });

    if (error) throw error;

    const stats: Record<string, number> = {};
    (data || []).forEach((row: any) => {
      stats[row.agent] = (stats[row.agent] || 0) + 1;
    });

    return stats as Record<AgentType, number>;
  } catch (err) {
    console.error('Failed to get artifact stats:', err);
    return {} as Record<AgentType, number>;
  }
}

/**
 * Content Companion: AI-powered inline content assistance
 * Captions, translations, hashtags, descriptions, alt text
 */

import { createClient, SupabaseClient } from '@supabase/supabase-js';
import OpenAI from 'openai';
import { logArtifact } from './artifactLogger';
import { canPerformAction, getAuthority } from './policy';

const supabase: SupabaseClient = createClient(
  process.env.SUPABASE_URL!,
  process.env.SUPABASE_SERVICE_KEY!
);

const openai = new OpenAI({ apiKey: process.env.OPENAI_API_KEY });

export interface ComposeRequest {
  userId: string;
  requestType: 'caption' | 'translate' | 'hashtags' | 'description' | 'alt_text';
  content: string;
  context?: {
    targetLanguage?: string;
    tone?: 'casual' | 'professional' | 'playful' | 'edgy';
    maxLength?: number;
    mediaUrl?: string;
  };
}

export interface ComposeResult {
  generatedContent: string;
  alternatives?: string[];
  artifactUrl?: string;
  usageCount?: number;
  limitRemaining?: number;
}

/**
 * Generate caption for post
 */
export async function generateCaption(
  request: ComposeRequest
): Promise<ComposeResult> {
  // Check entitlements
  const canUse = await checkEntitlement(request.userId, 'ai_assist_calls');
  if (!canUse.allowed) {
    throw new Error(`Entitlement limit reached: ${canUse.reason}`);
  }

  // Check policy
  const permission = await canPerformAction('content_companion', 'generate_caption');
  if (!permission.allowed) {
    throw new Error(`Permission denied: ${permission.reason}`);
  }

  const tone = request.context?.tone || 'casual';
  const maxLength = request.context?.maxLength || 280;

  const completion = await openai.chat.completions.create({
    model: 'gpt-4-turbo-preview',
    messages: [
      {
        role: 'system',
        content: `You are a creative content companion for ForTheWeebs, a media platform. Generate engaging ${tone} captions for user posts. Keep it under ${maxLength} characters. Be authentic and match the creator's voice.`,
      },
      {
        role: 'user',
        content: `Generate a caption for this content: "${request.content}"`,
      },
    ],
    max_tokens: 150,
    n: 3, // Generate 3 alternatives
  });

  const generatedContent = completion.choices[0].message.content || '';
  const alternatives = completion.choices
    .slice(1)
    .map((c) => c.message.content || '');

  // Log artifact
  const artifactId = await logArtifact({
    agentType: 'content_companion',
    action: 'generate_caption',
    entityType: 'user_post',
    entityId: request.userId,
    context: { originalContent: request.content.substring(0, 100), tone },
    result: { generatedContent, alternatives },
    authorityLevel: (await getAuthority('content_companion')).authorityLevel,
  });

  // Track usage
  await trackUsage(request.userId, 'ai_assist_calls');

  const usage = await getUsage(request.userId, 'ai_assist_calls');

  return {
    generatedContent,
    alternatives,
    artifactUrl: `${process.env.APP_URL}/admin/artifacts/${artifactId}`,
    usageCount: usage.current,
    limitRemaining: usage.remaining,
  };
}

/**
 * Translate content to target language
 */
export async function translateContent(
  request: ComposeRequest
): Promise<ComposeResult> {
  const canUse = await checkEntitlement(request.userId, 'ai_assist_calls');
  if (!canUse.allowed) {
    throw new Error(`Entitlement limit reached: ${canUse.reason}`);
  }

  const permission = await canPerformAction('content_companion', 'translate_content');
  if (!permission.allowed) {
    throw new Error(`Permission denied: ${permission.reason}`);
  }

  const targetLanguage = request.context?.targetLanguage || 'es';

  const completion = await openai.chat.completions.create({
    model: 'gpt-4-turbo-preview',
    messages: [
      {
        role: 'system',
        content: `You are a translation assistant. Translate content to ${targetLanguage} while preserving tone, slang, and cultural context. Keep it natural.`,
      },
      {
        role: 'user',
        content: request.content,
      },
    ],
    max_tokens: 500,
  });

  const generatedContent = completion.choices[0].message.content || '';

  const artifactId = await logArtifact({
    agentType: 'content_companion',
    action: 'translate_content',
    entityType: 'user_post',
    entityId: request.userId,
    context: { originalContent: request.content.substring(0, 100), targetLanguage },
    result: { generatedContent },
    authorityLevel: (await getAuthority('content_companion')).authorityLevel,
  });

  await trackUsage(request.userId, 'ai_assist_calls');
  const usage = await getUsage(request.userId, 'ai_assist_calls');

  return {
    generatedContent,
    artifactUrl: `${process.env.APP_URL}/admin/artifacts/${artifactId}`,
    usageCount: usage.current,
    limitRemaining: usage.remaining,
  };
}

/**
 * Suggest hashtags for post
 */
export async function suggestHashtags(
  request: ComposeRequest
): Promise<ComposeResult> {
  const canUse = await checkEntitlement(request.userId, 'ai_assist_calls');
  if (!canUse.allowed) {
    throw new Error(`Entitlement limit reached: ${canUse.reason}`);
  }

  const permission = await canPerformAction('content_companion', 'suggest_hashtags');
  if (!permission.allowed) {
    throw new Error(`Permission denied: ${permission.reason}`);
  }

  const completion = await openai.chat.completions.create({
    model: 'gpt-4-turbo-preview',
    messages: [
      {
        role: 'system',
        content: 'You are a social media expert. Generate 5-10 relevant, trending hashtags for content. Mix popular and niche tags. Return as comma-separated list.',
      },
      {
        role: 'user',
        content: request.content,
      },
    ],
    max_tokens: 100,
  });

  const generatedContent = completion.choices[0].message.content || '';

  const artifactId = await logArtifact({
    agentType: 'content_companion',
    action: 'suggest_hashtags',
    entityType: 'user_post',
    entityId: request.userId,
    context: { content: request.content.substring(0, 100) },
    result: { generatedContent },
    authorityLevel: (await getAuthority('content_companion')).authorityLevel,
  });

  await trackUsage(request.userId, 'ai_assist_calls');
  const usage = await getUsage(request.userId, 'ai_assist_calls');

  return {
    generatedContent,
    artifactUrl: `${process.env.APP_URL}/admin/artifacts/${artifactId}`,
    usageCount: usage.current,
    limitRemaining: usage.remaining,
  };
}

/**
 * Generate alt text for media
 */
export async function generateAltText(
  request: ComposeRequest
): Promise<ComposeResult> {
  if (!request.context?.mediaUrl) {
    throw new Error('Media URL required for alt text generation');
  }

  const canUse = await checkEntitlement(request.userId, 'ai_assist_calls');
  if (!canUse.allowed) {
    throw new Error(`Entitlement limit reached: ${canUse.reason}`);
  }

  const permission = await canPerformAction('content_companion', 'generate_alt_text');
  if (!permission.allowed) {
    throw new Error(`Permission denied: ${permission.reason}`);
  }

  const completion = await openai.chat.completions.create({
    model: 'gpt-4-vision-preview',
    messages: [
      {
        role: 'system',
        content: 'Generate concise, descriptive alt text for accessibility. Focus on key visual elements and context. Keep under 125 characters.',
      },
      {
        role: 'user',
        content: [
          { type: 'text', text: 'Describe this image for alt text:' },
          { type: 'image_url', image_url: { url: request.context.mediaUrl } },
        ],
      },
    ],
    max_tokens: 100,
  });

  const generatedContent = completion.choices[0].message.content || '';

  const artifactId = await logArtifact({
    agentType: 'content_companion',
    action: 'generate_alt_text',
    entityType: 'media',
    entityId: request.userId,
    context: { mediaUrl: request.context.mediaUrl },
    result: { generatedContent },
    authorityLevel: (await getAuthority('content_companion')).authorityLevel,
  });

  await trackUsage(request.userId, 'ai_assist_calls');
  const usage = await getUsage(request.userId, 'ai_assist_calls');

  return {
    generatedContent,
    artifactUrl: `${process.env.APP_URL}/admin/artifacts/${artifactId}`,
    usageCount: usage.current,
    limitRemaining: usage.remaining,
  };
}

/**
 * Check user entitlement for AI assist
 */
async function checkEntitlement(
  userId: string,
  entitlementKey: string
): Promise<{ allowed: boolean; reason?: string }> {
  const { data: entitlement } = await supabase
    .from('user_entitlements')
    .select('*')
    .eq('user_id', userId)
    .eq('entitlement_key', entitlementKey)
    .single();

  if (!entitlement) {
    return { allowed: false, reason: 'No entitlement found' };
  }

  // Check if expired
  if (entitlement.expires_at && new Date(entitlement.expires_at) < new Date()) {
    return { allowed: false, reason: 'Entitlement expired' };
  }

  // Check usage limit
  const limit = entitlement.entitlement_value?.limit || 0;
  if (limit === -1) return { allowed: true }; // Unlimited

  const usage = await getUsage(userId, entitlementKey);
  if (usage.current >= limit) {
    return { allowed: false, reason: `Usage limit reached: ${usage.current}/${limit}` };
  }

  return { allowed: true };
}

/**
 * Track usage
 */
async function trackUsage(userId: string, metricKey: string): Promise<void> {
  const now = new Date();
  const periodStart = new Date(now.getFullYear(), now.getMonth(), 1); // Start of month
  const periodEnd = new Date(now.getFullYear(), now.getMonth() + 1, 0); // End of month

  const { error } = await supabase.rpc('increment_usage', {
    p_user_id: userId,
    p_metric_key: metricKey,
    p_period_start: periodStart.toISOString(),
    p_period_end: periodEnd.toISOString(),
  });

  if (error) {
    // Fallback: manual upsert
    const { data: existing } = await supabase
      .from('usage_tracking')
      .select('*')
      .eq('user_id', userId)
      .eq('metric_key', metricKey)
      .eq('period_start', periodStart.toISOString())
      .single();

    if (existing) {
      await supabase
        .from('usage_tracking')
        .update({ current_value: existing.current_value + 1 })
        .eq('id', existing.id);
    } else {
      await supabase.from('usage_tracking').insert({
        user_id: userId,
        metric_key: metricKey,
        current_value: 1,
        period_start: periodStart.toISOString(),
        period_end: periodEnd.toISOString(),
      });
    }
  }
}

/**
 * Get current usage
 */
async function getUsage(
  userId: string,
  metricKey: string
): Promise<{ current: number; limit: number; remaining: number }> {
  const now = new Date();
  const periodStart = new Date(now.getFullYear(), now.getMonth(), 1);

  const { data: usage } = await supabase
    .from('usage_tracking')
    .select('*')
    .eq('user_id', userId)
    .eq('metric_key', metricKey)
    .eq('period_start', periodStart.toISOString())
    .single();

  const { data: entitlement } = await supabase
    .from('user_entitlements')
    .select('*')
    .eq('user_id', userId)
    .eq('entitlement_key', metricKey)
    .single();

  const limit = entitlement?.entitlement_value?.limit || 0;
  const current = usage?.current_value || 0;
  const remaining = limit === -1 ? -1 : Math.max(0, limit - current);

  return { current, limit, remaining };
}

export default {
  generateCaption,
  translateContent,
  suggestHashtags,
  generateAltText,
};

/**
 * Entitlements Service: Feature gates and Stripe subscription mirroring
 * Manages what users can do based on their tier
 */

import { createClient, SupabaseClient } from '@supabase/supabase-js';
import Stripe from 'stripe';
import { logArtifact } from './artifactLogger';

const supabase: SupabaseClient = createClient(
  process.env.SUPABASE_URL!,
  process.env.SUPABASE_SERVICE_KEY!
);

const stripe = new Stripe(process.env.STRIPE_SECRET_KEY!, {
  apiVersion: '2025-10-29.clover' as any,
});

export type Tier = 'free' | 'creator' | 'pro' | 'enterprise';

export interface Entitlement {
  key: string;
  value: any;
  source: 'manual' | 'stripe' | 'trial' | 'promo';
  expiresAt?: Date;
}

/**
 * Get user's current tier from Stripe
 */
export async function getUserTier(userId: string): Promise<Tier> {
  const { data: entitlement } = await supabase
    .from('user_entitlements')
    .select('*')
    .eq('user_id', userId)
    .eq('entitlement_key', 'subscription_tier')
    .single();

  if (!entitlement) return 'free';

  // Check if expired
  if (entitlement.expires_at && new Date(entitlement.expires_at) < new Date()) {
    return 'free';
  }

  return (entitlement.entitlement_value?.tier as Tier) || 'free';
}

/**
 * Check if user has entitlement
 */
export async function hasEntitlement(
  userId: string,
  entitlementKey: string
): Promise<boolean> {
  const tier = await getUserTier(userId);

  // Get entitlement definition
  const { data: definition } = await supabase
    .from('entitlement_definitions')
    .select('*')
    .eq('entitlement_key', entitlementKey)
    .single();

  if (!definition) return false;

  const tierValue = definition.tiers[tier];

  // Handle boolean entitlements
  if (typeof tierValue === 'boolean') return tierValue;

  // Handle numeric entitlements
  if (typeof tierValue === 'number') {
    return tierValue > 0 || tierValue === -1; // -1 means unlimited
  }

  return false;
}

/**
 * Get entitlement value for user
 */
export async function getEntitlement(
  userId: string,
  entitlementKey: string
): Promise<any> {
  const tier = await getUserTier(userId);

  const { data: definition } = await supabase
    .from('entitlement_definitions')
    .select('*')
    .eq('entitlement_key', entitlementKey)
    .single();

  if (!definition) return definition?.default_value;

  return definition.tiers[tier] ?? definition.default_value;
}

/**
 * Sync Stripe subscription to entitlements
 * Called by Stripe webhook
 */
export async function syncStripeSubscription(
  userId: string,
  stripeSubscriptionId: string
): Promise<void> {
  try {
    const subscription = await stripe.subscriptions.retrieve(stripeSubscriptionId);

    // Determine tier from price ID
    const priceId = subscription.items.data[0]?.price.id;
    const tier = mapPriceToTier(priceId);

    // Update subscription tier entitlement
    await supabase.from('user_entitlements').upsert({
      user_id: userId,
      entitlement_key: 'subscription_tier',
      entitlement_value: { tier },
      source: 'stripe',
      stripe_subscription_id: stripeSubscriptionId,
      expires_at: new Date((subscription as any).current_period_end * 1000).toISOString(),
    });

    // Get all entitlements for this tier
    const { data: definitions } = await supabase
      .from('entitlement_definitions')
      .select('*');

    // Sync all tier-based entitlements
    for (const def of definitions || []) {
      const value = def.tiers[tier];
      if (value !== undefined) {
        await supabase.from('user_entitlements').upsert({
          user_id: userId,
          entitlement_key: def.entitlement_key,
          entitlement_value: { limit: value, enabled: value },
          source: 'stripe',
          stripe_subscription_id: stripeSubscriptionId,
          expires_at: new Date((subscription as any).current_period_end * 1000).toISOString(),
        });
      }
    }

    await logArtifact({
      agentType: 'automation_clerk',
      action: 'sync_stripe_subscription',
      entityType: 'user',
      entityId: userId,
      context: { stripeSubscriptionId, tier },
      result: { success: true },
      authorityLevel: 'act',
    });

    console.log(`‚úÖ Synced Stripe subscription for user ${userId}: ${tier}`);
  } catch (error) {
    console.error('Failed to sync Stripe subscription:', error);
    throw error;
  }
}

/**
 * Handle Stripe subscription cancellation
 */
export async function handleSubscriptionCanceled(
  userId: string,
  stripeSubscriptionId: string
): Promise<void> {
  // Remove all Stripe-sourced entitlements
  await supabase
    .from('user_entitlements')
    .delete()
    .eq('user_id', userId)
    .eq('stripe_subscription_id', stripeSubscriptionId);

  await logArtifact({
    agentType: 'automation_clerk',
    action: 'cancel_stripe_subscription',
    entityType: 'user',
    entityId: userId,
    context: { stripeSubscriptionId },
    result: { success: true },
    authorityLevel: 'act',
  });

  console.log(`‚ùå Canceled Stripe subscription for user ${userId}`);
}

/**
 * Grant trial entitlements
 */
export async function grantTrial(
  userId: string,
  durationDays: number = 7
): Promise<void> {
  const expiresAt = new Date();
  expiresAt.setDate(expiresAt.getDate() + durationDays);

  // Grant pro tier trial
  await supabase.from('user_entitlements').insert({
    user_id: userId,
    entitlement_key: 'subscription_tier',
    entitlement_value: { tier: 'pro' },
    source: 'trial',
    expires_at: expiresAt.toISOString(),
  });

  // Get all pro tier entitlements
  const { data: definitions } = await supabase
    .from('entitlement_definitions')
    .select('*');

  for (const def of definitions || []) {
    const value = def.tiers['pro'];
    if (value !== undefined) {
      await supabase.from('user_entitlements').insert({
        user_id: userId,
        entitlement_key: def.entitlement_key,
        entitlement_value: { limit: value, enabled: value },
        source: 'trial',
        expires_at: expiresAt.toISOString(),
      });
    }
  }

  await logArtifact({
    agentType: 'automation_clerk',
    action: 'grant_trial',
    entityType: 'user',
    entityId: userId,
    context: { durationDays },
    result: { success: true, expiresAt },
    authorityLevel: 'act',
  });

  console.log(`üéÅ Granted ${durationDays}-day trial to user ${userId}`);
}

/**
 * Grant promo entitlement
 */
export async function grantPromo(
  userId: string,
  entitlementKey: string,
  value: any,
  durationDays?: number
): Promise<void> {
  const expiresAt = durationDays
    ? new Date(Date.now() + durationDays * 24 * 60 * 60 * 1000)
    : null;

  await supabase.from('user_entitlements').insert({
    user_id: userId,
    entitlement_key: entitlementKey,
    entitlement_value: value,
    source: 'promo',
    expires_at: expiresAt?.toISOString(),
  });

  await logArtifact({
    agentType: 'automation_clerk',
    action: 'grant_promo',
    entityType: 'user',
    entityId: userId,
    context: { entitlementKey, value, durationDays },
    result: { success: true, expiresAt },
    authorityLevel: 'act',
  });

  console.log(`üéâ Granted promo ${entitlementKey} to user ${userId}`);
}

/**
 * Check and enforce usage limits
 */
export async function enforceLimit(
  userId: string,
  metricKey: string
): Promise<{ allowed: boolean; current: number; limit: number }> {
  const limit = await getEntitlement(userId, metricKey);

  if (limit === -1) {
    return { allowed: true, current: 0, limit: -1 }; // Unlimited
  }

  const now = new Date();
  const periodStart = new Date(now.getFullYear(), now.getMonth(), 1);

  const { data: usage } = await supabase
    .from('usage_tracking')
    .select('*')
    .eq('user_id', userId)
    .eq('metric_key', metricKey)
    .eq('period_start', periodStart.toISOString())
    .single();

  const current = usage?.current_value || 0;

  return {
    allowed: current < limit,
    current,
    limit,
  };
}

/**
 * Map Stripe price ID to tier
 */
function mapPriceToTier(priceId: string): Tier {
  const tierMap: Record<string, Tier> = {
    [process.env.STRIPE_PRICE_CREATOR || '']: 'creator',
    [process.env.STRIPE_PRICE_PRO || '']: 'pro',
    [process.env.STRIPE_PRICE_ENTERPRISE || '']: 'enterprise',
  };

  return tierMap[priceId] || 'free';
}

/**
 * Get user's entitlement summary (dashboard view)
 */
export async function getEntitlementSummary(userId: string) {
  const tier = await getUserTier(userId);

  const { data: entitlements } = await supabase
    .from('user_entitlements')
    .select('*')
    .eq('user_id', userId);

  const { data: definitions } = await supabase
    .from('entitlement_definitions')
    .select('*');

  const summary = {
    tier,
    entitlements: [] as Array<{
      key: string;
      displayName: string;
      value: any;
      source: string;
      expiresAt?: string;
    }>,
  };

  for (const def of definitions || []) {
    const userEntitlement = entitlements?.find(
      (e) => e.entitlement_key === def.entitlement_key
    );

    summary.entitlements.push({
      key: def.entitlement_key,
      displayName: def.display_name,
      value: userEntitlement?.entitlement_value || def.tiers[tier] || def.default_value,
      source: userEntitlement?.source || 'tier',
      expiresAt: userEntitlement?.expires_at,
    });
  }

  return summary;
}

export default {
  getUserTier,
  hasEntitlement,
  getEntitlement,
  syncStripeSubscription,
  handleSubscriptionCanceled,
  grantTrial,
  grantPromo,
  enforceLimit,
  getEntitlementSummary,
};

/**
 * Governance Notary: Immutable authority trail for Mico's decisions
 * Records every override, escalation, and special decision
 */

import { createClient, SupabaseClient } from '@supabase/supabase-js';

const supabase: SupabaseClient = createClient(
  process.env.SUPABASE_URL!,
  process.env.SUPABASE_SERVICE_KEY!
);

export type GovernanceActionType =
  | 'threshold_override'
  | 'policy_escalation'
  | 'emergency_action'
  | 'authority_grant'
  | 'authority_revoke'
  | 'guard_mode_toggle'
  | 'manual_review';

export interface GovernanceRecord {
  actionType: GovernanceActionType;
  entityType?: string;
  entityId?: string;
  beforeState?: Record<string, any>;
  afterState?: Record<string, any>;
  justification: string;
  authorizedBy?: string;
  metadata?: Record<string, any>;
}

/**
 * Inscribe a governance decision (immutable record)
 */
export async function inscribeDecision(
  record: GovernanceRecord
): Promise<string> {
  const { data, error } = await supabase
    .from('governance_notary')
    .insert({
      action_type: record.actionType,
      entity_type: record.entityType,
      entity_id: record.entityId,
      before_state: record.beforeState || {},
      after_state: record.afterState || {},
      justification: record.justification,
      authorized_by: record.authorizedBy || 'mico',
      metadata: record.metadata || {},
    })
    .select('id')
    .single();

  if (error) {
    console.error('Failed to inscribe governance decision:', error);
    throw new Error(`Governance inscription failed: ${error.message}`);
  }

  console.log(
    `‚öñÔ∏è GOVERNANCE: ${record.actionType} by ${record.authorizedBy || 'mico'} - ${record.justification}`
  );

  return data.id;
}

/**
 * Query governance history
 */
export async function queryGovernanceHistory(filters: {
  actionType?: GovernanceActionType;
  entityType?: string;
  entityId?: string;
  authorizedBy?: string;
  since?: Date;
  limit?: number;
}) {
  let query = supabase
    .from('governance_notary')
    .select('*')
    .order('timestamp', { ascending: false });

  if (filters.actionType) {
    query = query.eq('action_type', filters.actionType);
  }

  if (filters.entityType) {
    query = query.eq('entity_type', filters.entityType);
  }

  if (filters.entityId) {
    query = query.eq('entity_id', filters.entityId);
  }

  if (filters.authorizedBy) {
    query = query.eq('authorized_by', filters.authorizedBy);
  }

  if (filters.since) {
    query = query.gte('timestamp', filters.since.toISOString());
  }

  if (filters.limit) {
    query = query.limit(filters.limit);
  }

  const { data, error } = await query;

  if (error) {
    console.error('Failed to query governance history:', error);
    throw new Error(`Governance query failed: ${error.message}`);
  }

  return data;
}

/**
 * Get governance summary (dashboard stats)
 */
export async function getGovernanceSummary(hours: number = 24) {
  const since = new Date(Date.now() - hours * 60 * 60 * 1000);

  const records = await queryGovernanceHistory({ since, limit: 1000 });

  const summary = {
    totalDecisions: records.length,
    byActionType: {} as Record<GovernanceActionType, number>,
    byAuthorizer: {} as Record<string, number>,
    recentDecisions: records.slice(0, 10),
  };

  records.forEach((record) => {
    const actionType = record.action_type as GovernanceActionType;
    summary.byActionType[actionType] =
      (summary.byActionType[actionType] || 0) + 1;

    const authorizer = record.authorized_by || 'unknown';
    summary.byAuthorizer[authorizer] =
      (summary.byAuthorizer[authorizer] || 0) + 1;
  });

  return summary;
}

/**
 * Get audit trail for specific entity
 */
export async function getAuditTrail(entityType: string, entityId: string) {
  return await queryGovernanceHistory({
    entityType,
    entityId,
    limit: 100,
  });
}

export default {
  inscribeDecision,
  queryGovernanceHistory,
  getGovernanceSummary,
  getAuditTrail,
};

