/**
 * Circuit Breaker Integration Examples
 * Shows how to protect external API calls with circuit breakers
 */

const { getBreaker } = require('../utils/circuitBreaker');

// Example: Protected OpenAI API call
async function callOpenAI(prompt) {
  const breaker = getBreaker('openai', {
    failureThreshold: 5,
    resetTimeout: 60000,
    timeout: 10000,
    fallback: () => ({ 
      error: 'OpenAI unavailable',
      fallbackResponse: 'Service temporarily unavailable. Please try again later.'
    })
  });
  
  return breaker.execute(async () => {
    const response = await fetch('https://api.openai.com/v1/chat/completions', {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${process.env.OPENAI_API_KEY}`,
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        model: 'gpt-4',
        messages: [{ role: 'user', content: prompt }]
      })
    });
    
    if (!response.ok) {
      throw new Error(`OpenAI API error: ${response.status}`);
    }
    
    return response.json();
  });
}

// Example: Protected Supabase query
async function querySupabase(supabase, query) {
  const breaker = getBreaker('supabase', {
    failureThreshold: 3,
    resetTimeout: 30000,
    timeout: 5000,
    fallback: () => ({ 
      data: null, 
      error: { message: 'Database temporarily unavailable' },
      fromCache: true 
    })
  });
  
  return breaker.execute(async () => {
    const { data, error } = await query;
    if (error) throw new Error(error.message);
    return { data, error: null };
  });
}

// Example: Protected Stripe API call
async function processStripePayment(amount, token) {
  const breaker = getBreaker('stripe', {
    failureThreshold: 5,
    resetTimeout: 120000,
    timeout: 15000,
    fallback: () => ({
      success: false,
      error: 'Payment processing temporarily unavailable. Your card has not been charged.',
      retryable: true
    })
  });
  
  return breaker.execute(async () => {
    const stripe = require('stripe')(process.env.STRIPE_SECRET_KEY);
    return await stripe.charges.create({
      amount,
      currency: 'usd',
      source: token
    });
  });
}

// Example: Protected external API with retry
async function fetchExternalAPI(url, retries = 2) {
  const breaker = getBreaker('external-api', {
    failureThreshold: 10,
    resetTimeout: 60000,
    timeout: 5000
  });
  
  let lastError;
  for (let i = 0; i <= retries; i++) {
    try {
      return await breaker.execute(async () => {
        const response = await fetch(url);
        if (!response.ok) throw new Error(`HTTP ${response.status}`);
        return response.json();
      });
    } catch (error) {
      lastError = error;
      if (i < retries) {
        await new Promise(resolve => setTimeout(resolve, 1000 * (i + 1))); // Exponential backoff
      }
    }
  }
  throw lastError;
}

module.exports = {
  callOpenAI,
  querySupabase,
  processStripePayment,
  fetchExternalAPI
};

// API Manager for ForTheWeebs - Integrates with Jikan API (MyAnimeList)
const https = require('https');

class APIManager {
  constructor() {
    this.baseURL = 'https://api.jikan.moe/v4';
    this.cache = new Map();
    this.lastRequest = 0;
    this.requestDelay = 1000; // 1 second delay between requests to respect rate limits
  }

  // Rate limiting helper
  async waitForRateLimit() {
    const now = Date.now();
    const timeSinceLastRequest = now - this.lastRequest;
    if (timeSinceLastRequest < this.requestDelay) {
      await new Promise(resolve => setTimeout(resolve, this.requestDelay - timeSinceLastRequest));
    }
    this.lastRequest = Date.now();
  }

  // Generic API request helper
  async makeRequest(endpoint) {
    await this.waitForRateLimit();
    
    return new Promise((resolve, reject) => {
      const url = `${this.baseURL}${endpoint}`;
      
      https.get(url, (res) => {
        let data = '';
        
        res.on('data', (chunk) => {
          data += chunk;
        });
        
        res.on('end', () => {
          try {
            const parsed = JSON.parse(data);
            resolve(parsed);
          } catch (error) {
            reject(new Error(`Failed to parse JSON: ${error.message}`));
          }
        });
      }).on('error', (error) => {
        reject(new Error(`Request failed: ${error.message}`));
      });
    });
  }

  // Search for anime by title
  async searchAnime(query, limit = 10) {
    if (!query || query.trim().length < 2) {
      throw new Error('Search query must be at least 2 characters long');
    }

    const cacheKey = `search_${query}_${limit}`;
    if (this.cache.has(cacheKey)) {
      return this.cache.get(cacheKey);
    }

    try {
      const encodedQuery = encodeURIComponent(query.trim());
      const response = await this.makeRequest(`/anime?q=${encodedQuery}&limit=${limit}`);
      
      if (!response.data) {
        throw new Error('No data received from API');
      }

      const results = response.data.map(anime => ({
        id: anime.mal_id,
        title: anime.title,
        englishTitle: anime.title_english || '',
        japaneseTitle: anime.title_japanese || '',
        synopsis: anime.synopsis || '',
        episodes: anime.episodes || 0,
        score: anime.score || 0,
        scored_by: anime.scored_by || 0,
        rank: anime.rank || null,
        popularity: anime.popularity || null,
        status: anime.status || 'Unknown',
        aired: {
          from: anime.aired?.from || null,
          to: anime.aired?.to || null,
          string: anime.aired?.string || ''
        },
        duration: anime.duration || '',
        rating: anime.rating || '',
        source: anime.source || '',
        genres: anime.genres?.map(g => g.name) || [],
        themes: anime.themes?.map(t => t.name) || [],
        demographics: anime.demographics?.map(d => d.name) || [],
        studios: anime.studios?.map(s => s.name) || [],
        image: anime.images?.jpg?.large_image_url || anime.images?.jpg?.image_url || '',
        trailer: anime.trailer?.youtube_id || null,
        url: anime.url || '',
        type: anime.type || '',
        year: anime.year || null
      }));

      // Cache for 10 minutes
      this.cache.set(cacheKey, results);
      setTimeout(() => this.cache.delete(cacheKey), 10 * 60 * 1000);

      return results;
    } catch (error) {
      console.error('API search error:', error);
      throw new Error(`Search failed: ${error.message}`);
    }
  }

  // Get anime details by ID
  async getAnimeDetails(animeId) {
    const cacheKey = `details_${animeId}`;
    if (this.cache.has(cacheKey)) {
      return this.cache.get(cacheKey);
