#!/usr/bin/env node

/**
 * Bundle analyzer script
 * Helps visualize bundle size and identify large dependencies
 * 
 * Usage: node scripts/analyze-bundle.js
 */

const fs = require('fs');
const path = require('path');

const DIST_DIR = path.join(__dirname, '../dist');

function getFileSizeInKB(filePath) {
    const stats = fs.statSync(filePath);
    return (stats.size / 1024).toFixed(2);
}

function analyzeBundle() {
    console.log('üì¶ Analyzing bundle...\n');

    if (!fs.existsSync(DIST_DIR)) {
        console.error('‚ùå dist directory not found. Run `npm run build` first.');
        process.exit(1);
    }

    const files = fs.readdirSync(DIST_DIR, { recursive: true });
    const jsFiles = [];
    const cssFiles = [];
    const otherFiles = [];

    files.forEach(file => {
        if (typeof file !== 'string') return;

        const filePath = path.join(DIST_DIR, file);
        if (!fs.statSync(filePath).isFile()) return;

        const size = getFileSizeInKB(filePath);
        const fileInfo = { name: file, size: parseFloat(size) };

        if (file.endsWith('.js')) {
            jsFiles.push(fileInfo);
        } else if (file.endsWith('.css')) {
            cssFiles.push(fileInfo);
        } else {
            otherFiles.push(fileInfo);
        }
    });

    // Sort by size
    jsFiles.sort((a, b) => b.size - a.size);
    cssFiles.sort((a, b) => b.size - a.size);

    // Calculate totals
    const totalJS = jsFiles.reduce((sum, f) => sum + f.size, 0);
    const totalCSS = cssFiles.reduce((sum, f) => sum + f.size, 0);
    const totalOther = otherFiles.reduce((sum, f) => sum + f.size, 0);
    const totalSize = totalJS + totalCSS + totalOther;

    console.log('JavaScript Files:');
    console.log('‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ');
    jsFiles.forEach(file => {
        const bar = '‚ñà'.repeat(Math.ceil(file.size / 10));
        console.log(`${file.name.padEnd(40)} ${bar} ${file.size} KB`);
    });
    console.log(`\nTotal JS: ${totalJS.toFixed(2)} KB\n`);

    console.log('CSS Files:');
    console.log('‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ');
    cssFiles.forEach(file => {
        const bar = '‚ñà'.repeat(Math.ceil(file.size / 5));
        console.log(`${file.name.padEnd(40)} ${bar} ${file.size} KB`);
    });
    console.log(`\nTotal CSS: ${totalCSS.toFixed(2)} KB\n`);

    console.log('Summary:');
    console.log('‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ');
    console.log(`üì¶ Total bundle size: ${totalSize.toFixed(2)} KB`);
    console.log(`üìú JavaScript: ${totalJS.toFixed(2)} KB (${((totalJS / totalSize) * 100).toFixed(1)}%)`);
    console.log(`üé® CSS: ${totalCSS.toFixed(2)} KB (${((totalCSS / totalSize) * 100).toFixed(1)}%)`);
    console.log(`üìÅ Other: ${totalOther.toFixed(2)} KB (${((totalOther / totalSize) * 100).toFixed(1)}%)`);

    // Warnings
    console.log('\n‚ö†Ô∏è  Recommendations:');
    if (totalJS > 500) {
        console.log('- Consider code splitting to reduce main bundle size');
    }
    if (jsFiles.some(f => f.size > 200)) {
        console.log('- Some JS files are very large, consider lazy loading');
    }
    if (totalSize > 1000) {
        console.log('- Total bundle size is large, optimize images and dependencies');
    }

    console.log('\n‚úÖ Analysis complete!');
}

analyzeBundle();

#!/usr/bin/env node
/* eslint-disable no-unused-vars, @typescript-eslint/no-explicit-any */
/**
 * Apply Supabase RLS Policy Fixes
 * Fixes 406 errors by updating Row Level Security policies
 */

require('dotenv').config();
const { createClient } = require('@supabase/supabase-js');
const fs = require('node:fs');
const path = require('node:path');

const supabaseUrl = process.env.SUPABASE_URL;
const supabaseServiceKey = process.env.SUPABASE_SERVICE_KEY;

if (!supabaseUrl || !supabaseServiceKey) {
  console.error('‚ùå Missing SUPABASE_URL or SUPABASE_SERVICE_KEY in .env');
  process.exit(1);
}

const supabase = createClient(supabaseUrl, supabaseServiceKey);

async function applyRLSFixes() {
  console.log('üîß Applying RLS policy fixes to Supabase...\n');

  const sqlFile = path.join(__dirname, '../supabase/migrations/fix_rls_policies.sql');
  const sql = fs.readFileSync(sqlFile, 'utf8');

  // Split SQL into individual statements
