          console.log(`   New: ${data.newRetainUntil}`);
          successCount++;
        }
      } catch (err) {
        console.error(`‚ùå Exception extending ${receipt.receipt_id}:`, err);
        errorCount++;
      }
    }

    console.log('\nüìä SUMMARY:');
