                <div className="achievement-grid">
                    {ACHIEVEMENTS.map(achievement => {
                        const isUnlocked = unlockedAchievements.includes(achievement.id);
                        return (
                            <div
                                key={achievement.id}
                                className={`achievement-card ${isUnlocked ? 'unlocked' : 'locked'} ${achievement.rarity}`}
