import React, { useState } from 'react';

export function CGIConverter({ userId }) {
  const [converting, setConverting] = useState(false);
  const [progress, setProgress] = useState(0);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  const handleConvert = async (file, outputFormats) => {
    setConverting(true);
    setError(null);
    setProgress(0);

    try {
      const formData = new FormData();
      formData.append('file', file);
      formData.append('formats', JSON.stringify(outputFormats));
      formData.append('userId', userId);

      const response = await fetch('/api/convert-cgi', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: formData
      });

      if (!response.ok) {
        throw new Error(`Conversion failed: ${response.statusText}`);
      }

      const data = await response.json();
      setResult(data);
      setProgress(100);

    } catch (err) {
      console.error('Conversion error:', err);
      setError(err.message);
    } finally {
      setTimeout(() => {
        setConverting(false);
        setProgress(0);
      }, 2000);
    }
  };

  return (
    <div style={{
      background: 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)',
      borderRadius: '20px',
      padding: '40px',
      color: 'white'
    }}>
      <h2 style={{
        fontSize: '32px',
        marginBottom: '10px',
        display: 'flex',
        alignItems: 'center',
        gap: '15px'
      }}>
        <span style={{ fontSize: '48px' }}>üîÑ</span>
        CGI to VR/AR Converter
      </h2>
      <p style={{ marginBottom: '30px', opacity: 0.9 }}>
        Convert your CGI content to VR and AR formats while keeping the original
      </p>

      <ConversionUploader onConvert={handleConvert} converting={converting} />

      {converting && (
        <div style={{ marginTop: '30px' }}>
          <div style={{
            background: 'rgba(255,255,255,0.2)',
            borderRadius: '10px',
            height: '30px',
            overflow: 'hidden',
            position: 'relative'
          }}>
            <div style={{
              background: 'linear-gradient(90deg, #4CAF50, #8BC34A)',
              height: '100%',
              width: `${progress}%`,
              transition: 'width 0.3s',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              fontSize: '14px',
              fontWeight: 'bold'
            }}>
              {progress}% Converting...
            </div>
          </div>
        </div>
      )}

      {error && (
        <div style={{
          marginTop: '30px',
          background: 'rgba(255, 0, 0, 0.2)',
          border: '2px solid rgba(255, 0, 0, 0.5)',
          borderRadius: '12px',
          padding: '20px'
        }}>
          <strong>‚ùå Error:</strong> {error}
        </div>
      )}

      {result && (
        <ConversionResult result={result} />
      )}

      {/* Info Section */}
      <div style={{
        marginTop: '40px',
        padding: '25px',
        background: 'rgba(0,0,0,0.2)',
        borderRadius: '15px'
      }}>
        <h3 style={{ marginBottom: '15px', fontSize: '20px' }}>üìö Supported Conversions</h3>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '15px' }}>
          <div>
            <strong>üé¨ Input Formats:</strong>
            <ul style={{ marginTop: '8px', marginLeft: '20px', lineHeight: '1.8' }}>
              <li>.mp4, .mov (video)</li>
              <li>.jpg, .png (images)</li>
              <li>.obj, .fbx (3D models)</li>
              <li>.blend (Blender files)</li>
            </ul>
          </div>
          <div>
            <strong>ü•Ω VR Output:</strong>
            <ul style={{ marginTop: '8px', marginLeft: '20px', lineHeight: '1.8' }}>
              <li>.glb (3D for VR)</li>
