                            onChange={(e) => setIntensity(Number(e.target.value))}
                            style={{ width: '100%' }}
                        />
                    </div>

                    {/* Preview Area */}
                    <div style={{
                        background: '#000',
                        borderRadius: '8px',
                        aspectRatio: '16/9',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        position: 'relative',
                        overflow: 'hidden'
                    }}>
                        <video
                            ref={videoRef}
                            style={{ display: 'none' }}
                            autoPlay
                            playsInline
                            muted
                        />
                        <canvas
                            ref={canvasRef}
                            width={1280}
                            height={720}
                            style={{ maxWidth: '100%', maxHeight: '100%' }}
                        />
                        {!videoStream && (
                            <div style={{ position: 'absolute', color: '#666', textAlign: 'center' }}>
                                <p style={{ fontSize: '48px', marginBottom: '10px' }}>üé•</p>
                                <p>Waiting for video stream...</p>
                            </div>
                        )}
                    </div>
                </>
            )}
        </div>
    );
}

