  if (!hasAccess) {
    return (
      <div style={{
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        padding: '60px 40px',
        borderRadius: '16px',
        color: '#fff',
        textAlign: 'center',
        maxWidth: '600px',
        margin: '40px auto'
      }}>
        <div style={{ fontSize: '64px', marginBottom: '20px' }}>üé¨</div>
