import React, { useState, useEffect } from 'react';
import './CommunityModTools.css';

const CommunityModTools = () => {
  const [activeTab, setActiveTab] = useState('reports');
  const [reports, setReports] = useState([]);
  const [bannedUsers, setBannedUsers] = useState([]);
  const [autoModRules, setAutoModRules] = useState([]);
  const [contentQueue, setContentQueue] = useState([]);

  useEffect(() => {
    loadModerationData();
  }, []);

  const loadModerationData = async () => {
    try {
      const response = await fetch('/api/moderation/dashboard', {
        headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
      });
      if (response.ok) {
        const data = await response.json();
        setReports(data.reports || []);
        setBannedUsers(data.bannedUsers || []);
        setAutoModRules(data.autoModRules || []);
        setContentQueue(data.contentQueue || []);
      }
    } catch (error) {
      console.error('Failed to load moderation data:', error);
    }
  };

  const handleReportAction = (reportId, action) => {
    setReports(reports.map(r => r.id === reportId ? { ...r, status: action } : r));
  };

  const handleContentAction = (contentId, action) => {
    if (action === 'approve' || action === 'remove') {
      setContentQueue(contentQueue.filter(c => c.id !== contentId));
    }
  };

  const toggleAutoModRule = (ruleId) => {
    setAutoModRules(autoModRules.map(r => r.id === ruleId ? { ...r, enabled: !r.enabled } : r));
  };

  return (
    <div className="community-mod-tools">
      <div className="mod-header">
        <h1>üõ°Ô∏è Community Moderation</h1>
        <div className="mod-stats-summary">
          <div className="stat-pill"><span>Pending</span><span className="urgent">{reports.filter(r => r.status === 'pending').length}</span></div>
          <div className="stat-pill"><span>Resolved Today</span><span>45</span></div>
          <div className="stat-pill"><span>Avg Response</span><span>12 min</span></div>
        </div>
      </div>

      <div className="mod-tabs">
        <button className={`mod-tab ${activeTab === 'reports' ? 'active' : ''}`} onClick={() => setActiveTab('reports')}>
          üìã Reports {reports.filter(r => r.status === 'pending').length > 0 && <span className="badge">{reports.filter(r => r.status === 'pending').length}</span>}
        </button>
        <button className={`mod-tab ${activeTab === 'content' ? 'active' : ''}`} onClick={() => setActiveTab('content')}>
          üé® Content Queue
        </button>
        <button className={`mod-tab ${activeTab === 'bans' ? 'active' : ''}`} onClick={() => setActiveTab('bans')}>
          üö´ Bans
        </button>
        <button className={`mod-tab ${activeTab === 'automod' ? 'active' : ''}`} onClick={() => setActiveTab('automod')}>
          ü§ñ Auto-Mod
        </button>
      </div>

      <div className="mod-content">
        {activeTab === 'reports' && (
          <div className="reports-section">
            <h2>User Reports</h2>
            <div className="reports-list">
              {reports.map(report => (
                <div key={report.id} className={`report-card ${report.severity}`}>
                  <div className="report-header">
                    <span className={`severity-badge ${report.severity}`}>
                      {report.severity === 'high' ? 'üî¥' : 'üü°'} {report.severity}
                    </span>
                    <span className={`status-badge ${report.status}`}>{report.status}</span>
                  </div>
                  <div className="report-body">
                    <p><strong>User:</strong> {report.reportedUser}</p>
                    <p><strong>By:</strong> {report.reportedBy}</p>
                    <p><strong>Reason:</strong> {report.reason}</p>
                    <p><strong>Content:</strong> {report.content}</p>
                    <p className="report-time">{report.timestamp}</p>
                  </div>
                  <div className="report-actions">
                    <button onClick={() => handleReportAction(report.id, 'investigating')} className="btn-secondary">üîç Investigate</button>
                    <button onClick={() => handleReportAction(report.id, 'dismissed')} className="btn-neutral">Dismiss</button>
                    <button onClick={() => handleReportAction(report.id, 'resolved')} className="btn-success">‚úì Resolve</button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {activeTab === 'content' && (
          <div className="content-section">
            <h2>Content Moderation Queue</h2>
            <div className="content-grid">
              {contentQueue.map(content => (
                <div key={content.id} className={`content-card ${content.flagged ? 'flagged' : ''}`}>
                  <div className="content-thumbnail">
                    <img src={content.thumbnail} alt={content.title} />
                    {content.flagged && <div className="flag-indicator">‚ö†Ô∏è FLAGGED</div>}
                  </div>
                  <div className="content-info">
                    <h4>{content.title}</h4>
                    <p>By {content.creator}</p>
                    <p className="content-meta">{content.type} ‚Ä¢ {content.uploadedDate}</p>
                    {content.flagged && <p className="flag-reason">{content.flagReason}</p>}
                  </div>
                  <div className="content-actions">
                    <button onClick={() => handleContentAction(content.id, 'approve')} className="btn-success">‚úì</button>
                    <button onClick={() => handleContentAction(content.id, 'review')} className="btn-secondary">üëÅÔ∏è</button>
                    <button onClick={() => handleContentAction(content.id, 'remove')} className="btn-danger">üóëÔ∏è</button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {activeTab === 'bans' && (
          <div className="bans-section">
            <h2>Banned Users</h2>
            <div className="bans-list">
              {bannedUsers.map(ban => (
                <div key={ban.id} className="ban-card">
                  <div className="ban-header">
                    <h4>{ban.username}</h4>
                    {ban.permanent ? <span className="ban-type permanent">PERMANENT</span> : <span className="ban-type">Expires: {ban.expiresDate}</span>}
                  </div>
                  <p><strong>Reason:</strong> {ban.reason}</p>
                  <p><strong>By:</strong> {ban.bannedBy} on {ban.bannedDate}</p>
                </div>
              ))}
            </div>
          </div>
        )}

        {activeTab === 'automod' && (
          <div className="automod-section">
            <h2>Auto-Moderation Rules</h2>
            <div className="rules-list">
              {autoModRules.map(rule => (
                <div key={rule.id} className="rule-card">
                  <div className="rule-header">
                    <h4>{rule.name}</h4>
                    <label className="toggle-switch">
                      <input type="checkbox" checked={rule.enabled} onChange={() => toggleAutoModRule(rule.id)} />
                      <span className="toggle-slider"></span>
                    </label>
                  </div>
                  <div className="rule-details">
                    <span className={`rule-type ${rule.type}`}>{rule.type}</span>
                    <span className="rule-action">{rule.action}</span>
                    <span className="rule-triggers">{rule.triggers} triggers</span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default CommunityModTools;

// CONTENT MODERATION - NSFW detection, age gate, content approval

import React, { useState } from 'react';
import './ContentModeration.css';

// Simple NSFW keyword detection (client-side fallback)
const NSFW_KEYWORDS = [
  'nsfw', 'adult', 'explicit', 'nude', 'naked', 'sex', 'porn', 'hentai',
  'ecchi', 'lewd', 'r18', 'xxx', '18+', 'mature', 'erotic'
];

const VIOLENCE_KEYWORDS = [
  'gore', 'blood', 'violence', 'death', 'murder', 'kill', 'weapon',
  'gun', 'knife', 'horror', 'scary', 'disturbing'
];

export function ContentModeration() {
  return null; // Utility functions only
}

// Age Gate Component
export function AgeGate({ onVerify }) {
  const [birthYear, setBirthYear] = useState('');
  const [error, setError] = useState('');

  const handleVerify = () => {
    const year = parseInt(birthYear);
    const currentYear = new Date().getFullYear();
    const age = currentYear - year;

    if (!year || year < 1900 || year > currentYear) {
      setError('Please enter a valid birth year');
      return;
    }

    if (age < 18) {
      setError('You must be 18 or older to view this content');
      return;
    }

    // Store verification in localStorage
    localStorage.setItem('ageVerified', 'true');
    localStorage.setItem('ageVerifiedAt', Date.now().toString());
    onVerify(true);
  };

  return (
    <div className="age-gate-overlay">
      <div className="age-gate-modal">
        <h1>üîû Age Verification Required</h1>
        <p className="age-gate-warning">
          This content may contain adult material. You must be 18 or older to proceed.
        </p>

        <div className="age-gate-form">
          <label>Enter Your Birth Year:</label>
          <input
            type="number"
            placeholder="YYYY"
            value={birthYear}
            onChange={(e) => setBirthYear(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleVerify()}
            min="1900"
            max={new Date().getFullYear()}
          />

          {error && <div className="age-gate-error">{error}</div>}

          <button className="age-gate-verify-btn" onClick={handleVerify}>
            ‚úì I am 18 or older
          </button>

          <button className="age-gate-exit-btn" onClick={() => window.history.back()}>
            ‚Üê Go Back
          </button>
        </div>

        <p className="age-gate-disclaimer">
          By proceeding, you confirm that you are of legal age in your jurisdiction
          and agree to view adult content at your own discretion.
        </p>
      </div>
    </div>
  );
}

// NSFW Content Wrapper
export function NSFWContent({ children, isNSFW, user = null }) {
  const [verified, setVerified] = useState(() => {
    // AUTO-VERIFY: User has payment history = 18+
    if (user && (user.hasCompletedPurchase || user.hasReceivedPayout || user.stripeAccountVerified)) {
      return true; // Payment = age verified
    }

    const stored = localStorage.getItem('ageVerified');
    const verifiedAt = localStorage.getItem('ageVerifiedAt');

    // Verification expires after 24 hours
    if (stored && verifiedAt) {
      const hoursSince = (Date.now() - parseInt(verifiedAt)) / 1000 / 60 / 60;
      return hoursSince < 24;
    }
    return false;
  });

  if (!isNSFW) {
    return <>{children}</>;
  }

  if (!verified) {
    return <AgeGate onVerify={setVerified} />;
  }

  return (
    <div className="nsfw-content-wrapper">
      <div className="nsfw-warning-banner">
        üîû This content is marked as NSFW/Adult
        {user?.hasCompletedPurchase && <span> (Age verified via payment)</span>}
      </div>
      {children}
    </div>
  );
}

// NSFW Blur Overlay (for thumbnails)
export function NSFWBlur({ src, alt, isNSFW, onClick, user = null }) {
  const [revealed, setRevealed] = useState(false);
  const [ageVerified, setAgeVerified] = useState(() => {
    // AUTO-VERIFY: User has payment history = 18+
    if (user && (user.hasCompletedPurchase || user.hasReceivedPayout || user.stripeAccountVerified)) {
      return true;
    }

    const stored = localStorage.getItem('ageVerified');
    return stored === 'true';
  });

  if (!isNSFW) {
    return <img src={src} alt={alt} onClick={onClick} />;
  }

  if (!ageVerified) {
    return (
      <div className="nsfw-blur-container" onClick={() => {
        const year = prompt('Enter your birth year to view NSFW content:');
        if (year) {
          const age = new Date().getFullYear() - parseInt(year);
          if (age >= 18) {
            localStorage.setItem('ageVerified', 'true');
            localStorage.setItem('ageVerifiedAt', Date.now().toString());
            setAgeVerified(true);
          } else {
            alert('You must be 18 or older');
          }
        }
      }}>
        <div className="nsfw-blur-overlay">
          <div className="nsfw-blur-icon">üîû</div>
          <div className="nsfw-blur-text">NSFW Content</div>
          <div className="nsfw-blur-subtext">Click to verify age</div>
        </div>
      </div>
    );
  }

  return (
    <div className="nsfw-blur-container">
      {!revealed && (
        <div className="nsfw-blur-overlay" onClick={(e) => {
          e.stopPropagation();
          setRevealed(true);
        }}>
          <div className="nsfw-blur-icon">üîû</div>
          <div className="nsfw-blur-text">NSFW Content</div>
          <div className="nsfw-blur-subtext">Click to reveal</div>
        </div>
      )}
      <img
        src={src}
        alt={alt}
        className={revealed ? '' : 'blurred'}
        onClick={onClick}
      />
    </div>
  );
}

// Content Analysis Utilities
export const analyzeContent = (title = '', description = '', tags = []) => {
  const text = `${title} ${description} ${tags.join(' ')}`.toLowerCase();

  const hasNSFWKeywords = NSFW_KEYWORDS.some(keyword => text.includes(keyword));
  const hasViolenceKeywords = VIOLENCE_KEYWORDS.some(keyword => text.includes(keyword));

  let contentRating = 'safe';
  let flags = [];

  if (hasNSFWKeywords) {
    contentRating = 'nsfw';
    flags.push('adult_content');
  }

  if (hasViolenceKeywords) {
    flags.push('violence');
    if (contentRating === 'safe') {
      contentRating = 'mature';
    }
  }

  return {
    isNSFW: contentRating === 'nsfw',
    isMature: contentRating === 'mature' || contentRating === 'nsfw',
    contentRating,
    flags,
    confidence: hasNSFWKeywords || hasViolenceKeywords ? 0.8 : 0.3
  };
};

// Content Approval Status
export const ContentApprovalStatus = {
  PENDING: 'pending',
  APPROVED: 'approved',
  REJECTED: 'rejected',
  FLAGGED: 'flagged'
};

// Moderation Queue Item Component
export function ModerationQueueItem({ artwork, onApprove, onReject, onFlag }) {
  const analysis = analyzeContent(artwork.title, artwork.description, artwork.tags);

  return (
    <div className="moderation-queue-item">
      <div className="moderation-thumbnail">
        <img src={artwork.url} alt={artwork.title} />
        {analysis.isNSFW && <div className="nsfw-badge">üîû NSFW</div>}
      </div>

      <div className="moderation-details">
        <h3>{artwork.title || 'Untitled'}</h3>
        <p>{artwork.description}</p>

        <div className="moderation-tags">
          {artwork.tags?.map((tag, idx) => (
            <span key={idx} className="tag">#{tag}</span>
          ))}
        </div>

        <div className="moderation-analysis">
          <strong>Auto-Analysis:</strong>
          <div>Rating: {analysis.contentRating}</div>
          <div>Flags: {analysis.flags.join(', ') || 'None'}</div>
          <div>Confidence: {(analysis.confidence * 100).toFixed(0)}%</div>
        </div>

        <div className="moderation-metadata">
          <div>Uploaded by: {artwork.userId}</div>
          <div>Date: {new Date(artwork.uploadedAt).toLocaleString()}</div>
          <div>Size: {(artwork.size / 1024 / 1024).toFixed(2)} MB</div>
        </div>
      </div>

      <div className="moderation-actions">
        <button className="approve-btn" onClick={() => onApprove(artwork)}>
          ‚úì Approve
        </button>
        <button className="reject-btn" onClick={() => onReject(artwork)}>
          ‚úï Reject
        </button>
        <button className="flag-btn" onClick={() => onFlag(artwork)}>
          ‚ö†Ô∏è Flag
        </button>
      </div>
    </div>
  );
}

export default ContentModeration;

