import React, { useState, useEffect } from 'react';
import './CookieConsent.css';

const CookieConsent = () => {
    const [visible, setVisible] = useState(false);

    useEffect(() => {
        // Check if user has already consented
        const consent = localStorage.getItem('cookie-consent');
        if (!consent) {
            setVisible(true);
        }
    }, []);

    const handleAccept = () => {
        localStorage.setItem('cookie-consent', 'accepted');
        setVisible(false);

        // Initialize analytics if accepted
        if (window.gtag) {
            window.gtag('consent', 'update', {
                'analytics_storage': 'granted'
            });
        }
    };

    const handleDecline = () => {
        localStorage.setItem('cookie-consent', 'declined');
        setVisible(false);

        // Disable analytics if declined
        if (window.gtag) {
            window.gtag('consent', 'update', {
                'analytics_storage': 'denied'
            });
        }
    };

    if (!visible) return null;

    return (
        <div className="cookie-consent">
            <div className="cookie-content">
                <p>
                    üç™ We use cookies to enhance your experience, analyze site traffic, and personalize content.
                    By clicking "Accept", you consent to our use of cookies.
                </p>
                <div className="cookie-buttons">
                    <button onClick={handleAccept} className="accept-btn">
                        Accept All
                    </button>
                    <button onClick={handleDecline} className="decline-btn">
                        Decline
                    </button>
                    <a href="/privacy" className="privacy-link">Privacy Policy</a>
                </div>
            </div>
        </div>
    );
};

export default CookieConsent;

import React from "react";

/**
 * @param {{ tier: string, label: string, color: string, icon: string }} props
 */
export const CreatorBadge = ({ label, color, icon }) => {
  return (
    <div
      style={{
        backgroundColor: color,
        borderRadius: "8px",
        padding: "6px 12px",
        display: "inline-flex",
        alignItems: "center",
        gap: "8px",
        fontWeight: "bold",
        color: "#fff",
        fontSize: "0.9rem",
        boxShadow: "0 2px 6px rgba(0,0,0,0.2)",
      }}
    >
      <span style={{ fontSize: "1.2rem" }}>{icon}</span>
      <span>{label}</span>
    </div>
  );
};

