    const data = labels.map(() => 0);

    setEarningsData({
      labels,
      datasets: [
        {
          label: 'Earnings ($)',
          data,
          borderColor: '#e91e63',
          backgroundColor: 'rgba(233, 30, 99, 0.1)',
          tension: 0.4,
        },
      ],
    });

    // Mock stats
    setStats({
      balance: 2847.50,
      totalEarnings: 12450.00,
      monthlyEarnings: 1820.00,
      followers: 3524,
      artworkViews: 45230,
