                  <div className="subscriber-info">
                    <div className="subscriber-username">{subscriber.username}</div>
                    <div className="subscriber-tier">
                      <span className={`tier-badge ${subscriber.tier}`}>
                        {subscriber.tier === 'adult' && 'üîû'}
