import React, { useState, useEffect } from 'react';
import { remixConfig } from '../config/remix.config';
import { mintBadge } from '../utils/mintBadge';
import { propagateProtocol } from '../utils/propagateProtocol';
import { supabase } from '../lib/supabase';

export const CreatorOnboard = ({ userId }) => {
  const [status, setStatus] = useState('idle');
  const [remixAnchor] = useState(remixConfig.remixAnchor);

  const onboard = async () => {
    setStatus('processing');
    try {
      // Step 1: Lock remix lineage
      const lineage = {
        remixAnchor,
        creatorId: userId,
        timestamp: remixConfig.timestamp,
        flags: remixConfig.sovereignFlags,
      };
      // Step 2: Propagate sovereign protocol
      await propagateProtocol(lineage);
      // Step 3: Mint remix badge
      await mintBadge(userId, remixAnchor);
      // Step 4: Save onboarding status
      await supabase.from('creators').upsert([
        {
          user_id: userId,
          remix_anchor: remixAnchor,
          onboarded_at: new Date().toISOString(),
        },
      ]);
      setStatus('complete');
    } catch (err) {
      console.error('‚ùå Onboarding failed:', err);
      setStatus('error');
    }
  };

  useEffect(() => {
    onboard();
    // eslint-disable-next-line
  }, []);

  return (
    <div className="onboard-panel">
      <h2>üß¨ Creator Onboarding</h2>
      <p>Status: {status}</p>
      {status === 'complete' && <p>‚úÖ Remix lineage locked. Badge minted.</p>}
      {status === 'error' && <p>‚ùå Something went wrong. Try again.</p>}
    </div>
  );
};

/* eslint-disable @typescript-eslint/no-explicit-any, no-console, sonarjs/cognitive-complexity */
import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import FAQ from './FAQ';
import CreatorPricing from './CreatorPricing';

const FAQComponent = FAQ;
import { isOwner } from '../utils/ownerAuth';

/**
 * CREATOR OVERVIEW - COMMAND CENTER
 * Dashboard with personal stats, activity, quick launch
 * + Platform-wide analytics for owner
 */
function CreatorOverview({ userId, creatorName, onNavigate }) {
  const [isOwnerUser, setIsOwnerUser] = useState(false);
  const [stats, setStats] = useState({
    totalCreations: 0,
    storageUsed: 0,
    storageTotal: Infinity, // Unlimited storage
    mythicRank: 'Initiate',
    weeklyStreak: 0,
    totalRevenue: 0
  });

  const [platformStats] = useState({
    totalUsers: 1247,
    activeUsers: 892,
    newUsersToday: 23,
    totalPlatformRevenue: 45678,
    avgSessionTime: '12m 34s',
    conversionRate: '3.4%'
  });

  const [recentActivity, setRecentActivity] = useState([]);
  const [quickLaunchTools, setQuickLaunchTools] = useState([]);
  const [showFAQ, setShowFAQ] = useState(false);
  const [showPricing, setShowPricing] = useState(false);

  useEffect(() => {
    checkOwnerStatus();
    loadDashboardData();
  }, [userId]);

  const checkOwnerStatus = async () => {
    const ownerStatus = await isOwner();
    setIsOwnerUser(ownerStatus);
  };

  const loadDashboardData = async () => {
    // Load actual storage stats from IndexedDB
    try {
      const storageManager = await import('../utils/storageManager');
      if (storageManager && storageManager.default && storageManager.default.checkQuota) {
        const quota = await storageManager.default.checkQuota();
        
        setStats(prev => ({
          ...prev,
          storageUsed: Math.floor(quota.used / (1024 * 1024)), // Convert to MB
          storageTotal: Infinity // Unlimited storage for creators
        }));
      }
    } catch (error) {
      console.error('Failed to load storage stats:', error);
      // Set unlimited storage on error
      setStats(prev => ({
        ...prev,
        storageUsed: 0,
        storageTotal: Infinity
      }));
    }

    // Load stats from Supabase/backend
    setStats(prev => ({
      ...prev,
      totalCreations: 0,
      mythicRank: getMythicRank(0),
      weeklyStreak: 0,
      totalRevenue: '0.00'
    }));

    // Load real activity from API
    fetch('/api/user/recent-activity', {
      headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
    })
      .then(res => res.json())
      .then(data => setRecentActivity(data.activities || []))
      .catch(err => {
        console.error('Failed to load activity:', err);
        setRecentActivity([]);
      });

    setQuickLaunchTools([
      { name: 'Zoom & Enhance', icon: 'üîç', tab: 'photo', color: '#9C27B0' },
      { name: 'Video Export', icon: 'üé¨', tab: 'video', color: '#F44336' },
      { name: 'Audio Mixer', icon: 'üéµ', tab: 'audio', color: '#2196F3' },
      { name: 'Meme Generator', icon: 'üòÇ', tab: 'video', color: '#FF9800' },
      { name: 'Mythic Avatar', icon: 'üé≠', tab: 'mythic', color: '#667eea' },
      { name: 'Experimental Lab', icon: '‚öóÔ∏è', tab: 'experimental', color: '#F44336' }
    ]);
  };

  const getMythicRank = (count) => {
    if (count >= 1000) return 'Mythic';
    if (count >= 500) return 'Legend';
    if (count >= 250) return 'Master';
    if (count >= 100) return 'Adept';
    if (count >= 50) return 'Artisan';
    return 'Initiate';
  };

  const getMythicColor = (rank) => {
    const colors = {
      'Initiate': '#4CAF50',
      'Artisan': '#2196F3',
      'Adept': '#9C27B0',
      'Master': '#FF5722',
      'Legend': '#FFD700',
      'Mythic': '#E91E63'
    };
    return colors[rank] || '#4CAF50';
  };

  const getActivityIcon = (type) => {
    const icons = {
      upscale: 'üîç',
      export: 'üì§',
      create: '‚ú®',
      mint: 'üíé',
      collab: 'ü§ù',
      upload: 'üìÅ',
      edit: '‚úèÔ∏è'
    };
    return icons[type] || '‚ö°';
  };

  return (
    <div style={{
      padding: '30px',
      maxWidth: '1600px',
      margin: '0 auto',
      color: 'white'
    }}>
      {/* Header */}
      <div style={{
        marginBottom: '40px',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'flex-start'
      }}>
        <div>
          <h1 style={{ fontSize: '48px', marginBottom: '10px', fontWeight: 'bold' }}>
            Welcome back, {creatorName || 'Creator'} üëã
          </h1>
          <p style={{ fontSize: '18px', opacity: 0.7 }}>
            Your creator command center. Let's make something legendary today.
          </p>
        </div>
        <div style={{ display: 'flex', gap: '12px' }}>
          <button
            onClick={() => setShowPricing(!showPricing)}
            style={{
              background: 'rgba(76, 175, 80, 0.2)',
              border: '2px solid #4CAF50',
              borderRadius: '12px',
              padding: '12px 24px',
              color: 'white',
              fontSize: '16px',
              fontWeight: 600,
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              gap: '8px'
            }}
          >
            üí∞ Set Pricing
          </button>
          <button
            onClick={() => setShowFAQ(!showFAQ)}
            style={{
              background: 'rgba(102, 126, 234, 0.2)',
              border: '2px solid #667eea',
              borderRadius: '12px',
              padding: '12px 24px',
              color: 'white',
              fontSize: '16px',
              fontWeight: 600,
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              gap: '8px'
            }}
          >
            ‚ùì FAQ & Legal
          </button>
        </div>
      </div>

      {/* Show Pricing Modal */}
      {showPricing && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'rgba(0,0,0,0.8)',
          zIndex: 9999,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          padding: '20px'
