        }} onClick={() => setShowFAQ(false)} onKeyDown={(e) => e.key === 'Escape' && setShowFAQ(false)} role="button" tabIndex={0}>
          <div style={{
            maxWidth: '800px',
            maxHeight: '90vh',
            overflow: 'auto',
            background: '#1a1a2e',
            borderRadius: '16px',
            padding: '0'
          }} onClick={(e) => e.stopPropagation()} onKeyDown={(e) => e.stopPropagation()} role="dialog" tabIndex={-1}>
            <div style={{
              position: 'sticky',
              top: 0,
              background: '#1a1a2e',
              padding: '20px',
              borderBottom: '1px solid rgba(255,255,255,0.1)',
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center'
            }}>
              <h2 style={{ margin: 0 }}>FAQ & Legal Documents</h2>
              <button
                onClick={() => setShowFAQ(false)}
                style={{
                  background: 'transparent',
                  border: 'none',
                  color: 'white',
                  fontSize: '24px',
                  cursor: 'pointer'
                }}
              >
                ‚úï
              </button>
            </div>
            <FAQComponent />
          </div>
        </div>
      )}

      {/* Top Stats Grid */}
      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
        gap: '25px',
        marginBottom: '40px'
      }}>
        {/* Total Creations */}
        <div style={{
          background: 'linear-gradient(135deg, rgba(156,39,176,0.2) 0%, rgba(103,58,183,0.2) 100%)',
          borderRadius: '20px',
          padding: '30px',
          border: '2px solid rgba(156,39,176,0.3)',
          position: 'relative',
          overflow: 'hidden'
        }}>
          <div style={{ position: 'absolute', top: '20px', right: '20px', fontSize: '48px', opacity: 0.3 }}>
            üé®
          </div>
          <div style={{ fontSize: '14px', opacity: 0.8, marginBottom: '8px' }}>Total Creations</div>
          <div style={{ fontSize: '42px', fontWeight: 'bold', marginBottom: '5px' }}>
            {stats.totalCreations}
          </div>
          <div style={{ fontSize: '13px', opacity: 0.7 }}>
            +0 this week
          </div>
        </div>

        {/* Storage */}
        <div style={{
