            <div style={{
              flex: 1,
              height: '8px',
              background: 'rgba(0,0,0,0.3)',
              borderRadius: '4px',
              overflow: 'hidden'
            }}>
              <div style={{
                width: `${(stats.storageUsed / stats.storageTotal) * 100}%`,
                height: '100%',
                background: 'linear-gradient(90deg, #2196F3 0%, #03A9F4 100%)',
                transition: 'width 0.5s'
              }} />
            </div>
            <span style={{ fontSize: '12px', opacity: 0.7 }}>
              ‚àû Unlimited
            </span>
          </div>
        </div>

        {/* Mythic Rank */}
        <div style={{
