import React, { useState, useEffect } from 'react';
import { useAuth } from './AuthSupabase.jsx';
import './CreatorPaymentSetup.css';

/**
 * Creator Payment Setup Component
 * Handles Stripe Connect onboarding for creators to receive payments
 * Stripe handles all tax compliance automatically
 */
export function CreatorPaymentSetup() {
  const { user } = useAuth();
  const [status, setStatus] = useState('loading');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    checkAccountStatus();
  }, [user]);

  const checkAccountStatus = async () => {
    try {
      const response = await fetch(`/api/stripe-connect/account-status/${user.id}`);
      const data = await response.json();

      if (data.connected && data.status === 'active') {
        setStatus('active');
      } else if (data.connected && data.status === 'pending') {
        setStatus('pending');
      } else {
        setStatus('not_setup');
      }
    } catch (err) {
      console.error('Error checking account status:', err);
      setStatus('not_setup');
    }
  };

  const handleEnablePayments = async () => {
    setLoading(true);
    setError('');

    try {
      const response = await fetch('/api/stripe-connect/create-account', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          userId: user.id,
          email: user.email
        })
      });

      const data = await response.json();

      if (data.success) {
        // Redirect to Stripe onboarding
        globalThis.location.href = data.onboardingUrl;
      } else {
        setError(data.error || 'Failed to start setup');
      }
    } catch (err) {
      console.error('Enable payments error:', err);
      setError('Network error. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleAccessDashboard = async () => {
    setLoading(true);

    try {
      const response = await fetch('/api/stripe-connect/dashboard-link', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId: user.id })
      });

      const data = await response.json();

      if (data.success) {
        globalThis.open(data.url, '_blank');
      }
    } catch (err) {
      console.error('Dashboard access error:', err);
      setError('Could not open dashboard');
    } finally {
      setLoading(false);
    }
  };

  if (status === 'loading') {
    return (
      <div className="payment-setup-card">
        <div className="loading-spinner"></div>
        <p>Checking payment status...</p>
      </div>
    );
  }

  if (status === 'active') {
    return (
      <div className="payment-setup-card payment-setup-active">
        <div className="status-icon">‚úÖ</div>
        <h3>Payments Enabled</h3>
        <p>You're all set to receive payments!</p>

        <div className="payment-features">
          <div className="feature-item">
            <span className="feature-icon">üí∞</span>
            <span>Receive tips & commissions</span>
          </div>
          <div className="feature-item">
            <span className="feature-icon">üîÑ</span>
            <span>Auto payouts to your bank</span>
          </div>
          <div className="feature-item">
            <span className="feature-icon">üìä</span>
            <span>Tax forms generated automatically</span>
          </div>
          <div className="feature-item">
            <span className="feature-icon">üåç</span>
            <span>Accept payments worldwide</span>
          </div>
        </div>

        <button
          onClick={handleAccessDashboard}
          className="btn-dashboard"
          disabled={loading}
        >
          {loading ? 'Opening...' : 'View Stripe Dashboard'}
        </button>

        <div className="payment-info">
          <p><strong>Tax Compliance:</strong> Stripe automatically handles all tax forms (1099-K) and IRS reporting. You'll receive your tax documents by January 31st each year.</p>
        </div>
      </div>
    );
  }

  if (status === 'pending') {
    return (
      <div className="payment-setup-card payment-setup-pending">
        <div className="status-icon">‚è≥</div>
        <h3>Payment Setup In Progress</h3>
        <p>Complete your Stripe setup to start receiving payments</p>

        <button
          onClick={handleEnablePayments}
          className="btn-continue-setup"
          disabled={loading}
        >
          {loading ? 'Loading...' : 'Continue Setup'}
        </button>
      </div>
    );
  }

  return (
    <div className="payment-setup-card payment-setup-new">
      <div className="status-icon">üí≥</div>
      <h3>Enable Payments</h3>
      <p>Start receiving tips, commissions, and subscriptions from your fans</p>

      {error && <div className="setup-error">{error}</div>}

      <div className="setup-benefits">
        <h4>What You Get:</h4>
        <ul>
          <li>‚úÖ Receive payments directly to your bank account</li>
          <li>‚úÖ Automatic payouts every 2 days</li>
          <li>‚úÖ Accept cards, crypto (BTC/ETH), Cash App, ACH</li>
          <li>‚úÖ Tax forms (1099-K) generated automatically</li>
          <li>‚úÖ Full payment history & analytics</li>
          <li>‚úÖ Keep 100% of earnings (Stripe processing ~2.9% only)</li>
          <li>‚úÖ Zero platform fees on tier purchases ($50-$1000)</li>
        </ul>
      </div>

      <div className="setup-process">
        <h4>Setup Process (5 minutes):</h4>
        <ol>
          <li>Click "Enable Payments" below</li>
          <li>Enter your legal name & SSN/Tax ID (securely stored by Stripe)</li>
          <li>Add your bank account for payouts</li>
          <li>Verify your identity (takes 1-2 days)</li>
          <li>Start receiving payments!</li>
        </ol>
      </div>

      <div className="tax-info">
        <h4>üìã Tax Compliance Made Easy:</h4>
        <p><strong>Stripe handles everything:</strong></p>
        <ul>
          <li>Collects W-9 (US) or W-8 (International) during setup</li>
          <li>Generates 1099-K tax forms automatically</li>
          <li>Files forms with IRS on your behalf</li>
          <li>You receive forms by January 31st</li>
          <li>Only required if you earn $600+/year</li>
        </ul>
      </div>

      <button
        onClick={handleEnablePayments}
        className="btn-enable-payments"
        disabled={loading}
      >
        {loading ? 'Starting setup...' : 'Enable Payments'}
      </button>

      <div className="setup-footer">
        <small>
          üîí Secure setup powered by Stripe. Your sensitive info is never stored on our servers.
        </small>
      </div>
    </div>
  );
}

export default CreatorPaymentSetup;

import React, { useState } from 'react';

/**
 * Creator Content Pricing Component
 * Allows creators to set:
 * 1. Regular subscription pricing (daily, weekly, monthly, yearly)
 * 2. Special release custom pricing (one-time payments)
 */
export default function CreatorPricing({ contentId, onSave }) {
  const [pricingType, setPricingType] = useState('subscription'); // 'subscription' or 'special'
  const [price, setPrice] = useState('');
  const [frequency, setFrequency] = useState('monthly');
  const [specialPrice, setSpecialPrice] = useState('');

  const handleSave = () => {
    const pricingData = {
      contentId,
      type: pricingType,
      ...(pricingType === 'subscription' 
        ? { price: parseFloat(price), frequency }
        : { price: parseFloat(specialPrice), frequency: 'one-time' }
      ),
      createdAt: new Date().toISOString()
    };

    if (onSave) {
      onSave(pricingData);
    }

    // Store in localStorage for now (move to backend later)
    const existingPricing = JSON.parse(localStorage.getItem('creatorPricing') || '{}');
    existingPricing[contentId] = pricingData;
    localStorage.setItem('creatorPricing', JSON.stringify(existingPricing));

    alert('Pricing saved successfully!');
  };

  return (
    <div style={{
      padding: '2rem',
      background: 'rgba(0,0,0,0.3)',
      borderRadius: '12px',
      maxWidth: '600px'
    }}>
      <h3 style={{ marginBottom: '1.5rem' }}>üí∞ Set Your Pricing</h3>

      {/* Pricing Type Selector */}
      <div style={{ marginBottom: '1.5rem' }}>
        <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 600 }}>
          Pricing Type
        </label>
        <div style={{ display: 'flex', gap: '1rem' }}>
          <button
            onClick={() => setPricingType('subscription')}
            style={{
              flex: 1,
              padding: '1rem',
              background: pricingType === 'subscription' ? '#667eea' : 'rgba(255,255,255,0.1)',
              border: 'none',
              borderRadius: '8px',
              color: 'white',
              cursor: 'pointer',
              fontWeight: 600
            }}
          >
            üìÖ Subscription
          </button>
          <button
            onClick={() => setPricingType('special')}
            style={{
              flex: 1,
              padding: '1rem',
              background: pricingType === 'special' ? '#667eea' : 'rgba(255,255,255,0.1)',
              border: 'none',
              borderRadius: '8px',
              color: 'white',
              cursor: 'pointer',
              fontWeight: 600
            }}
          >
            ‚≠ê Special Release
          </button>
        </div>
      </div>

      {/* Subscription Pricing */}
      {pricingType === 'subscription' && (
        <>
          <div style={{ marginBottom: '1.5rem' }}>
            <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 600 }}>
              Price ($)
            </label>
            <input
              type="number"
              min="0"
              step="0.01"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
              placeholder="9.99"
              style={{
                width: '100%',
                padding: '0.75rem',
                borderRadius: '8px',
                border: '1px solid rgba(255,255,255,0.2)',
                background: 'rgba(0,0,0,0.3)',
                color: 'white',
                fontSize: '1rem'
              }}
            />
          </div>

          <div style={{ marginBottom: '1.5rem' }}>
            <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 600 }}>
              Billing Frequency
            </label>
            <select
              value={frequency}
              onChange={(e) => setFrequency(e.target.value)}
              style={{
                width: '100%',
                padding: '0.75rem',
                borderRadius: '8px',
                border: '1px solid rgba(255,255,255,0.2)',
                background: 'rgba(0,0,0,0.3)',
                color: 'white',
                fontSize: '1rem'
              }}
            >
              <option value="per-view">Pay Per View</option>
              <option value="daily">Daily</option>
              <option value="weekly">Weekly</option>
              <option value="monthly">Monthly</option>
              <option value="yearly">Yearly</option>
            </select>
          </div>

          <p style={{ fontSize: '0.9rem', opacity: 0.8, marginBottom: '1rem' }}>
            Fans will be charged ${price || '0.00'} {frequency === 'per-view' ? 'each time they view' : frequency}
          </p>
        </>
      )}

      {/* Special Release Pricing */}
      {pricingType === 'special' && (
        <>
          <div style={{ marginBottom: '1.5rem' }}>
            <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 600 }}>
              Special Release Price ($)
            </label>
            <input
              type="number"
              min="0"
              step="0.01"
              value={specialPrice}
              onChange={(e) => setSpecialPrice(e.target.value)}
              placeholder="19.99"
              style={{
                width: '100%',
                padding: '0.75rem',
                borderRadius: '8px',
                border: '1px solid rgba(255,255,255,0.2)',
                background: 'rgba(0,0,0,0.3)',
                color: 'white',
                fontSize: '1rem'
              }}
            />
          </div>

          <div style={{
            padding: '1rem',
            background: 'rgba(102, 126, 234, 0.2)',
            borderRadius: '8px',
            marginBottom: '1rem'
          }}>
            <p style={{ fontSize: '0.9rem', margin: 0 }}>
              üí° <strong>Special Release:</strong> One-time payment for exclusive content.
              This is perfect for limited editions, special events, or premium releases!
            </p>
          </div>

          <p style={{ fontSize: '0.9rem', opacity: 0.8, marginBottom: '1rem' }}>
            Fans will pay ${specialPrice || '0.00'} one time to access this content
          </p>
        </>
      )}

      {/* Save Button */}
      <button
        onClick={handleSave}
        disabled={!price && !specialPrice}
        style={{
          width: '100%',
          padding: '1rem',
          background: (!price && !specialPrice) ? 'rgba(255,255,255,0.1)' : '#667eea',
          border: 'none',
          borderRadius: '8px',
          color: 'white',
          fontSize: '1rem',
          fontWeight: 600,
          cursor: (!price && !specialPrice) ? 'not-allowed' : 'pointer'
        }}
      >
        Save Pricing
      </button>
    </div>
  );
}

