import React, { useState, useEffect } from 'react';
import {
    registerOwnerDevice,
    getTrustedDevices,
    removeDevice,
    getCurrentDeviceInfo,
    generateRecoveryPassphrase,
    linkDeviceWithPassphrase
} from '../utils/deviceAuth';

/**
 * Device Manager - Manage trusted devices and recovery
 */
export default function DeviceManager({ isOwner }) {
    const [devices, setDevices] = useState([]);
    const [currentDevice, setCurrentDevice] = useState(null);
    const [showRecovery, setShowRecovery] = useState(false);
    const [recoveryPassphrase, setRecoveryPassphrase] = useState('');
    const [showLinkDevice, setShowLinkDevice] = useState(false);
    const [linkPassphrase, setLinkPassphrase] = useState('');
    const [linkDeviceType, setLinkDeviceType] = useState('mobile');

    useEffect(() => {
        loadDeviceInfo();
    }, []);

    const loadDeviceInfo = () => {
        const info = getCurrentDeviceInfo();
        setCurrentDevice(info);
        setDevices(getTrustedDevices());
    };

    const handleRegisterDevice = () => {
        const deviceType = /Mobile|Android|iPhone|iPad/i.test(navigator.userAgent) ? 'mobile' : 'desktop';
        registerOwnerDevice(deviceType);
        loadDeviceInfo();
        alert('‚úÖ This device has been registered as a trusted device!');
    };

    const handleGenerateRecovery = () => {
        const passphrase = generateRecoveryPassphrase();
        setRecoveryPassphrase(passphrase);
        setShowRecovery(true);
    };

    const handleCopyPassphrase = () => {
        navigator.clipboard.writeText(recoveryPassphrase);
        alert('‚úÖ Recovery passphrase copied to clipboard!\n\nStore this somewhere safe - you\'ll need it to add new devices.');
    };

    const handleLinkDevice = () => {
        const result = linkDeviceWithPassphrase(linkPassphrase, linkDeviceType);
        if (result.success) {
            alert(`‚úÖ ${result.message}\n\nThis device is now trusted and will auto-login.`);
            setShowLinkDevice(false);
            setLinkPassphrase('');
            loadDeviceInfo();
        } else {
            alert(`‚ùå ${result.error}\n\nPlease check your recovery passphrase and try again.`);
        }
    };

    const handleRemoveDevice = (deviceId) => {
        if (confirm('Are you sure you want to remove this device?\n\nIt will no longer auto-login.')) {
            removeDevice(deviceId);
            loadDeviceInfo();
            alert('‚úÖ Device removed from trusted devices list.');
        }
    };

    if (!isOwner) {
        return null;
    }

    return (
        <div style={{
            background: 'linear-gradient(135deg, #1e1e1e 0%, #2d2d2d 100%)',
            padding: '30px',
            borderRadius: '20px',
            color: '#fff',
            maxWidth: '900px',
            margin: '30px auto'
        }}>
            <h2 style={{ fontSize: '28px', marginBottom: '10px', fontWeight: 'bold' }}>
                üîê Device Management
            </h2>
            <p style={{ opacity: 0.7, marginBottom: '30px' }}>
                Manage trusted devices and recovery access
            </p>

            {/* Current Device Status */}
            <div style={{
                background: currentDevice?.isTrusted ? 'rgba(34, 197, 94, 0.1)' : 'rgba(239, 68, 68, 0.1)',
                border: `2px solid ${currentDevice?.isTrusted ? '#22c55e' : '#ef4444'}`,
                padding: '20px',
                borderRadius: '12px',
                marginBottom: '30px'
            }}>
                <h3 style={{ fontSize: '20px', marginBottom: '15px' }}>üì± Current Device</h3>
                <div style={{ fontSize: '14px', opacity: 0.9, lineHeight: '1.8' }}>
                    <div><strong>Device ID:</strong> {currentDevice?.deviceId || 'Unknown'}</div>
                    <div><strong>Status:</strong> {currentDevice?.isTrusted ? '‚úÖ Trusted (Auto-login enabled)' : '‚ùå Not trusted'}</div>
                    <div><strong>Type:</strong> {/Mobile|Android|iPhone|iPad/i.test(navigator.userAgent) ? 'üì± Mobile' : 'üíª Desktop'}</div>
                </div>

                {!currentDevice?.isTrusted && (
                    <button
                        onClick={handleRegisterDevice}
                        style={{
                            marginTop: '15px',
                            padding: '12px 24px',
                            background: '#22c55e',
                            color: '#fff',
                            border: 'none',
                            borderRadius: '8px',
                            cursor: 'pointer',
                            fontWeight: 'bold',
                            fontSize: '14px'
                        }}
                    >
                        ‚úÖ Register This Device
                    </button>
                )}
            </div>

            {/* Recovery System */}
            <div style={{
                background: 'rgba(59, 130, 246, 0.1)',
                border: '2px solid #3b82f6',
                padding: '20px',
                borderRadius: '12px',
                marginBottom: '30px'
            }}>
                <h3 style={{ fontSize: '20px', marginBottom: '15px' }}>üîë Recovery System</h3>
                <p style={{ fontSize: '14px', opacity: 0.9, marginBottom: '15px' }}>
                    Generate a recovery passphrase to link new devices if you lose access to your current ones.
                    <br />
                    <strong>Store this passphrase safely - it's your backup!</strong>
                </p>

                {!showRecovery ? (
                    <button
                        onClick={handleGenerateRecovery}
                        style={{
                            padding: '12px 24px',
                            background: '#3b82f6',
                            color: '#fff',
                            border: 'none',
                            borderRadius: '8px',
                            cursor: 'pointer',
                            fontWeight: 'bold',
                            fontSize: '14px'
                        }}
                    >
                        üîë Generate Recovery Passphrase
                    </button>
                ) : (
                    <div>
                        <div style={{
                            background: 'rgba(0, 0, 0, 0.3)',
                            padding: '15px',
                            borderRadius: '8px',
                            marginBottom: '15px',
                            fontFamily: 'monospace',
                            fontSize: '14px',
                            wordBreak: 'break-all'
                        }}>
                            {recoveryPassphrase}
                        </div>
                        <button
                            onClick={handleCopyPassphrase}
                            style={{
                                padding: '10px 20px',
                                background: '#10b981',
                                color: '#fff',
                                border: 'none',
                                borderRadius: '8px',
                                cursor: 'pointer',
                                fontWeight: 'bold',
                                fontSize: '14px'
                            }}
                        >
                            üìã Copy Passphrase
                        </button>
                    </div>
                )}
            </div>

            {/* Link New Device */}
            <div style={{
                background: 'rgba(168, 85, 247, 0.1)',
                border: '2px solid #a855f7',
                padding: '20px',
                borderRadius: '12px',
                marginBottom: '30px'
            }}>
                <h3 style={{ fontSize: '20px', marginBottom: '15px' }}>üì≤ Link New Device</h3>
                <p style={{ fontSize: '14px', opacity: 0.9, marginBottom: '15px' }}>
                    Use your recovery passphrase to add a new phone or computer.
                </p>

                {!showLinkDevice ? (
                    <button
                        onClick={() => setShowLinkDevice(true)}
                        style={{
                            padding: '12px 24px',
                            background: '#a855f7',
                            color: '#fff',
                            border: 'none',
                            borderRadius: '8px',
                            cursor: 'pointer',
                            fontWeight: 'bold',
                            fontSize: '14px'
                        }}
                    >
                        üì≤ Link Device
                    </button>
                ) : (
                    <div>
                        <select
                            value={linkDeviceType}
                            onChange={(e) => setLinkDeviceType(e.target.value)}
                            style={{
                                width: '100%',
                                padding: '12px',
                                background: 'rgba(0, 0, 0, 0.3)',
                                border: '1px solid rgba(255, 255, 255, 0.2)',
                                borderRadius: '8px',
                                color: '#fff',
                                marginBottom: '15px',
                                fontSize: '14px'
                            }}
                        >
                            <option value="mobile">üì± Mobile Device</option>
                            <option value="desktop">üíª Desktop/Laptop</option>
                        </select>

                        <input
                            type="text"
                            placeholder="Enter recovery passphrase..."
                            value={linkPassphrase}
                            onChange={(e) => setLinkPassphrase(e.target.value)}
                            style={{
                                width: '100%',
                                padding: '12px',
                                background: 'rgba(0, 0, 0, 0.3)',
                                border: '1px solid rgba(255, 255, 255, 0.2)',
                                borderRadius: '8px',
                                color: '#fff',
                                marginBottom: '15px',
                                fontSize: '14px'
                            }}
                        />

                        <div style={{ display: 'flex', gap: '10px' }}>
                            <button
                                onClick={handleLinkDevice}
                                disabled={!linkPassphrase.trim()}
                                style={{
                                    flex: 1,
                                    padding: '12px',
                                    background: linkPassphrase.trim() ? '#10b981' : '#4b5563',
                                    color: '#fff',
                                    border: 'none',
                                    borderRadius: '8px',
                                    cursor: linkPassphrase.trim() ? 'pointer' : 'not-allowed',
                                    fontWeight: 'bold',
                                    fontSize: '14px'
                                }}
                            >
                                ‚úÖ Link This Device
                            </button>
                            <button
                                onClick={() => {
                                    setShowLinkDevice(false);
                                    setLinkPassphrase('');
                                }}
                                style={{
                                    padding: '12px 24px',
                                    background: '#6b7280',
                                    color: '#fff',
                                    border: 'none',
                                    borderRadius: '8px',
                                    cursor: 'pointer',
                                    fontSize: '14px'
                                }}
                            >
                                Cancel
                            </button>
                        </div>
                    </div>
                )}
            </div>

            {/* Trusted Devices List */}
            <div>
                <h3 style={{ fontSize: '20px', marginBottom: '15px' }}>üì± Trusted Devices ({devices.length})</h3>
                {devices.length === 0 ? (
                    <p style={{ opacity: 0.7, fontSize: '14px' }}>No trusted devices yet. Register this device to enable auto-login.</p>
                ) : (
                    <div style={{ display: 'grid', gap: '15px' }}>
                        {devices.map((device) => (
                            <div
                                key={device.id}
                                style={{
                                    background: device.id === currentDevice?.deviceId ? 'rgba(34, 197, 94, 0.1)' : 'rgba(255, 255, 255, 0.05)',
                                    border: device.id === currentDevice?.deviceId ? '2px solid #22c55e' : '1px solid rgba(255, 255, 255, 0.1)',
                                    padding: '15px',
                                    borderRadius: '12px',
                                    display: 'flex',
                                    justifyContent: 'space-between',
                                    alignItems: 'center'
                                }}
                            >
                                <div style={{ fontSize: '14px' }}>
                                    <div style={{ fontWeight: 'bold', marginBottom: '5px' }}>
                                        {device.type === 'mobile' ? 'üì±' : 'üíª'} {device.type === 'mobile' ? 'Mobile Device' : 'Desktop'}
                                        {device.id === currentDevice?.deviceId && ' (This Device)'}
                                    </div>
                                    <div style={{ opacity: 0.7, fontSize: '12px' }}>
                                        Registered: {new Date(device.registeredAt).toLocaleDateString()}
                                    </div>
                                    <div style={{ opacity: 0.7, fontSize: '12px' }}>
                                        Last access: {new Date(device.lastAccess).toLocaleString()}
                                    </div>
                                </div>
                                <button
                                    onClick={() => handleRemoveDevice(device.id)}
                                    style={{
                                        padding: '8px 16px',
                                        background: '#ef4444',
                                        color: '#fff',
                                        border: 'none',
                                        borderRadius: '6px',
                                        cursor: 'pointer',
                                        fontSize: '12px'
                                    }}
                                >
                                    üóëÔ∏è Remove
                                </button>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
}

import React, { useState, useEffect } from 'react';
import './Discovery.css';

const API_URL = import.meta.env.VITE_API_URL || 'http://localhost:3001';

/**
 * Creator Discovery Component
 * Search, trending, recommendations, featured creators
 */
export default function Discovery() {
    const [searchQuery, setSearchQuery] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const [trending, setTrending] = useState([]);
    const [featured, setFeatured] = useState([]);
    const [niches, setNiches] = useState([]);
    const [selectedNiche, setSelectedNiche] = useState(null);
    const [loading, setLoading] = useState(false);
    const [activeTab, setActiveTab] = useState('featured'); // featured, search, trending, niches

    useEffect(() => {
        loadFeatured();
        loadTrending();
        loadNiches();
    }, []);

    const loadFeatured = async () => {
        try {
            const response = await fetch(`${API_URL}/api/discovery/featured`);
            const data = await response.json();
            setFeatured(data.featured || []);
        } catch (error) {
            console.error('Failed to load featured creators:', error);
        }
    };

    const loadTrending = async () => {
        try {
            const response = await fetch(`${API_URL}/api/discovery/trending?limit=10`);
            const data = await response.json();
            setTrending(data.trending || []);
        } catch (error) {
            console.error('Failed to load trending creators:', error);
        }
    };

    const loadNiches = async () => {
        try {
            const response = await fetch(`${API_URL}/api/discovery/niches`);
            const data = await response.json();
            setNiches(data.niches || []);
        } catch (error) {
            console.error('Failed to load niches:', error);
        }
    };

    const handleSearch = async (e) => {
        e.preventDefault();
        if (!searchQuery.trim()) return;

        setLoading(true);
        try {
            const response = await fetch(
                `${API_URL}/api/discovery/search?query=${encodeURIComponent(searchQuery)}`
            );
            const data = await response.json();
            setSearchResults(data.results || []);
            setActiveTab('search');
        } catch (error) {
            console.error('Search failed:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleNicheSelect = async (niche) => {
        setSelectedNiche(niche);
        setLoading(true);
        try {
            const response = await fetch(
                `${API_URL}/api/discovery/search?niche=${encodeURIComponent(niche)}`
            );
            const data = await response.json();
            setSearchResults(data.results || []);
            setActiveTab('search');
        } catch (error) {
            console.error('Niche search failed:', error);
        } finally {
            setLoading(false);
        }
    };

    const CreatorCard = ({ creator }) => (
        <div className="creator-card">
            <img 
                src={creator.avatar_url || '/default-avatar.png'} 
                alt={creator.display_name}
                className="creator-avatar"
            />
            <div className="creator-info">
                <h3>{creator.display_name}</h3>
                <p className="creator-username">@{creator.username}</p>
                {creator.niche && <span className="creator-niche">{creator.niche}</span>}
                {creator.verified && <span className="verified-badge">‚úì</span>}
                <p className="creator-bio">{creator.bio?.substring(0, 100)}</p>
                <div className="creator-stats">
                    <span>{creator.follower_count || 0} followers</span>
                    {creator.tags && (
                        <div className="creator-tags">
                            {creator.tags.slice(0, 3).map(tag => (
                                <span key={tag} className="tag">{tag}</span>
                            ))}
                        </div>
                    )}
                </div>
                <button className="btn-follow">Follow</button>
            </div>
        </div>
    );

    return (
        <div className="discovery-page">
            <header className="discovery-header">
                <h1>Discover Creators</h1>
                <form onSubmit={handleSearch} className="search-form">
                    <input
                        type="text"
                        placeholder="Search creators, tags, or niches..."
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                        className="search-input"
                    />
                    <button type="submit" className="btn-search" disabled={loading}>
                        {loading ? 'Searching...' : 'Search'}
                    </button>
                </form>
            </header>

            <div className="discovery-tabs">
                <button 
                    className={activeTab === 'featured' ? 'tab active' : 'tab'}
                    onClick={() => setActiveTab('featured')}
                >
                    Featured
                </button>
                <button 
                    className={activeTab === 'trending' ? 'tab active' : 'tab'}
                    onClick={() => setActiveTab('trending')}
                >
                    Trending
                </button>
                <button 
                    className={activeTab === 'niches' ? 'tab active' : 'tab'}
                    onClick={() => setActiveTab('niches')}
                >
                    By Niche
                </button>
                {searchResults.length > 0 && (
                    <button 
                        className={activeTab === 'search' ? 'tab active' : 'tab'}
                        onClick={() => setActiveTab('search')}
                    >
                        Search Results ({searchResults.length})
                    </button>
                )}
            </div>

            <div className="discovery-content">
                {activeTab === 'featured' && (
                    <div className="creators-grid">
                        {featured.map(creator => (
                            <CreatorCard key={creator.id} creator={creator} />
                        ))}
                    </div>
                )}

                {activeTab === 'trending' && (
                    <div className="creators-grid">
                        {trending.map(creator => (
                            <CreatorCard key={creator.id} creator={creator} />
                        ))}
                    </div>
                )}

                {activeTab === 'niches' && (
                    <div className="niches-list">
                        {niches.map(niche => (
                            <div 
                                key={niche.name} 
                                className="niche-card"
                                onClick={() => handleNicheSelect(niche.name)}
                            >
                                <h3>{niche.name}</h3>
                                <p>{niche.count} creators</p>
                            </div>
                        ))}
                    </div>
                )}

                {activeTab === 'search' && (
                    <div>
                        {selectedNiche && (
                            <div className="search-info">
                                <p>Showing creators in: <strong>{selectedNiche}</strong></p>
                                <button onClick={() => setSelectedNiche(null)}>Clear filter</button>
                            </div>
                        )}
                        <div className="creators-grid">
                            {searchResults.map(creator => (
                                <CreatorCard key={creator.id} creator={creator} />
                            ))}
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
}

