/**
 * Docked Console: Real-time governance visibility for Mico
 * Streams artifacts, governance decisions, and policy overrides
 */

import React, { useState, useEffect, useRef } from 'react';
import CommandPanel from './CommandPanel';
import './DockedConsole.css';

const API_BASE = import.meta.env.VITE_API_URL || 'http://localhost:3001';

export default function DockedConsole({ minimized = false }) {
  const [artifacts, setArtifacts] = useState([]);
  const [governanceRecords, setGovernanceRecords] = useState([]);
  const [overrides, setOverrides] = useState([]);
  const [activeTab, setActiveTab] = useState('artifacts'); // artifacts, governance, overrides, command
  const [isMinimized, setIsMinimized] = useState(minimized);
  const [isConnected, setIsConnected] = useState(false);
  const eventSourceRef = useRef(null);

  // Connect to SSE stream for real-time artifacts
  useEffect(() => {
    const connectSSE = () => {
      try {
        const eventSource = new EventSource(`${API_BASE}/api/governance/artifacts/stream`);
        eventSourceRef.current = eventSource;

        eventSource.onopen = () => {
          console.log('üîå DockedConsole SSE connected');
          setIsConnected(true);
        };

        eventSource.onmessage = (event) => {
          try {
            const data = JSON.parse(event.data);

            if (data.type === 'connected') {
              console.log('üì° SSE stream established:', data.timestamp);
            } else if (data.type === 'artifact') {
              setArtifacts((prev) => [data.artifact, ...prev].slice(0, 50)); // Keep last 50
            }
          } catch (error) {
            console.error('Failed to parse SSE message:', error);
          }
        };

        eventSource.onerror = (error) => {
          console.error('SSE error:', error);
          setIsConnected(false);
          eventSource.close();

          // Reconnect after 5 seconds
          setTimeout(connectSSE, 5000);
        };
      } catch (error) {
        console.error('Failed to connect SSE:', error);
        setIsConnected(false);
      }
    };

    connectSSE();

    return () => {
      if (eventSourceRef.current) {
        eventSourceRef.current.close();
      }
    };
  }, []);

  // Load initial data
  useEffect(() => {
    loadArtifacts();
    loadGovernance();
    loadOverrides();
  }, []);

  const loadArtifacts = async () => {
    try {
      const res = await fetch(`${API_BASE}/api/governance/artifacts/recent?limit=50`);
      const data = await res.json();
      if (data.artifacts) {
        setArtifacts(data.artifacts);
      }
    } catch (error) {
      console.error('Failed to load artifacts:', error);
    }
  };

  const loadGovernance = async () => {
    try {
      const res = await fetch(`${API_BASE}/api/governance/notary/history?limit=20`);
      const data = await res.json();
      if (data.history) {
        setGovernanceRecords(data.history);
      }
    } catch (error) {
      console.error('Failed to load governance records:', error);
    }
  };

  const loadOverrides = async () => {
    try {
      const res = await fetch(`${API_BASE}/api/governance/overrides`);
      const data = await res.json();
      if (data.overrides) {
        setOverrides(data.overrides);
      }
    } catch (error) {
      console.error('Failed to load overrides:', error);
    }
  };

  const formatTimestamp = (timestamp) => {
    const date = new Date(timestamp);
    return date.toLocaleTimeString('en-US', { hour12: false });
  };

  const getAgentIcon = (agentType) => {
    const icons = {
      moderation_sentinel: 'üõ°Ô∏è',
      content_companion: '‚ú®',
      automation_clerk: 'ü§ñ',
      profile_architect: 'üé®',
      legacy_archivist: 'üìú',
    };
    return icons[agentType] || '‚ö°';
  };

  const getGovernanceIcon = (actionType) => {
    const icons = {
      threshold_override: '‚öôÔ∏è',
      policy_escalation: 'üö®',
      emergency_action: 'üö®',
      authority_grant: 'üîë',
      authority_revoke: 'üîí',
      guard_mode_toggle: 'üõ°Ô∏è',
      manual_review: 'üëÅÔ∏è',
    };
    return icons[actionType] || '‚öñÔ∏è';
  };

  const handleCommandExecuted = (commandData) => {
    console.log('Command executed:', commandData);
    // Refresh data after command execution
    loadArtifacts();
    loadGovernance();
    loadOverrides();
  };

  if (isMinimized) {
    return (
      <div className="docked-console-minimized" onClick={() => setIsMinimized(false)}>
        <span className="console-indicator">
          {isConnected ? 'üü¢' : 'üî¥'} Mico Console
        </span>
      </div>
    );
  }

  return (
    <div className="docked-console">
      <div className="console-header">
        <div className="console-title">
          <span className="console-icon">‚ö°</span>
          <span>Mico Governance Console</span>
          <span className={`console-status ${isConnected ? 'connected' : 'disconnected'}`}>
            {isConnected ? 'üü¢ Live' : 'üî¥ Offline'}
          </span>
        </div>
        <button className="console-minimize" onClick={() => setIsMinimized(true)}>
          ‚àí
        </button>
      </div>

      <div className="console-tabs">
        <button
          className={`console-tab ${activeTab === 'artifacts' ? 'active' : ''}`}
          onClick={() => setActiveTab('artifacts')}
        >
          üìú Artifacts ({artifacts.length})
        </button>
        <button
          className={`console-tab ${activeTab === 'governance' ? 'active' : ''}`}
          onClick={() => setActiveTab('governance')}
        >
          ‚öñÔ∏è Governance ({governanceRecords.length})
        </button>
        <button
          className={`console-tab ${activeTab === 'overrides' ? 'active' : ''}`}
          onClick={() => setActiveTab('overrides')}
        >
          ‚öôÔ∏è Overrides ({overrides.length})
        </button>
        <button
          className={`console-tab ${activeTab === 'command' ? 'active' : ''}`}
          onClick={() => setActiveTab('command')}
        >
          ‚ö° Commands
        </button>
      </div>

      <div className="console-body">
        {activeTab === 'artifacts' && (
          <div className="console-list">
            {artifacts.length === 0 && (
              <div className="console-empty">No artifacts yet...</div>
            )}
            {artifacts.map((artifact) => (
              <div key={artifact.id} className="console-item artifact-item">
                <div className="console-item-header">
                  <span className="artifact-icon">
                    {getAgentIcon(artifact.agent_type)}
                  </span>
                  <span className="artifact-agent">{artifact.agent_type}</span>
                  <span className="artifact-time">
                    {formatTimestamp(artifact.timestamp)}
                  </span>
                </div>
                <div className="console-item-body">
                  <div className="artifact-action">{artifact.action}</div>
                  {artifact.entity_type && (
                    <div className="artifact-entity">
                      {artifact.entity_type}
                      {artifact.entity_id && `: ${artifact.entity_id.substring(0, 8)}`}
                    </div>
                  )}
                  {artifact.authority_level && (
                    <span className={`authority-badge ${artifact.authority_level}`}>
                      {artifact.authority_level}
                    </span>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}

        {activeTab === 'governance' && (
          <div className="console-list">
            {governanceRecords.length === 0 && (
              <div className="console-empty">No governance records yet...</div>
            )}
            {governanceRecords.map((record) => (
              <div key={record.id} className="console-item governance-item">
                <div className="console-item-header">
                  <span className="governance-icon">
                    {getGovernanceIcon(record.action_type)}
                  </span>
                  <span className="governance-action">{record.action_type}</span>
                  <span className="governance-time">
                    {formatTimestamp(record.timestamp)}
                  </span>
                </div>
                <div className="console-item-body">
                  <div className="governance-justification">
                    {record.justification}
                  </div>
                  <div className="governance-authorizer">
                    by {record.authorized_by}
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}

        {activeTab === 'overrides' && (
          <div className="console-list">
            {overrides.length === 0 && (
              <div className="console-empty">No active overrides...</div>
            )}
            {overrides.map((override, idx) => (
              <div key={idx} className="console-item override-item">
                <div className="console-item-header">
                  <span className="override-icon">‚öôÔ∏è</span>
                  <span className="override-key">{override.overrideKey}</span>
                  {override.expiresAt && (
                    <span className="override-expires">
                      ‚è∞ {formatTimestamp(override.expiresAt)}
                    </span>
                  )}
                </div>
                <div className="console-item-body">
                  <div className="override-type">{override.overrideType}</div>
                  <div className="override-value">
                    {JSON.stringify(override.overrideValue)}
                  </div>
                  {override.reason && (
                    <div className="override-reason">{override.reason}</div>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}

        {activeTab === 'command' && (
          <div className="command-tab-content">
            <CommandPanel onCommandExecuted={handleCommandExecuted} />
          </div>
        )}
      </div>

      <div className="console-footer">
        <button className="console-refresh" onClick={loadArtifacts}>
          üîÑ Refresh
        </button>
        <span className="console-count">
          {activeTab === 'artifacts' && `${artifacts.length} artifacts`}
          {activeTab === 'governance' && `${governanceRecords.length} records`}
          {activeTab === 'overrides' && `${overrides.length} overrides`}
        </span>
      </div>
    </div>
  );
}

import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function DropScheduler({ tier }) {
  const [drops, setDrops] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function fetchDrops() {
      setLoading(true);
      setError(null);
      try {
        const res = await axios.get(`/api/drops?tier=${tier}`);
        setDrops(res.data);
      } catch (e) {
        setError(e.message);
      } finally {
        setLoading(false);
      }
    }
    if (tier) fetchDrops();
  }, [tier]);

  if (loading) return <div>Loading drops...</div>;
  if (error) return <div style={{ color: 'red' }}>{error}</div>;

  return (
    <section className="bg-black text-white py-20 px-6 md:px-24">
      <h2 className="text-4xl font-bold text-center mb-12">Upcoming Drops</h2>
      <div className="space-y-6">
        {drops.map((drop, i) => (
          <div key={i} className="border border-pink-600 rounded-lg p-6">
            <h3 className="text-xl font-bold mb-2">{drop.title}</h3>
            <p className="text-sm text-gray-400 mb-1">Tier Gate: {drop.tierGate}</p>
            <p className="text-sm text-pink-400 mb-1">Ritual Tag: {drop.ritualTag}</p>
            <p className="text-sm">Scheduled: {new Date(drop.timestamp).toLocaleString()}</p>
          </div>
        ))}
      </div>
    </section>
  );
}

import React, { useState, useEffect } from 'react';
import { useAuth } from './AuthSupabase.jsx';

/**
 * EasyPaymentSetup - Simple payment setup for creators
 * No Stripe dashboard needed - just enter bank info and done
 */
export function EasyPaymentSetup({ userId }) {
  const { user } = useAuth();
  const [step, setStep] = useState(1); // 1: info, 2: bank details, 3: done
  const [processing, setProcessing] = useState(false);
  const [error, setError] = useState('');
  const [accountStatus, setAccountStatus] = useState(null);

  const [formData, setFormData] = useState({
    email: user?.email || '',
    country: 'US',
    // Bank info
    accountHolderName: '',
    routingNumber: '',
    accountNumber: '',
    accountType: 'checking', // checking or savings
    // Personal info for verification
    firstName: '',
    lastName: '',
    dateOfBirth: '',
    ssn_last4: '',
    address: '',
    city: '',
    state: '',
    zipCode: ''
  });

  useEffect(() => {
    checkAccountStatus();
  }, [userId]);

  const checkAccountStatus = async () => {
    try {
      const response = await fetch(`/api/stripe-connect/account-status/${userId}`);
      const data = await response.json();
      setAccountStatus(data);

      if (data.connected && data.status === 'active') {
        setStep(3); // Already set up
      }
    } catch (err) {
      console.error('Error checking account status:', err);
    }
  };

  const handleChange = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const handleSetupPayments = async () => {
    setProcessing(true);
    setError('');

    try {
      // Create Stripe Connect account with bank info
      const response = await fetch('/api/stripe-connect/easy-setup', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          userId,
          ...formData
        })
      });

      const data = await response.json();

      if (data.success) {
        setStep(3);
      } else {
        setError(data.error || 'Setup failed. Please try again.');
      }
    } catch (err) {
      setError('Error setting up payments: ' + err.message);
    } finally {
      setProcessing(false);
    }
  };

  if (step === 3) {
    return (
      <div style={{
        background: 'linear-gradient(135deg, #4CAF50 0%, #45a049 100%)',
        borderRadius: '20px',
        padding: '40px',
        color: 'white',
        textAlign: 'center'
      }}>
        <div style={{ fontSize: '64px', marginBottom: '20px' }}>‚úÖ</div>
        <h2 style={{ fontSize: '32px', marginBottom: '15px' }}>
          Payment Setup Complete!
        </h2>
        <p style={{ fontSize: '18px', opacity: 0.9 }}>
          You can now receive tips, commissions, and subscription payments directly to your bank account.
        </p>
        <div style={{
          marginTop: '30px',
          padding: '20px',
          background: 'rgba(255,255,255,0.2)',
          borderRadius: '10px'
        }}>
          <p style={{ fontSize: '16px', marginBottom: '10px' }}>
            üí∞ Platform Fee: {accountStatus?.platformFee || '15%'} (0% if you paid $15+ to unlock tools)
          </p>
          <p style={{ fontSize: '14px', opacity: 0.8 }}>
            Payments are deposited to your bank within 2-7 business days
          </p>
        </div>
      </div>
    );
  }

  return (
    <div style={{
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      borderRadius: '20px',
      padding: '40px',
      color: 'white',
      maxWidth: '700px',
      margin: '0 auto'
    }}>
      <div style={{ textAlign: 'center', marginBottom: '30px' }}>
        <h2 style={{ fontSize: '36px', marginBottom: '10px' }}>
          üí∏ Get Paid
        </h2>
        <p style={{ fontSize: '16px', opacity: 0.9 }}>
          Set up payments in 2 minutes - no Stripe account needed!
        </p>
      </div>

      {/* Progress Bar */}
      <div style={{
        display: 'flex',
        justifyContent: 'space-between',
        marginBottom: '40px'
      }}>
        {[1, 2, 3].map(num => (
          <div key={num} style={{
            flex: 1,
            textAlign: 'center',
            opacity: step >= num ? 1 : 0.5
          }}>
            <div style={{
              width: '40px',
              height: '40px',
              borderRadius: '50%',
              background: step >= num ? 'white' : 'rgba(255,255,255,0.3)',
              color: '#667eea',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              margin: '0 auto 10px',
              fontWeight: 'bold'
            }}>
              {num}
            </div>
            <div style={{ fontSize: '12px' }}>
              {num === 1 ? 'Info' : num === 2 ? 'Bank Details' : 'Done'}
            </div>
          </div>
        ))}
      </div>

      {step === 1 && (
        <div>
          <h3 style={{ fontSize: '24px', marginBottom: '20px' }}>Personal Information</h3>

          <div style={{ marginBottom: '15px' }}>
            <label style={{ display: 'block', marginBottom: '8px' }}>Email</label>
            <input
              type="email"
              value={formData.email}
              onChange={(e) => handleChange('email', e.target.value)}
              style={{
                width: '100%',
                padding: '12px',
                borderRadius: '8px',
                border: 'none',
                fontSize: '16px'
              }}
            />
          </div>

          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '15px', marginBottom: '15px' }}>
            <div>
              <label style={{ display: 'block', marginBottom: '8px' }}>First Name</label>
              <input
                type="text"
                value={formData.firstName}
                onChange={(e) => handleChange('firstName', e.target.value)}
                style={{
                  width: '100%',
                  padding: '12px',
                  borderRadius: '8px',
                  border: 'none',
                  fontSize: '16px'
                }}
              />
            </div>
            <div>
              <label style={{ display: 'block', marginBottom: '8px' }}>Last Name</label>
              <input
                type="text"
                value={formData.lastName}
                onChange={(e) => handleChange('lastName', e.target.value)}
                style={{
                  width: '100%',
                  padding: '12px',
                  borderRadius: '8px',
                  border: 'none',
                  fontSize: '16px'
                }}
              />
            </div>
          </div>

          <div style={{ marginBottom: '15px' }}>
            <label style={{ display: 'block', marginBottom: '8px' }}>Date of Birth</label>
            <input
              type="date"
              value={formData.dateOfBirth}
              onChange={(e) => handleChange('dateOfBirth', e.target.value)}
              style={{
                width: '100%',
                padding: '12px',
                borderRadius: '8px',
                border: 'none',
                fontSize: '16px'
              }}
            />
          </div>

          <div style={{ marginBottom: '15px' }}>
            <label style={{ display: 'block', marginBottom: '8px' }}>Last 4 digits of SSN (for verification)</label>
            <input
              type="text"
              value={formData.ssn_last4}
              onChange={(e) => handleChange('ssn_last4', e.target.value)}
              maxLength={4}
              placeholder="1234"
              style={{
                width: '100%',
                padding: '12px',
                borderRadius: '8px',
                border: 'none',
                fontSize: '16px'
              }}
            />
          </div>

          <button
            onClick={() => setStep(2)}
            disabled={!formData.firstName || !formData.lastName || !formData.dateOfBirth || !formData.ssn_last4}
            style={{
              width: '100%',
              background: 'white',
              color: '#667eea',
              border: 'none',
              padding: '15px',
              borderRadius: '10px',
              fontSize: '18px',
              fontWeight: 'bold',
              cursor: 'pointer',
              marginTop: '20px'
            }}
          >
            Next: Bank Details ‚Üí
          </button>
        </div>
      )}

      {step === 2 && (
        <div>
          <h3 style={{ fontSize: '24px', marginBottom: '20px' }}>Bank Account</h3>

          <div style={{ marginBottom: '15px' }}>
            <label style={{ display: 'block', marginBottom: '8px' }}>Account Holder Name</label>
            <input
              type="text"
              value={formData.accountHolderName}
              onChange={(e) => handleChange('accountHolderName', e.target.value)}
              placeholder="Full name on account"
              style={{
                width: '100%',
                padding: '12px',
                borderRadius: '8px',
                border: 'none',
                fontSize: '16px'
              }}
            />
          </div>

          <div style={{ marginBottom: '15px' }}>
            <label style={{ display: 'block', marginBottom: '8px' }}>Routing Number</label>
            <input
              type="text"
              value={formData.routingNumber}
              onChange={(e) => handleChange('routingNumber', e.target.value)}
              maxLength={9}
              placeholder="9 digits"
              style={{
                width: '100%',
                padding: '12px',
                borderRadius: '8px',
                border: 'none',
                fontSize: '16px'
              }}
            />
          </div>

          <div style={{ marginBottom: '15px' }}>
            <label style={{ display: 'block', marginBottom: '8px' }}>Account Number</label>
            <input
              type="text"
              value={formData.accountNumber}
              onChange={(e) => handleChange('accountNumber', e.target.value)}
              placeholder="Account number"
              style={{
                width: '100%',
                padding: '12px',
                borderRadius: '8px',
                border: 'none',
                fontSize: '16px'
              }}
            />
          </div>

          <div style={{ marginBottom: '15px' }}>
            <label style={{ display: 'block', marginBottom: '8px' }}>Account Type</label>
            <select
              value={formData.accountType}
              onChange={(e) => handleChange('accountType', e.target.value)}
              style={{
                width: '100%',
                padding: '12px',
                borderRadius: '8px',
                border: 'none',
                fontSize: '16px'
              }}
            >
              <option value="checking">Checking</option>
              <option value="savings">Savings</option>
            </select>
          </div>

          <div style={{
            background: 'rgba(255,255,255,0.1)',
            padding: '15px',
            borderRadius: '10px',
            marginBottom: '20px',
            fontSize: '14px'
          }}>
            üîí Your bank info is encrypted and secure. We use Stripe for all payment processing.
          </div>

          {error && (
            <div style={{
              background: 'rgba(244, 67, 54, 0.2)',
              border: '2px solid #f44336',
              padding: '15px',
              borderRadius: '10px',
              marginBottom: '20px'
            }}>
              {error}
            </div>
          )}

          <div style={{ display: 'flex', gap: '15px' }}>
            <button
              onClick={() => setStep(1)}
              style={{
                flex: 1,
                background: 'rgba(255,255,255,0.2)',
                color: 'white',
                border: 'none',
                padding: '15px',
                borderRadius: '10px',
                fontSize: '18px',
                fontWeight: 'bold',
                cursor: 'pointer'
              }}
            >
              ‚Üê Back
            </button>
            <button
              onClick={handleSetupPayments}
              disabled={processing || !formData.accountHolderName || !formData.routingNumber || !formData.accountNumber}
              style={{
                flex: 2,
                background: processing ? '#999' : 'white',
                color: '#667eea',
                border: 'none',
                padding: '15px',
                borderRadius: '10px',
                fontSize: '18px',
                fontWeight: 'bold',
                cursor: processing ? 'not-allowed' : 'pointer'
              }}
            >
              {processing ? '‚è≥ Setting up...' : '‚úÖ Complete Setup'}
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default EasyPaymentSetup;

