import React, { useEffect, useState } from 'react';

export default function EngagementTracker({ children }) {
  const [sessionStart] = useState(Date.now());
  const [events, setEvents] = useState([]);

  useEffect(() => {
    const trackEvent = (event) => {
      const eventData = {
        type: event.type,
        target: event.target.tagName,
        timestamp: Date.now(),
        sessionTime: Date.now() - sessionStart
      };
      setEvents(prev => [...prev.slice(-99), eventData]); // Keep last 100 events
    };

    // Track clicks
    document.addEventListener('click', trackEvent);

    // Track session duration
    const sessionInterval = setInterval(() => {
      const duration = Math.floor((Date.now() - sessionStart) / 1000);
      console.log(`Session duration: ${duration}s, Events: ${events.length}`);
    }, 60000); // Log every minute

    // Track page visibility
    const handleVisibilityChange = () => {
      if (document.hidden) {
        console.log('User left page', { sessionTime: Date.now() - sessionStart, totalEvents: events.length });
      }
    };
    document.addEventListener('visibilitychange', handleVisibilityChange);

    // Cleanup
    return () => {
      document.removeEventListener('click', trackEvent);
      document.removeEventListener('visibilitychange', handleVisibilityChange);
      clearInterval(sessionInterval);

      // Send analytics on unmount
      const finalDuration = Math.floor((Date.now() - sessionStart) / 1000);
      console.log('Session ended', {
        duration: finalDuration,
        totalEvents: events.length,
        events: events.slice(-10) // Last 10 events
      });
    };
  }, [sessionStart, events.length]);

  return <>{children}</>;
}

// src/components/ErrorBoundary.jsx - React error boundary with crash reporting
import React, { Component } from 'react';

class ErrorBoundary extends Component {
  constructor(props) {
    super(props);
    this.state = {
      hasError: false,
      error: null,
    };
  }

  static getDerivedStateFromError(error) {
    return {
      hasError: true,
      error,
    };
  }

  componentDidCatch(error, errorInfo) {
    console.error('[ErrorBoundary] Caught error:', error, errorInfo);
    
    // Report crash to backend
    this.reportCrash(error, errorInfo);
  }

  async reportCrash(error, errorInfo) {
    try {
      const apiUrl = window.__VITE_API_URL__ || 'http://localhost:3001';
      
      await fetch(`${apiUrl}/userfix/feedback/report`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          report_type: 'crash',
          message: `Frontend crash: ${error.message}`,
          page_url: window.location.href,
          severity: 'high',
          metadata: {
            stack: error.stack,
            componentStack: errorInfo.componentStack,
            userAgent: navigator.userAgent,
          },
        }),
      });
    } catch (err) {
      console.error('[ErrorBoundary] Failed to report crash:', err);
    }
  }

  render() {
    if (this.state.hasError) {
      return (
        <div style={{
          padding: '2rem',
          textAlign: 'center',
          maxWidth: '600px',
          margin: '0 auto',
        }}>
          <h1>üòî Something went wrong</h1>
          <p>We've been notified and are working on it.</p>
          <button
            onClick={() => window.location.reload()}
            style={{
              padding: '0.5rem 1rem',
              backgroundColor: '#0066cc',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer',
            }}
          >
            Reload Page
          </button>
          {this.state.error && (
            <details style={{ marginTop: '1rem', textAlign: 'left' }}>
              <summary>Error details</summary>
              <pre style={{
                backgroundColor: '#f5f5f5',
                padding: '1rem',
                borderRadius: '4px',
                overflow: 'auto',
              }}>
                {this.state.error.stack}
              </pre>
            </details>
          )}
        </div>
      );
    }

    return this.props.children;
  }
}

export { ErrorBoundary };
export default ErrorBoundary;

