import React, { useState } from 'react';

/**
 * HelpPopup - Reusable help popup for showing feature descriptions
 * Shows a small "?" button that opens a modal with feature info
 */
export function HelpPopup({ title, features, showOnFirstVisit = false, storageKey }) {
    const [isOpen, setIsOpen] = useState(() => {
        if (showOnFirstVisit && storageKey) {
            const seen = localStorage.getItem(`help_seen_${storageKey}`);
            return !seen;
        }
        return false;
    });

    const handleClose = () => {
        if (storageKey) {
            localStorage.setItem(`help_seen_${storageKey}`, 'true');
        }
        setIsOpen(false);
    };

    if (!isOpen) {
        return (
            <button
                onClick={() => setIsOpen(true)}
                style={{
                    position: 'fixed',
                    bottom: '20px',
                    right: '20px',
                    width: '50px',
                    height: '50px',
                    borderRadius: '50%',
                    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                    border: 'none',
                    color: 'white',
                    fontSize: '24px',
                    cursor: 'pointer',
                    boxShadow: '0 4px 12px rgba(0,0,0,0.3)',
                    zIndex: 1000,
                    transition: 'transform 0.2s'
                }}
                onMouseEnter={(e) => e.target.style.transform = 'scale(1.1)'}
                onMouseLeave={(e) => e.target.style.transform = 'scale(1)'}
                title="Help & Features"
            >
                ?
            </button>
        );
    }

    return (
        <div style={{
            position: 'fixed',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: 'rgba(0,0,0,0.8)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            zIndex: 10000,
            padding: '20px'
        }}>
            <div style={{
                background: 'linear-gradient(135deg, #1e1e2e 0%, #2a2a3e 100%)',
                borderRadius: '20px',
                maxWidth: '800px',
                width: '100%',
                maxHeight: '90vh',
                overflow: 'auto',
                padding: '40px',
                color: 'white',
                position: 'relative',
                boxShadow: '0 20px 60px rgba(0,0,0,0.5)'
            }}>
                {/* Close Button */}
                <button
                    onClick={handleClose}
                    style={{
                        position: 'absolute',
                        top: '20px',
                        right: '20px',
                        background: 'rgba(255,255,255,0.1)',
                        border: 'none',
                        borderRadius: '50%',
                        width: '40px',
                        height: '40px',
                        color: 'white',
                        fontSize: '24px',
                        cursor: 'pointer',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center'
                    }}
                >
                    √ó
                </button>

                {/* Title */}
                <h2 style={{
                    fontSize: '32px',
                    marginBottom: '30px',
                    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                    WebkitBackgroundClip: 'text',
                    WebkitTextFillColor: 'transparent',
                    backgroundClip: 'text'
                }}>
                    {title}
                </h2>

                {/* Features Grid */}
                <div style={{
                    display: 'grid',
                    gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
                    gap: '20px',
                    marginBottom: '30px'
                }}>
                    {features.map((feature, i) => (
                        <div
                            key={i}
                            style={{
                                background: 'rgba(255,255,255,0.05)',
                                borderRadius: '15px',
                                padding: '20px',
                                transition: 'transform 0.2s',
                                cursor: 'default'
                            }}
                        >
                            <div style={{ fontSize: '40px', marginBottom: '15px' }}>{feature.icon}</div>
                            <h3 style={{ fontSize: '18px', marginBottom: '10px', fontWeight: 'bold' }}>
                                {feature.title}
                            </h3>
                            <p style={{ fontSize: '14px', opacity: 0.9, lineHeight: '1.5' }}>
                                {feature.desc}
                            </p>
                        </div>
                    ))}
                </div>

                {/* Got It Button */}
                <button
                    onClick={handleClose}
                    style={{
                        width: '100%',
                        padding: '16px',
                        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                        border: 'none',
                        borderRadius: '12px',
                        color: 'white',
                        fontSize: '18px',
                        fontWeight: 'bold',
                        cursor: 'pointer',
                        transition: 'opacity 0.2s'
                    }}
                    onMouseEnter={(e) => e.target.style.opacity = '0.9'}
                    onMouseLeave={(e) => e.target.style.opacity = '1'}
                >
                    Got it!
                </button>
            </div>
        </div>
    );
}

import React, { useState, useEffect, useRef } from 'react';
import './ImageLazyLoad.css';

/**
 * Lazy loading image component with placeholder
 * Only loads image when it enters viewport
 * Shows loading skeleton until image loads
 */
const ImageLazyLoad = ({
    src,
    alt,
    className = '',
    placeholder = null,
    width,
    height,
    onLoad
}) => {
    const [isLoaded, setIsLoaded] = useState(false);
    const [isInView, setIsInView] = useState(false);
    const imgRef = useRef(null);

    useEffect(() => {
        if (!imgRef.current) return;

        const observer = new IntersectionObserver(
            (entries) => {
                entries.forEach((entry) => {
                    if (entry.isIntersecting) {
                        setIsInView(true);
                        observer.disconnect();
                    }
                });
            },
            {
                rootMargin: '50px', // Start loading 50px before entering viewport
            }
        );

        observer.observe(imgRef.current);

        return () => {
            if (observer) observer.disconnect();
        };
    }, []);

    const handleLoad = () => {
        setIsLoaded(true);
        if (onLoad) onLoad();
    };

    return (
        <div
            ref={imgRef}
            className={`lazy-image-container ${className}`}
            style={{ width, height }}
        >
            {!isLoaded && (
                <div className="lazy-image-placeholder">
                    {placeholder || <div className="lazy-image-skeleton" />}
                </div>
            )}
            {isInView && (
                <img
                    src={src}
                    alt={alt}
                    className={`lazy-image ${isLoaded ? 'loaded' : ''}`}
                    onLoad={handleLoad}
                    loading="lazy"
                    width={width}
                    height={height}
                />
            )}
        </div>
    );
};

export default ImageLazyLoad;

import React, { useState } from 'react';
import { uploadFiles } from '../utils/storageSupabase';
import { useAuth } from './AuthSupabase.jsx';
import './ImageUploader.css';

const MAX_FILES = 10;
const MAX_SIZE_BYTES = 10 * 1024 * 1024; // 10MB
const ALLOWED_TYPES = ['image/jpeg', 'image/png', 'image/gif', 'image/webp'];

function ImageUploaderSupabase({ onUploadComplete }) {
  const [files, setFiles] = useState([]);
  const [previews, setPreviews] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState({});
  const [errors, setErrors] = useState([]);
  const { user } = useAuth();

  const validateFile = (file) => {
    const errors = [];

    if (!ALLOWED_TYPES.includes(file.type)) {
      errors.push(`${file.name}: Invalid file type. Allowed: JPG, PNG, GIF, WEBP`);
    }

    if (file.size > MAX_SIZE_BYTES) {
      errors.push(`${file.name}: File too large. Max size: 10MB`);
    }

    return errors;
  };

  const handleFileSelect = (e) => {
    const selectedFiles = Array.from(e.target.files);
    const validationErrors = [];

    // Check file count
    if (files.length + selectedFiles.length > MAX_FILES) {
      validationErrors.push(`Maximum ${MAX_FILES} files allowed`);
      setErrors(validationErrors);
      return;
    }

    // Validate each file
    const validFiles = [];
    selectedFiles.forEach(file => {
      const fileErrors = validateFile(file);
      if (fileErrors.length > 0) {
        validationErrors.push(...fileErrors);
      } else {
        validFiles.push(file);
      }
    });

    setErrors(validationErrors);

    if (validFiles.length > 0) {
      setFiles(prev => [...prev, ...validFiles]);

      // Generate previews
      validFiles.forEach(file => {
        const reader = new FileReader();
        reader.onloadend = () => {
          setPreviews(prev => [...prev, { file: file.name, url: reader.result }]);
        };
        reader.readAsDataURL(file);
      });
    }
  };

  const handleRemoveFile = (index) => {
    setFiles(prev => prev.filter((_, i) => i !== index));
    setPreviews(prev => prev.filter((_, i) => i !== index));
    setErrors([]);
  };

  const handleUpload = async () => {
    if (files.length === 0) {
      setErrors(['Please select at least one file']);
      return;
    }

    if (!user) {
      setErrors(['You must be logged in to upload files']);
      return;
    }

    setUploading(true);
    setErrors([]);

    try {
      // Upload files to Supabase Storage
      const uploadedFiles = await uploadFiles(files, user.id);

      // Update progress for each file
      uploadedFiles.forEach((file, index) => {
        setUploadProgress(prev => ({
          ...prev,
          [files[index].name]: 100
        }));
      });

      // Notify parent component
      if (onUploadComplete) {
        onUploadComplete(uploadedFiles);
      }

      // Reset state
      setFiles([]);
      setPreviews([]);
      setUploadProgress({});

      alert(`‚úÖ Successfully uploaded ${uploadedFiles.length} file(s)!`);
    } catch (error) {
      console.error('Upload error:', error);
      setErrors([`Upload failed: ${error.message}`]);
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="image-uploader">
      <div className="uploader-header">
        <h3>üì§ Upload Artwork</h3>
        <p className="uploader-subtitle">
          Max {MAX_FILES} files ‚Ä¢ Max 10MB each ‚Ä¢ JPG, PNG, GIF, WEBP
        </p>
      </div>

      {errors.length > 0 && (
        <div className="uploader-errors">
          {errors.map((error, index) => (
            <div key={index} className="error-message">
              ‚ö†Ô∏è {error}
            </div>
          ))}
        </div>
      )}

      <div className="upload-area">
        <input
          type="file"
          id="file-input"
          multiple
          accept={ALLOWED_TYPES.join(',')}
          onChange={handleFileSelect}
          disabled={uploading || files.length >= MAX_FILES}
          style={{ display: 'none' }}
        />

        <label htmlFor="file-input" className={`upload-label ${uploading ? 'disabled' : ''}`}>
          <div className="upload-icon">üé®</div>
          <div className="upload-text">
            <strong>Click to upload</strong> or drag and drop
          </div>
          <div className="upload-hint">
            {files.length}/{MAX_FILES} files selected
          </div>
        </label>
      </div>

      {previews.length > 0 && (
        <div className="preview-grid">
          {previews.map((preview, index) => (
            <div key={index} className="preview-item">
              <img src={preview.url} alt={`Preview ${index + 1}`} />
              <div className="preview-overlay">
                <button
                  onClick={() => handleRemoveFile(index)}
                  className="remove-button"
                  disabled={uploading}
                >
                  ‚úï
                </button>
              </div>
              {uploading && uploadProgress[files[index].name] !== undefined && (
                <div className="upload-progress">
                  <div
                    className="progress-bar"
                    style={{ width: `${uploadProgress[files[index].name]}%` }}
                  />
                  <div className="progress-text">
                    {uploadProgress[files[index].name]}%
                  </div>
                </div>
              )}
            </div>
          ))}
        </div>
      )}

      {files.length > 0 && (
        <div className="upload-actions">
          <button
            onClick={handleUpload}
            className="upload-button"
            disabled={uploading}
          >
            {uploading ? '‚è≥ Uploading...' : `üì§ Upload ${files.length} File(s)`}
          </button>

          <button
            onClick={() => {
              setFiles([]);
              setPreviews([]);
              setErrors([]);
            }}
            className="clear-button"
            disabled={uploading}
          >
            Clear All
          </button>
        </div>
      )}
    </div>
  );
}

export default ImageUploaderSupabase;

import React, { useState } from 'react';

/**
 * InfluencerVerification - Verify social media following to unlock $500 Full Unlock tier
 * Limited to 25 influencers total - First come, first served
 * Verified influencers get $500 Full Unlock for FREE (all tools, no ads, 0% fees)
 */
export function InfluencerVerification({ userId, onVerified }) {
  const [platform, setPlatform] = useState('');
  const [username, setUsername] = useState('');
  const [followers, setFollowers] = useState('');
  const [verifying, setVerifying] = useState(false);
  const [result, setResult] = useState(null);
  const [proofMethod, setProofMethod] = useState('screenshot'); // 'screenshot', 'api', 'code'
  const [screenshot, setScreenshot] = useState(null);
  const [verificationCode, setVerificationCode] = useState('');

  const PLATFORMS = [
    { id: 'youtube', name: 'YouTube', icon: 'üì∫', minFollowers: 10000 },
    { id: 'tiktok', name: 'TikTok', icon: 'üéµ', minFollowers: 10000 },
    { id: 'instagram', name: 'Instagram', icon: 'üì∏', minFollowers: 10000 },
    { id: 'twitter', name: 'Twitter/X', icon: 'üê¶', minFollowers: 10000 },
    { id: 'twitch', name: 'Twitch', icon: 'üéÆ', minFollowers: 5000 },
    { id: 'facebook', name: 'Facebook', icon: 'üë•', minFollowers: 10000 },
    { id: 'linkedin', name: 'LinkedIn', icon: 'üíº', minFollowers: 10000 },
    { id: 'reddit', name: 'Reddit', icon: 'ü§ñ', minFollowers: 10000 }
  ];

  const selectedPlatform = PLATFORMS.find(p => p.id === platform);

  const generateVerificationCode = () => {
    const code = `FTW-VERIFY-${Math.random().toString(36).substring(2, 10).toUpperCase()}`;
    setVerificationCode(code);
    return code;
  };

  const handleScreenshotUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (event) => {
      setScreenshot(event.target.result);
    };
    reader.readAsDataURL(file);
  };

  const verifyInfluencer = async () => {
    if (!platform || !username || !followers) {
      setResult({ success: false, message: 'Please fill in all fields' });
      return;
    }

    const followerCount = parseInt(followers.replace(/,/g, ''));
    if (isNaN(followerCount) || followerCount < selectedPlatform.minFollowers) {
      setResult({
        success: false,
        message: `Minimum ${selectedPlatform.minFollowers.toLocaleString()} followers required for ${selectedPlatform.name}`
      });
      return;
    }

    if (proofMethod === 'screenshot' && !screenshot) {
      setResult({ success: false, message: 'Please upload a screenshot of your profile' });
      return;
    }

    setVerifying(true);

    try {
      // Submit verification request to backend
      const response = await fetch('/api/verify-influencer', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          userId,
          platform,
          username,
          followers: followerCount,
          proofMethod,
          screenshot,
          verificationCode: proofMethod === 'code' ? verificationCode : null
        })
      });

      const data = await response.json();

      if (data.success) {
        setResult({
          success: true,
          message: `‚úÖ Verification submitted! Your account will be upgraded within 24 hours after manual review.`
        });
        
        if (onVerified) {
          onVerified({ tier: 'INFLUENCER', platform, username, followers: followerCount });
        }
      } else {
        setResult({
          success: false,
          message: data.message || 'Verification failed. Please try again.'
        });
      }
    } catch (err) {
      setResult({ success: false, message: 'Error submitting verification: ' + err.message });
    } finally {
      setVerifying(false);
    }
  };

  return (
    <div style={{
      background: 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)',
      borderRadius: '20px',
      padding: '40px',
      color: 'white',
      maxWidth: '900px',
      margin: '0 auto'
    }}>
      <div style={{ textAlign: 'center', marginBottom: '30px' }}>
        <h1 style={{ fontSize: '48px', fontWeight: 'bold', marginBottom: '10px' }}>
          üëë Influencer Verification
        </h1>
        <p style={{ fontSize: '18px', opacity: 0.9 }}>
          Verify your following to unlock all creator tools for FREE
        </p>
        <p style={{ fontSize: '14px', opacity: 0.7, marginTop: '10px' }}>
          Worth $500 - Limited to first 25 influencers only!
        </p>
      </div>

      {/* Benefits */}
      <div style={{
        background: 'rgba(255,255,255,0.1)',
        borderRadius: '15px',
        padding: '30px',
        marginBottom: '30px'
      }}>
        <h3 style={{ fontSize: '24px', marginBottom: '20px', textAlign: 'center' }}>
          üéÅ What You Get FREE ($500 Full Unlock)
        </h3>
        <ul style={{ listStyle: 'none', padding: 0, margin: 0 }}>
          {[
            'üí∞ 0% Platform Fees - Keep 100% of ALL earnings',
            'üé® ALL Tools Unlocked Forever - Photo, VR/AR, CGI, Design, everything',
            'üîû Adult Content Access Included',
            '‚ö° Never See Ads - Premium ad-free experience',
            'üíé Priority Support',
            'üëë Verified Influencer Badge',
            'üìä Advanced Analytics',
            'üöÄ Better Revenue Split',
            'üéÅ Lifetime Access - Never pay again',
            '‚ú® Premium Features Unlocked'
          ].map((benefit, i) => (
            <li key={i} style={{
              padding: '10px 0',
              borderTop: i > 0 ? '1px solid rgba(255,255,255,0.1)' : 'none',
              display: 'flex',
              alignItems: 'center',
              gap: '10px'
            }}>
              <span style={{ fontSize: '20px' }}>‚úì</span>
              <span>{benefit}</span>
            </li>
          ))}
        </ul>
      </div>

      {/* Platform Selection */}
      <div style={{
        background: 'rgba(255,255,255,0.1)',
        borderRadius: '15px',
        padding: '30px',
        marginBottom: '30px'
      }}>
        <h3 style={{ fontSize: '20px', marginBottom: '15px' }}>1. Select Your Platform</h3>
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fill, minmax(120px, 1fr))',
          gap: '10px'
        }}>
          {PLATFORMS.map(p => (
            <button
              key={p.id}
              onClick={() => setPlatform(p.id)}
              style={{
                background: platform === p.id ? 'white' : 'rgba(255,255,255,0.2)',
                color: platform === p.id ? '#f5576c' : 'white',
                border: 'none',
                padding: '15px 10px',
                borderRadius: '10px',
                fontSize: '14px',
                fontWeight: 'bold',
                cursor: 'pointer',
                transition: 'all 0.3s',
                textAlign: 'center'
              }}
            >
              <div style={{ fontSize: '28px', marginBottom: '5px' }}>{p.icon}</div>
              <div>{p.name}</div>
              <div style={{ fontSize: '11px', opacity: 0.8, marginTop: '5px' }}>
                {p.minFollowers.toLocaleString()}+ followers
              </div>
            </button>
          ))}
        </div>
      </div>

      {/* Username and Followers */}
      {platform && (
        <div style={{
          background: 'rgba(255,255,255,0.1)',
          borderRadius: '15px',
          padding: '30px',
          marginBottom: '30px'
        }}>
          <h3 style={{ fontSize: '20px', marginBottom: '15px' }}>2. Enter Your Details</h3>
          <div style={{ marginBottom: '20px' }}>
            <label style={{ display: 'block', marginBottom: '8px', fontWeight: 'bold' }}>
              {selectedPlatform.name} Username
            </label>
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder={`@yourhandle`}
              style={{
                width: '100%',
                padding: '12px',
                borderRadius: '8px',
                border: 'none',
                fontSize: '16px'
              }}
            />
          </div>
          <div>
            <label style={{ display: 'block', marginBottom: '8px', fontWeight: 'bold' }}>
              Follower Count
            </label>
            <input
              type="text"
              value={followers}
              onChange={(e) => setFollowers(e.target.value)}
              placeholder="10,000"
              style={{
                width: '100%',
                padding: '12px',
                borderRadius: '8px',
                border: 'none',
                fontSize: '16px'
              }}
            />
            {selectedPlatform && (
              <p style={{ fontSize: '12px', opacity: 0.8, marginTop: '8px' }}>
                Minimum: {selectedPlatform.minFollowers.toLocaleString()} followers
              </p>
            )}
          </div>
        </div>
      )}

      {/* Proof Method */}
      {platform && username && followers && (
        <div style={{
          background: 'rgba(255,255,255,0.1)',
          borderRadius: '15px',
          padding: '30px',
          marginBottom: '30px'
        }}>
          <h3 style={{ fontSize: '20px', marginBottom: '15px' }}>3. Choose Verification Method</h3>
          <div style={{ display: 'flex', gap: '15px', marginBottom: '20px', flexWrap: 'wrap' }}>
            {[
              { id: 'screenshot', name: 'Upload Screenshot', icon: 'üì∏' },
              { id: 'code', name: 'Post Verification Code', icon: 'üîê' },
              { id: 'api', name: 'API Connection', icon: 'üîå' }
            ].map(method => (
              <button
                key={method.id}
                onClick={() => setProofMethod(method.id)}
                style={{
                  background: proofMethod === method.id ? 'white' : 'rgba(255,255,255,0.2)',
                  color: proofMethod === method.id ? '#f5576c' : 'white',
                  border: 'none',
                  padding: '15px 25px',
                  borderRadius: '10px',
                  fontSize: '16px',
                  fontWeight: 'bold',
                  cursor: 'pointer',
                  transition: 'all 0.3s',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '10px'
                }}
              >
                <span style={{ fontSize: '24px' }}>{method.icon}</span>
                <span>{method.name}</span>
              </button>
            ))}
          </div>

          {proofMethod === 'screenshot' && (
            <div>
              <p style={{ marginBottom: '15px', opacity: 0.9 }}>
                Upload a screenshot of your {selectedPlatform.name} profile showing your follower count
              </p>
              <input
                type="file"
                accept="image/*"
                onChange={handleScreenshotUpload}
                style={{ display: 'none' }}
                id="screenshot-upload"
              />
              <label
                htmlFor="screenshot-upload"
                style={{
                  background: 'white',
                  color: '#f5576c',
                  padding: '12px 30px',
                  borderRadius: '8px',
                  fontSize: '16px',
                  fontWeight: 'bold',
                  cursor: 'pointer',
                  display: 'inline-block'
                }}
              >
                üìÅ Choose Screenshot
              </label>
              {screenshot && (
                <div style={{ marginTop: '15px' }}>
                  <img
                    src={screenshot}
                    alt="Profile screenshot"
                    style={{
                      maxWidth: '100%',
                      maxHeight: '300px',
                      borderRadius: '10px',
                      border: '2px solid rgba(255,255,255,0.3)'
                    }}
                  />
                </div>
              )}
            </div>
          )}

          {proofMethod === 'code' && (
            <div>
              <p style={{ marginBottom: '15px', opacity: 0.9 }}>
                Post this verification code in your {selectedPlatform.name} bio or as a post, then submit
              </p>
              <button
                onClick={generateVerificationCode}
                style={{
                  background: 'white',
                  color: '#f5576c',
                  padding: '12px 30px',
                  borderRadius: '8px',
                  fontSize: '16px',
                  fontWeight: 'bold',
                  cursor: 'pointer',
                  border: 'none',
                  marginBottom: '15px'
                }}
              >
                üîê Generate Code
              </button>
              {verificationCode && (
                <div style={{
                  background: 'rgba(255,255,255,0.2)',
                  padding: '20px',
                  borderRadius: '10px',
                  textAlign: 'center'
                }}>
                  <div style={{ fontSize: '24px', fontWeight: 'bold', fontFamily: 'monospace' }}>
                    {verificationCode}
                  </div>
                  <p style={{ fontSize: '14px', marginTop: '10px', opacity: 0.9 }}>
                    Copy and post this code on your {selectedPlatform.name} profile
                  </p>
                </div>
              )}
            </div>
          )}

          {proofMethod === 'api' && (
            <div>
              <p style={{ opacity: 0.9 }}>
                Connect your {selectedPlatform.name} account directly via API (requires OAuth authorization)
              </p>
              <button
                style={{
                  background: 'white',
                  color: '#f5576c',
                  padding: '12px 30px',
                  borderRadius: '8px',
                  fontSize: '16px',
                  fontWeight: 'bold',
                  cursor: 'pointer',
                  border: 'none',
                  marginTop: '15px'
                }}
              >
                üîå Connect {selectedPlatform.name}
              </button>
            </div>
          )}
        </div>
      )}

      {/* Submit Button */}
      {platform && username && followers && (proofMethod === 'screenshot' ? screenshot : proofMethod) && (
        <div style={{ textAlign: 'center', marginBottom: '30px' }}>
          <button
            onClick={verifyInfluencer}
            disabled={verifying}
            style={{
              background: 'white',
              color: '#f5576c',
              padding: '20px 60px',
              borderRadius: '30px',
              fontSize: '20px',
              fontWeight: 'bold',
              cursor: verifying ? 'not-allowed' : 'pointer',
              border: 'none',
              opacity: verifying ? 0.6 : 1,
              transition: 'all 0.3s'
            }}
          >
            {verifying ? '‚è≥ Verifying...' : 'üöÄ Submit Verification'}
          </button>
        </div>
      )}

      {/* Result */}
      {result && (
        <div style={{
          background: result.success ? 'rgba(76, 175, 80, 0.2)' : 'rgba(244, 67, 54, 0.2)',
          border: `2px solid ${result.success ? '#4CAF50' : '#f44336'}`,
          borderRadius: '15px',
          padding: '20px',
          textAlign: 'center'
        }}>
          <div style={{ fontSize: '48px', marginBottom: '15px' }}>
            {result.success ? 'üéâ' : '‚ùå'}
          </div>
          <p style={{ fontSize: '18px', fontWeight: 'bold' }}>{result.message}</p>
        </div>
      )}

      {/* FAQ */}
      <div style={{
        background: 'rgba(255,255,255,0.1)',
        borderRadius: '15px',
        padding: '30px',
        marginTop: '30px'
      }}>
        <h3 style={{ fontSize: '24px', marginBottom: '20px' }}>‚ùì FAQ</h3>
        <div>
          {[
            { q: 'Why do I need to verify?', a: 'We want to support real influencers and creators who will use our tools professionally.' },
            { q: 'How long does verification take?', a: 'Most verifications are reviewed within 24 hours.' },
            { q: 'What if I lose my following?', a: 'Your access remains as long as you stay active on the platform.' },
            { q: 'Can I verify multiple platforms?', a: 'Yes! Verify each platform to increase your credibility.' },
            { q: 'Is this really free?', a: 'Yes, 100% free for verified influencers. No hidden fees.' }
          ].map((faq, i) => (
            <div key={i} style={{
              marginBottom: '20px',
              paddingBottom: '20px',
              borderBottom: i < 4 ? '1px solid rgba(255,255,255,0.1)' : 'none'
            }}>
              <div style={{ fontWeight: 'bold', marginBottom: '8px' }}>{faq.q}</div>
              <div style={{ opacity: 0.9, fontSize: '14px' }}>{faq.a}</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

