    }
];

const InteractiveTutorial = ({ onComplete }) => {
    const [isActive, setIsActive] = useState(false);
    const [currentStep, setCurrentStep] = useState(0);
    const [isMinimized, setIsMinimized] = useState(false);

    useEffect(() => {
        // Check if user has completed tutorial
        const hasCompleted = localStorage.getItem('tutorial-completed');
        if (!hasCompleted) {
            // Show tutorial after a short delay
            setTimeout(() => setIsActive(true), 1000);
        }

        // Global function to restart tutorial
        window.startTutorial = () => {
            setCurrentStep(0);
            setIsActive(true);
            setIsMinimized(false);
        };
    }, []);

    const handleNext = () => {
        if (currentStep < TUTORIAL_STEPS.length - 1) {
            setCurrentStep(prev => prev + 1);
        } else {
            completeTutorial();
        }
    };

    const handleSkip = () => {
        completeTutorial();
    };

    const completeTutorial = () => {
        localStorage.setItem('tutorial-completed', 'true');
        setIsActive(false);
        if (onComplete) onComplete();

        // Unlock first achievement
        if (window.unlockAchievement) {
            window.unlockAchievement('first_steps');
        }
    };

    const handleAction = (action) => {
        switch (action) {
            case 'Next':
                handleNext();
                break;
            case 'Skip':
                handleSkip();
                break;
            case 'Try it':
                // Highlight the feature
                handleNext();
                break;
            case 'Show Me Tools':
                // Navigate to tools
                handleNext();
                break;
            case 'View All Shortcuts':
                if (window.showKeyboardShortcuts) {
                    window.showKeyboardShortcuts();
                }
                handleNext();
                break;
            case 'View Achievements':
                // Show achievements panel
                handleNext();
                break;
            case 'Open Help Center':
                window.open('/help', '_blank');
                handleNext();
                break;
            case 'Start Creating!':
                completeTutorial();
                break;
            case 'Restart Tour':
                setCurrentStep(0);
                break;
            default:
                handleNext();
        }
    };

    if (!isActive) return null;

    const step = TUTORIAL_STEPS[currentStep];

    if (isMinimized) {
        return (
            <button
                className="tutorial-minimized"
                onClick={() => setIsMinimized(false)}
                title="Resume Tutorial"
            >
                üìö Resume Tutorial ({currentStep + 1}/{TUTORIAL_STEPS.length})
            </button>
        );
    }

    return (
        <>
            <div className="tutorial-overlay" />

            <div className={`tutorial-card ${step.position}`}>
                <div className="tutorial-header">
                    <div className="tutorial-progress">
                        <div className="progress-bar">
                            <div
                                className="progress-fill"
                                style={{ width: `${((currentStep + 1) / TUTORIAL_STEPS.length) * 100}%` }}
                            />
                        </div>
                        <span className="progress-text">
                            Step {currentStep + 1} of {TUTORIAL_STEPS.length}
                        </span>
                    </div>
                    <div className="tutorial-controls">
                        <button
                            className="tutorial-minimize"
                            onClick={() => setIsMinimized(true)}
                            title="Minimize"
                        >
                            ‚ûñ
                        </button>
                        <button
                            className="tutorial-close"
                            onClick={handleSkip}
                            title="Exit Tutorial"
                        >
                            ‚úï
                        </button>
                    </div>
                </div>

                <div className="tutorial-content">
                    <h2 className="tutorial-title">{step.title}</h2>
                    <p className="tutorial-description">{step.description}</p>

                    {step.highlight && (
                        <div className="tutorial-highlight">
                            <kbd>{step.highlight}</kbd>
                        </div>
                    )}
                </div>

                <div className="tutorial-actions">
                    {step.actions.map((action, idx) => (
                        <button
                            key={idx}
                            className={`tutorial-btn ${idx === 0 ? 'primary' : 'secondary'}`}
                            onClick={() => handleAction(action)}
                        >
                            {action}
                        </button>
                    ))}
                </div>

                <div className="tutorial-footer">
                    <button
                        className="tutorial-link"
                        onClick={() => window.open('/tutorials', '_blank')}
                    >
                        üìñ View Full Tutorials
                    </button>
                </div>
            </div>
        </>
    );
};

export default InteractiveTutorial;
export { TUTORIAL_STEPS };

