import React from 'react';
import './LoadingSpinner.css';

export function LoadingSpinner({ message = "Processing..." }) {
  return (
    <div className="loading-spinner-container">
      <div className="loading-spinner"></div>
      <p className="loading-message">{message}</p>
    </div>
  );
}

import React from 'react';
import './LoadingState.css';

/**
 * Reusable Loading Component
 * Provides consistent loading UI across the platform
 */
export const LoadingSpinner = ({ size = 'medium', message = 'Loading...' }) => {
  const sizeClass = {
    small: 'spinner-small',
    medium: 'spinner-medium',
    large: 'spinner-large'
  }[size];

  return (
    <div className="loading-spinner-container">
      <div className={`loading-spinner ${sizeClass}`}>
        <div className="spinner-ring"></div>
        <div className="spinner-ring"></div>
        <div className="spinner-ring"></div>
      </div>
      {message && <p className="loading-message">{message}</p>}
    </div>
  );
};

/**
 * Skeleton Loader for content placeholders
 */
export const SkeletonLoader = ({ type = 'post', count = 1 }) => {
  const skeletons = Array.from({ length: count }, (_, i) => i);

  if (type === 'post') {
    return (
      <div className="skeleton-container">
        {skeletons.map((i) => (
          <div key={i} className="skeleton-post">
            <div className="skeleton-header">
              <div className="skeleton-avatar"></div>
              <div className="skeleton-info">
                <div className="skeleton-line skeleton-line-short"></div>
                <div className="skeleton-line skeleton-line-tiny"></div>
              </div>
            </div>
            <div className="skeleton-content">
              <div className="skeleton-line"></div>
              <div className="skeleton-line"></div>
              <div className="skeleton-line skeleton-line-medium"></div>
            </div>
            <div className="skeleton-actions">
              <div className="skeleton-button"></div>
              <div className="skeleton-button"></div>
              <div className="skeleton-button"></div>
            </div>
          </div>
        ))}
      </div>
    );
  }

  if (type === 'profile') {
    return (
      <div className="skeleton-profile">
        <div className="skeleton-cover"></div>
        <div className="skeleton-avatar-large"></div>
        <div className="skeleton-line skeleton-line-short"></div>
        <div className="skeleton-line skeleton-line-medium"></div>
      </div>
    );
  }

  if (type === 'grid') {
    return (
      <div className="skeleton-grid">
        {skeletons.map((i) => (
          <div key={i} className="skeleton-card"></div>
        ))}
      </div>
    );
  }

  // Default: simple lines
  return (
    <div className="skeleton-lines">
      {skeletons.map((i) => (
        <div key={i} className="skeleton-line"></div>
      ))}
    </div>
  );
};

/**
 * Progress Bar Component
 */
export const ProgressBar = ({ progress = 0, message = '', color = '#667eea' }) => {
  return (
    <div className="progress-container">
      {message && <p className="progress-message">{message}</p>}
      <div className="progress-bar-bg">
        <div 
          className="progress-bar-fill" 
          style={{ 
            width: `${Math.min(100, Math.max(0, progress))}%`,
            background: color
          }}
        >
          <span className="progress-percentage">{Math.round(progress)}%</span>
        </div>
      </div>
    </div>
  );
};

/**
 * Full Page Loader
 */
export const PageLoader = ({ message = 'Loading ForTheWeebs...' }) => {
  return (
    <div className="page-loader">
      <div className="page-loader-content">
        <div className="logo-animation">üéå</div>
        <LoadingSpinner size="large" message={message} />
      </div>
    </div>
  );
};

/**
 * Empty State Component
 */
export const EmptyState = ({ 
  icon = 'üì≠', 
  title = 'Nothing Here Yet', 
  message = 'Create your first post to get started!',
  action = null
}) => {
  return (
    <div className="empty-state">
      <div className="empty-icon">{icon}</div>
      <h3 className="empty-title">{title}</h3>
      <p className="empty-message">{message}</p>
      {action && <div className="empty-action">{action}</div>}
    </div>
  );
};

export default LoadingSpinner;

import React, { useState, useEffect } from 'react';
import './Marketplace.css';

const API_URL = import.meta.env.VITE_API_URL || 'http://localhost:3001';

/**
 * Marketplace Component
 * Browse and purchase assets, templates, presets from creators
 */
export default function Marketplace() {
    const [items, setItems] = useState([]);
    const [categories, setCategories] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState(null);
    const [sortBy, setSortBy] = useState('popular');
    const [loading, setLoading] = useState(false);
    const [myPurchases, setMyPurchases] = useState([]);
    const [view, setView] = useState('browse'); // browse or purchases

    const userId = localStorage.getItem('userId'); // Get from auth

    useEffect(() => {
        loadCategories();
        loadItems();
        if (userId) {
            loadMyPurchases();
        }
    }, [selectedCategory, sortBy]);

    const loadCategories = async () => {
        try {
            const response = await fetch(`${API_URL}/api/marketplace/categories`);
            const data = await response.json();
            setCategories(data.categories || []);
        } catch (error) {
            console.error('Failed to load categories:', error);
        }
    };

    const loadItems = async () => {
        setLoading(true);
        try {
            let url = `${API_URL}/api/marketplace/browse?sort=${sortBy}`;
            if (selectedCategory) {
                url += `&category=${selectedCategory}`;
            }
            
            const response = await fetch(url);
            const data = await response.json();
            setItems(data.items || []);
        } catch (error) {
            console.error('Failed to load items:', error);
        } finally {
            setLoading(false);
        }
    };

    const loadMyPurchases = async () => {
        try {
            const response = await fetch(`${API_URL}/api/marketplace/my-purchases/${userId}`);
            const data = await response.json();
            setMyPurchases(data.purchases || []);
        } catch (error) {
            console.error('Failed to load purchases:', error);
        }
    };

    const handlePurchase = async (itemId) => {
        if (!userId) {
            alert('Please log in to purchase items');
            return;
        }

        try {
            const response = await fetch(`${API_URL}/api/marketplace/purchase`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ itemId, buyerId: userId })
            });

            const data = await response.json();

            if (data.clientSecret) {
                // Redirect to Stripe checkout
                alert('Redirecting to payment...');
                // Implement Stripe payment flow here
            }
        } catch (error) {
            console.error('Purchase failed:', error);
            alert('Purchase failed. Please try again.');
        }
    };

    const ItemCard = ({ item }) => (
        <div className="marketplace-item">
            <div className="item-preview">
                {item.preview_images && item.preview_images.length > 0 ? (
                    <img src={item.preview_images[0]} alt={item.title} />
                ) : (
                    <div className="no-preview">No preview available</div>
                )}
                {item.type && <span className="item-type">{item.type}</span>}
            </div>
            <div className="item-info">
                <h3>{item.title}</h3>
                <p className="item-seller">
                    by {item.seller?.display_name || 'Unknown'}
                    {item.seller?.verified && <span className="verified">‚úì</span>}
                </p>
                <p className="item-description">
                    {item.description?.substring(0, 100)}...
                </p>
                <div className="item-tags">
                    {item.tags?.slice(0, 3).map(tag => (
                        <span key={tag} className="tag">{tag}</span>
                    ))}
                </div>
                <div className="item-footer">
                    <div className="item-stats">
                        <span>‚≠ê {item.rating_avg?.toFixed(1) || 'New'}</span>
                        <span>üõí {item.sales_count || 0} sales</span>
                    </div>
                    <div className="item-price">
                        <strong>${(item.price / 100).toFixed(2)}</strong>
                        <button 
                            className="btn-purchase"
                            onClick={() => handlePurchase(item.id)}
                        >
                            Purchase
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );

    const PurchaseCard = ({ purchase }) => (
        <div className="purchase-card">
            <img 
                src={purchase.item?.preview_images?.[0] || '/default-item.png'} 
                alt={purchase.item?.title}
                className="purchase-thumb"
            />
            <div className="purchase-info">
                <h4>{purchase.item?.title}</h4>
                <p>by {purchase.seller?.display_name}</p>
                <p className="purchase-date">
                    Purchased {new Date(purchase.completed_at).toLocaleDateString()}
                </p>
                <button className="btn-download">Download Files</button>
            </div>
        </div>
    );

    return (
        <div className="marketplace-page">
            <header className="marketplace-header">
                <h1>Marketplace</h1>
                <p>Assets, templates, and tools from creators</p>
                
                <div className="marketplace-nav">
                    <button 
                        className={view === 'browse' ? 'nav-btn active' : 'nav-btn'}
                        onClick={() => setView('browse')}
                    >
                        Browse
                    </button>
                    {userId && (
                        <button 
                            className={view === 'purchases' ? 'nav-btn active' : 'nav-btn'}
                            onClick={() => setView('purchases')}
                        >
                            My Purchases ({myPurchases.length})
                        </button>
                    )}
                </div>
            </header>

            {view === 'browse' ? (
                <>
                    <div className="marketplace-controls">
                        <div className="categories">
                            <button
                                className={!selectedCategory ? 'category-btn active' : 'category-btn'}
                                onClick={() => setSelectedCategory(null)}
                            >
                                All
                            </button>
                            {categories.map(cat => (
                                <button
                                    key={cat.id}
                                    className={selectedCategory === cat.id ? 'category-btn active' : 'category-btn'}
                                    onClick={() => setSelectedCategory(cat.id)}
                                >
                                    {cat.icon} {cat.name} ({cat.itemCount})
                                </button>
                            ))}
                        </div>
                        
                        <div className="sort-controls">
                            <label>Sort by:</label>
                            <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
                                <option value="popular">Most Popular</option>
                                <option value="recent">Recently Added</option>
                                <option value="price_low">Price: Low to High</option>
                                <option value="price_high">Price: High to Low</option>
                                <option value="rating">Highest Rated</option>
                            </select>
                        </div>
                    </div>

                    <div className="marketplace-grid">
                        {loading ? (
                            <div className="loading">Loading items...</div>
                        ) : items.length > 0 ? (
                            items.map(item => <ItemCard key={item.id} item={item} />)
                        ) : (
                            <div className="no-items">No items found in this category</div>
                        )}
                    </div>
                </>
            ) : (
                <div className="purchases-list">
                    <h2>My Purchases</h2>
                    {myPurchases.length > 0 ? (
                        myPurchases.map(purchase => (
                            <PurchaseCard key={purchase.id} purchase={purchase} />
                        ))
                    ) : (
                        <div className="no-purchases">
                            <p>You haven't purchased anything yet</p>
                            <button onClick={() => setView('browse')}>Browse Marketplace</button>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
}

import React, { useState } from 'react';
import './MerchStore.css';
import { PrintOnDemand } from './PrintOnDemand';

const MerchStore = () => {
  const [activeSection, setActiveSection] = useState('shop'); // 'shop' or 'print-on-demand'
  const [cart, setCart] = useState([]);
  const [showCart, setShowCart] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [products] = useState([
    {
      id: 1,
      name: 'Anime Character T-Shirt',
      price: 29.99,
      category: 'clothing',
      image: 'https://via.placeholder.com/300x400/667eea/fff?text=T-Shirt',
      stock: 45,
      sizes: ['S', 'M', 'L', 'XL', 'XXL']
    },
    {
      id: 2,
      name: 'CGI Art Print Poster',
      price: 19.99,
      category: 'prints',
      image: 'https://via.placeholder.com/300x400/764ba2/fff?text=Poster',
      stock: 120,
      sizes: ['12x18', '18x24', '24x36']
    },
    {
      id: 3,
      name: 'Creator Logo Hoodie',
      price: 49.99,
      category: 'clothing',
      image: 'https://via.placeholder.com/300x400/f093fb/fff?text=Hoodie',
      stock: 28,
      sizes: ['S', 'M', 'L', 'XL', 'XXL']
    },
    {
      id: 4,
      name: 'Limited Edition Art Book',
      price: 39.99,
      category: 'books',
      image: 'https://via.placeholder.com/300x400/4facfe/fff?text=Book',
      stock: 50,
      sizes: ['Standard']
    },
    {
      id: 5,
      name: 'Character Sticker Pack',
      price: 9.99,
      category: 'accessories',
      image: 'https://via.placeholder.com/300x400/00f2fe/fff?text=Stickers',
      stock: 200,
      sizes: ['Pack of 10']
    },
    {
      id: 6,
      name: 'Coffee Mug',
      price: 14.99,
      category: 'accessories',
      image: 'https://via.placeholder.com/300x400/667eea/fff?text=Mug',
      stock: 85,
      sizes: ['11oz', '15oz']
    }
  ]);

  const addToCart = (product, size) => {
    const existingItem = cart.find(item => item.id === product.id && item.size === size);
    if (existingItem) {
      setCart(cart.map(item => 
        item.id === product.id && item.size === size
          ? { ...item, quantity: item.quantity + 1 }
          : item
      ));
    } else {
      setCart([...cart, { ...product, size, quantity: 1 }]);
    }
  };

  const removeFromCart = (productId, size) => {
    setCart(cart.filter(item => !(item.id === productId && item.size === size)));
  };

  const updateQuantity = (productId, size, newQuantity) => {
    if (newQuantity <= 0) {
      removeFromCart(productId, size);
    } else {
      setCart(cart.map(item =>
        item.id === productId && item.size === size
          ? { ...item, quantity: newQuantity }
          : item
      ));
    }
  };

  const getCartTotal = () => {
    return cart.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  const getCartItemCount = () => {
    return cart.reduce((count, item) => count + item.quantity, 0);
  };

  const filteredProducts = selectedCategory === 'all'
    ? products
    : products.filter(p => p.category === selectedCategory);

  const checkout = () => {
    if (cart.length === 0) {
      alert('Your cart is empty!');
      return;
    }
    alert(`Checkout - Total: $${getCartTotal().toFixed(2)}\n\nThis would redirect to payment processing.`);
  };

  return (
    <div className="merch-store">
      <div className="store-header">
        <h1>üõçÔ∏è Marketplace</h1>
        <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
          <button 
            className={`section-tab ${activeSection === 'shop' ? 'active' : ''}`}
            onClick={() => setActiveSection('shop')}
            style={{
              padding: '10px 20px',
              background: activeSection === 'shop' ? '#667eea' : 'rgba(255,255,255,0.1)',
              color: '#fff',
              border: 'none',
              borderRadius: '8px',
              cursor: 'pointer'
            }}
          >
            üõí Merch Shop
          </button>
          <button 
            className={`section-tab ${activeSection === 'print-on-demand' ? 'active' : ''}`}
            onClick={() => setActiveSection('print-on-demand')}
            style={{
              padding: '10px 20px',
              background: activeSection === 'print-on-demand' ? '#667eea' : 'rgba(255,255,255,0.1)',
              color: '#fff',
              border: 'none',
              borderRadius: '8px',
              cursor: 'pointer'
            }}
          >
            üì¶ Print On Demand
          </button>
          {activeSection === 'shop' && (
            <button className="cart-btn" onClick={() => setShowCart(!showCart)}>
              üõí Cart ({getCartItemCount()})
            </button>
          )}
        </div>
      </div>

      {activeSection === 'print-on-demand' ? (
        <PrintOnDemand />
      ) : (
      <div className="store-layout">
        <aside className="store-sidebar">
          <h3>Categories</h3>
          <div className="category-list">
            <button 
              className={`category-btn ${selectedCategory === 'all' ? 'active' : ''}`}
              onClick={() => setSelectedCategory('all')}
            >
              All Products
            </button>
            <button 
              className={`category-btn ${selectedCategory === 'clothing' ? 'active' : ''}`}
              onClick={() => setSelectedCategory('clothing')}
            >
              üëï Clothing
            </button>
            <button 
              className={`category-btn ${selectedCategory === 'prints' ? 'active' : ''}`}
              onClick={() => setSelectedCategory('prints')}
            >
              üñºÔ∏è Prints
            </button>
            <button 
              className={`category-btn ${selectedCategory === 'books' ? 'active' : ''}`}
              onClick={() => setSelectedCategory('books')}
            >
              üìö Books
            </button>
            <button 
              className={`category-btn ${selectedCategory === 'accessories' ? 'active' : ''}`}
              onClick={() => setSelectedCategory('accessories')}
            >
              ‚ú® Accessories
            </button>
          </div>
        </aside>

        <main className="store-main">
          <div className="products-grid">
            {filteredProducts.map(product => (
              <ProductCard 
                key={product.id} 
                product={product} 
                onAddToCart={addToCart}
              />
            ))}
          </div>
        </main>

        {showCart && (
          <div className="cart-sidebar">
            <div className="cart-header">
              <h2>Shopping Cart</h2>
              <button className="close-cart" onClick={() => setShowCart(false)}>√ó</button>
            </div>

            {cart.length === 0 ? (
              <div className="empty-cart">
                <p>üõí</p>
                <p>Your cart is empty</p>
              </div>
            ) : (
              <>
                <div className="cart-items">
                  {cart.map((item, index) => (
                    <div key={`${item.id}-${item.size}-${index}`} className="cart-item">
                      <img src={item.image} alt={item.name} />
                      <div className="cart-item-info">
                        <h4>{item.name}</h4>
                        <p className="cart-item-size">Size: {item.size}</p>
                        <p className="cart-item-price">${item.price}</p>
                        <div className="cart-item-quantity">
                          <button onClick={() => updateQuantity(item.id, item.size, item.quantity - 1)}>-</button>
                          <span>{item.quantity}</span>
                          <button onClick={() => updateQuantity(item.id, item.size, item.quantity + 1)}>+</button>
                        </div>
                      </div>
                      <button 
                        className="remove-item"
                        onClick={() => removeFromCart(item.id, item.size)}
                      >
                        üóëÔ∏è
                      </button>
                    </div>
                  ))}
                </div>

                <div className="cart-footer">
                  <div className="cart-total">
                    <span>Total:</span>
                    <span className="total-amount">${getCartTotal().toFixed(2)}</span>
                  </div>
                  <button className="checkout-btn" onClick={checkout}>
                    Checkout
                  </button>
                </div>
              </>
            )}
          </div>
        )}
      </div>
      )}
    </div>
  );
};

const ProductCard = ({ product, onAddToCart }) => {
  const [selectedSize, setSelectedSize] = useState(product.sizes[0]);
  const [showDetails, setShowDetails] = useState(false);

  const handleAddToCart = () => {
    onAddToCart(product, selectedSize);
    alert(`Added ${product.name} (${selectedSize}) to cart!`);
  };

  return (
    <div className="product-card">
      <div className="product-image">
        <img src={product.image} alt={product.name} />
        {product.stock < 20 && (
          <div className="low-stock-badge">Only {product.stock} left!</div>
        )}
      </div>

      <div className="product-info">
        <h3>{product.name}</h3>
        <p className="product-price">${product.price}</p>

        <div className="size-selector">
          <label>Size:</label>
          <select value={selectedSize} onChange={(e) => setSelectedSize(e.target.value)}>
            {product.sizes.map(size => (
              <option key={size} value={size}>{size}</option>
            ))}
          </select>
        </div>

        <div className="product-actions">
          <button className="add-to-cart-btn" onClick={handleAddToCart}>
            Add to Cart
          </button>
          <button className="details-btn" onClick={() => setShowDetails(!showDetails)}>
            {showDetails ? 'Hide' : 'Details'}
          </button>
        </div>

        {showDetails && (
          <div className="product-details">
            <p><strong>Stock:</strong> {product.stock} available</p>
            <p><strong>Category:</strong> {product.category}</p>
            <p><strong>Available Sizes:</strong> {product.sizes.join(', ')}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default MerchStore;

import { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { checkTierAccess } from '../utils/tierAccess';
import { supabase } from '../lib/supabase';

/**
 * OWNER-ONLY Dashboard to view Mico's bug triage
 * Shows what Mico decided about each bug report
 */
export default function MicoTriageDashboard() {
  const { user } = useAuth();
  const [reports, setReports] = useState([]);
  const [filter, setFilter] = useState('all'); // all, legitimate, spam, malicious
  const [loading, setLoading] = useState(true);

  const tierAccess = checkTierAccess(user?.id, user?.tier, user?.email);

  // Only owner can see this
  if (!tierAccess.isOwner) return null;

  useEffect(() => {
    loadBugReports();
  }, [filter]);

  const loadBugReports = async () => {
    setLoading(true);
    try {
      let query = supabase
        .from('bug_reports')
        .select('*')
        .order('created_at', { ascending: false })
        .limit(100);

      if (filter !== 'all') {
        query = query.eq('status', filter);
      }

      const { data, error } = await query;

      if (error) throw error;
      setReports(data || []);
    } catch (error) {
      console.error('Failed to load bug reports:', error);
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    const colors = {
      pending: '#fb923c',
      triaged: '#3b82f6',
      pending_fix: '#8b5cf6',
      fixed: '#10b981',
      discarded: '#6b7280',
      spam: '#ef4444',
      malicious: '#dc2626'
    };
    return colors[status] || '#6b7280';
  };

  const getPriorityColor = (priority) => {
    const colors = {
      critical: '#dc2626',
      high: '#f97316',
      medium: '#eab308',
      low: '#3b82f6',
      none: '#6b7280'
    };
    return colors[priority] || '#6b7280';
  };

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      background: 'rgba(0,0,0,0.95)',
      zIndex: 10000,
      overflowY: 'auto',
      padding: '40px 20px'
    }}>
      <div style={{ maxWidth: '1400px', margin: '0 auto', color: 'white' }}>
        <h1 style={{ fontSize: '32px', marginBottom: '10px' }}>
          üß† Mico Bug Triage Dashboard
        </h1>
        <p style={{ opacity: 0.7, marginBottom: '30px' }}>
          Mico automatically analyzes bug reports and decides: Legitimate, Spam, or Malicious
        </p>

        {/* Filter Buttons */}
        <div style={{ display: 'flex', gap: '10px', marginBottom: '30px', flexWrap: 'wrap' }}>
          {['all', 'pending', 'triaged', 'legitimate', 'spam', 'malicious', 'fixed'].map(f => (
            <button
              key={f}
              onClick={() => setFilter(f)}
              style={{
                padding: '10px 20px',
                background: filter === f ? '#667eea' : 'rgba(255,255,255,0.1)',
                border: filter === f ? '2px solid #667eea' : '2px solid rgba(255,255,255,0.2)',
                borderRadius: '8px',
                color: 'white',
                cursor: 'pointer',
                textTransform: 'capitalize',
                fontWeight: filter === f ? 'bold' : 'normal'
              }}
            >
              {f.replace('_', ' ')}
            </button>
          ))}
        </div>

        {/* Stats */}
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
          gap: '20px',
          marginBottom: '30px'
        }}>
          <div style={{
            background: 'rgba(59, 130, 246, 0.2)',
            border: '2px solid rgba(59, 130, 246, 0.5)',
            borderRadius: '12px',
            padding: '20px',
            textAlign: 'center'
          }}>
            <div style={{ fontSize: '36px', fontWeight: 'bold', color: '#3b82f6' }}>
              {reports.filter(r => r.status === 'triaged' || r.status === 'legitimate').length}
            </div>
            <div style={{ opacity: 0.8 }}>Legitimate Bugs</div>
          </div>

          <div style={{
            background: 'rgba(239, 68, 68, 0.2)',
            border: '2px solid rgba(239, 68, 68, 0.5)',
            borderRadius: '12px',
            padding: '20px',
            textAlign: 'center'
          }}>
            <div style={{ fontSize: '36px', fontWeight: 'bold', color: '#ef4444' }}>
              {reports.filter(r => r.status === 'spam').length}
            </div>
            <div style={{ opacity: 0.8 }}>Spam/Crap</div>
          </div>

          <div style={{
            background: 'rgba(220, 38, 38, 0.2)',
            border: '2px solid rgba(220, 38, 38, 0.5)',
            borderRadius: '12px',
            padding: '20px',
            textAlign: 'center'
          }}>
            <div style={{ fontSize: '36px', fontWeight: 'bold', color: '#dc2626' }}>
              {reports.filter(r => r.status === 'malicious').length}
            </div>
            <div style={{ opacity: 0.8 }}>Malicious Attempts</div>
          </div>

          <div style={{
            background: 'rgba(16, 185, 129, 0.2)',
            border: '2px solid rgba(16, 185, 129, 0.5)',
            borderRadius: '12px',
            padding: '20px',
            textAlign: 'center'
          }}>
            <div style={{ fontSize: '36px', fontWeight: 'bold', color: '#10b981' }}>
              {reports.filter(r => r.status === 'fixed').length}
            </div>
            <div style={{ opacity: 0.8 }}>Fixed</div>
          </div>
        </div>

        {/* Bug Reports List */}
        {loading ? (
          <div style={{ textAlign: 'center', padding: '60px', opacity: 0.5 }}>
            <div style={{ fontSize: '48px', marginBottom: '20px' }}>‚è≥</div>
            <div>Loading bug reports...</div>
          </div>
        ) : reports.length === 0 ? (
          <div style={{ textAlign: 'center', padding: '60px', opacity: 0.5 }}>
            <div style={{ fontSize: '48px', marginBottom: '20px' }}>üì≠</div>
            <div>No bug reports found</div>
          </div>
        ) : (
          <div style={{ display: 'flex', flexDirection: 'column', gap: '20px' }}>
            {reports.map(report => (
              <div
                key={report.id}
                style={{
                  background: 'rgba(255,255,255,0.05)',
                  border: `2px solid ${getStatusColor(report.status)}`,
                  borderRadius: '12px',
                  padding: '20px'
                }}
              >
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'start', marginBottom: '15px' }}>
                  <div>
                    <div style={{ display: 'flex', gap: '10px', alignItems: 'center', marginBottom: '8px' }}>
                      <span style={{
                        padding: '4px 12px',
                        background: getStatusColor(report.status),
                        borderRadius: '6px',
                        fontSize: '12px',
                        fontWeight: 'bold',
                        textTransform: 'uppercase'
                      }}>
                        {report.status}
                      </span>

                      {report.priority && report.priority !== 'none' && (
                        <span style={{
                          padding: '4px 12px',
                          background: getPriorityColor(report.priority),
                          borderRadius: '6px',
                          fontSize: '12px',
                          fontWeight: 'bold',
                          textTransform: 'uppercase'
                        }}>
                          {report.priority}
                        </span>
                      )}

                      {report.category && (
                        <span style={{
                          padding: '4px 12px',
                          background: 'rgba(255,255,255,0.1)',
                          borderRadius: '6px',
                          fontSize: '12px',
                          opacity: 0.8
                        }}>
                          {report.category}
                        </span>
                      )}

                      {report.auto_fixable && (
                        <span style={{
                          padding: '4px 12px',
                          background: 'rgba(16, 185, 129, 0.3)',
                          borderRadius: '6px',
                          fontSize: '12px',
                          color: '#10b981'
                        }}>
                          ‚ú® Auto-fixable
                        </span>
                      )}
                    </div>

                    <div style={{ fontSize: '12px', opacity: 0.6 }}>
                      From: {report.email || 'Anonymous'} ({report.tier || 'FREE'})
                    </div>
                    <div style={{ fontSize: '12px', opacity: 0.6 }}>
                      {new Date(report.created_at).toLocaleString()}
                    </div>
                  </div>
                </div>

                <div style={{ marginBottom: '15px' }}>
                  <strong>Description:</strong>
                  <div style={{
                    marginTop: '8px',
                    padding: '12px',
                    background: 'rgba(0,0,0,0.3)',
                    borderRadius: '6px',
                    whiteSpace: 'pre-wrap',
                    wordBreak: 'break-word'
                  }}>
                    {report.description}
                  </div>
                </div>

                {report.url && (
                  <div style={{ marginBottom: '15px', fontSize: '13px', opacity: 0.7 }}>
                    <strong>URL:</strong> {report.url}
                  </div>
                )}

                {report.mico_analysis && (
                  <div style={{
                    marginTop: '15px',
                    padding: '15px',
                    background: 'rgba(102, 126, 234, 0.1)',
                    border: '2px solid rgba(102, 126, 234, 0.3)',
                    borderRadius: '8px'
                  }}>
                    <div style={{ fontWeight: 'bold', marginBottom: '10px', color: '#667eea' }}>
                      üß† Mico's Analysis:
                    </div>

                    <div style={{ marginBottom: '10px' }}>
                      <strong>Reasoning:</strong>
                      <div style={{ marginTop: '5px', opacity: 0.9 }}>
                        {report.mico_analysis.reasoning}
                      </div>
                    </div>

                    {report.mico_analysis.suggested_fix && (
                      <div>
                        <strong>Suggested Fix:</strong>
                        <div style={{ marginTop: '5px', opacity: 0.9 }}>
                          {report.mico_analysis.suggested_fix}
                        </div>
                      </div>
                    )}
                  </div>
                )}

                {report.logs && report.logs.length > 0 && (
                  <details style={{ marginTop: '15px' }}>
                    <summary style={{ cursor: 'pointer', opacity: 0.7 }}>
                      View Logs ({report.logs.length})
                    </summary>
                    <div style={{
                      marginTop: '10px',
                      padding: '12px',
                      background: 'rgba(0,0,0,0.5)',
                      borderRadius: '6px',
                      fontSize: '12px',
                      fontFamily: 'monospace',
                      maxHeight: '200px',
                      overflowY: 'auto'
                    }}>
                      {report.logs.map((log, idx) => (
                        <div key={idx} style={{ marginBottom: '8px' }}>
                          <span style={{ color: log.type === 'error' ? '#ef4444' : '#fb923c' }}>
                            [{log.type}]
                          </span> {log.message}
                        </div>
                      ))}
                    </div>
                  </details>
                )}
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

