      tags: ['cyberpunk', 'street', 'neon']
    },
    {
      id: 3,
      name: 'Katana Sword',
      category: 'props',
      thumbnail: '‚öîÔ∏è',
      format: 'GLB',
      size: '1.1 MB',
      polygons: '5.4K',
      isPremium: false,
      tags: ['weapon', 'sword', 'anime']
    },
    {
      id: 4,
      name: 'Gaming Chair',
      category: 'furniture',
      thumbnail: 'ü™ë',
      format: 'GLB',
      size: '3.2 MB',
      polygons: '15.8K',
      isPremium: false,
      tags: ['furniture', 'chair', 'gaming']
    },
    {
      id: 5,
      name: 'Mecha Robot',
      category: 'characters',
      thumbnail: 'ü§ñ',
      format: 'GLB',
      size: '8.9 MB',
      polygons: '42.3K',
      isPremium: true,
      tags: ['robot', 'mecha', 'rigged']
    },
    {
      id: 6,
      name: 'Futuristic Car',
      category: 'vehicles',
      thumbnail: 'üöó',
      format: 'GLB',
      size: '6.5 MB',
      polygons: '28.7K',
      isPremium: true,
      tags: ['car', 'vehicle', 'futuristic']
    },
    {
      id: 7,
      name: 'Magical Staff',
      category: 'props',
      thumbnail: '‚ú®',
      format: 'GLB',
      size: '1.8 MB',
      polygons: '7.2K',
      isPremium: false,
      tags: ['magic', 'staff', 'fantasy']
    },
    {
      id: 8,
      name: 'Space Station',
      category: 'environments',
      thumbnail: 'üõ∏',
      format: 'GLB',
      size: '24.3 MB',
      polygons: '105.6K',
      isPremium: true,
      tags: ['space', 'station', 'sci-fi']
    },
    {
      id: 9,
      name: 'Japanese Garden',
      category: 'environments',
      thumbnail: 'üéã',
      format: 'GLB',
      size: '15.2 MB',
      polygons: '65.4K',
      isPremium: true,
      tags: ['garden', 'japanese', 'zen']
    },
    {
      id: 10,
      name: 'Holographic Display',
      category: 'props',
      thumbnail: 'üì±',
      format: 'GLB',
      size: '2.1 MB',
      polygons: '9.8K',
      isPremium: false,
      tags: ['tech', 'hologram', 'ui']
    }
  ];

  const filteredModels = models.filter(model => {
    const matchesCategory = selectedCategory === 'all' || model.category === selectedCategory;
    const matchesSearch = model.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         model.tags.some(tag => tag.toLowerCase().includes(searchQuery.toLowerCase()));
    return matchesCategory && matchesSearch;
  });

  const handleImport = (model) => {
    alert(`Importing "${model.name}" to your VR/AR Creator Studio...\n\nModel will be added to your object library and ready to use!`);
  };

  const handlePreview = (model) => {
    setSelectedModel(model);
  };

  return (
