          marginBottom: '40px'
        }}>
          {filteredModels.map(model => (
            <div
              key={model.id}
              style={{
                background: 'rgba(255, 255, 255, 0.05)',
                border: '2px solid rgba(255, 255, 255, 0.1)',
                borderRadius: '20px',
                padding: '20px',
                transition: 'all 0.3s ease',
                cursor: 'pointer'
              }}
              onClick={() => handlePreview(model)}
            >
              <div style={{
                width: '100%',
                height: '200px',
                background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                borderRadius: '15px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '80px',
                marginBottom: '15px',
                position: 'relative'
              }}>
                {model.thumbnail}
                {model.isPremium && (
                  <div style={{
                    position: 'absolute',
                    top: '10px',
                    right: '10px',
                    background: 'linear-gradient(135deg, #fbbf24 0%, #f59e0b 100%)',
                    padding: '6px 12px',
                    borderRadius: '8px',
                    fontSize: '12px',
                    fontWeight: '700'
                  }}>
                    üëë PRO
                  </div>
                )}
              </div>

              <h3 style={{
                fontSize: '16px',
                fontWeight: '700',
                marginBottom: '10px',
                whiteSpace: 'nowrap',
                overflow: 'hidden',
                textOverflow: 'ellipsis'
              }}>
                {model.name}
              </h3>

              <div style={{
                display: 'flex',
                gap: '8px',
                marginBottom: '15px',
                flexWrap: 'wrap'
              }}>
                {model.tags.slice(0, 3).map(tag => (
                  <span
                    key={tag}
                    style={{
                      padding: '4px 10px',
                      background: 'rgba(102, 126, 234, 0.2)',
                      border: '1px solid rgba(102, 126, 234, 0.4)',
                      borderRadius: '12px',
                      fontSize: '11px',
                      color: '#aaa'
                    }}
                  >
                    {tag}
                  </span>
                ))}
              </div>

              <div style={{
                display: 'flex',
                justifyContent: 'space-between',
                fontSize: '12px',
                color: '#aaa',
                marginBottom: '15px'
              }}>
                <span>{model.format}</span>
                <span>{model.size}</span>
                <span>{model.polygons} polys</span>
              </div>

              <button
                onClick={(e) => {
                  e.stopPropagation();
                  handleImport(model);
                }}
                style={{
                  width: '100%',
                  background: '#667eea',
                  color: 'white',
                  border: 'none',
                  padding: '12px',
                  borderRadius: '10px',
                  fontSize: '14px',
                  fontWeight: '600',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease'
                }}
              >
                ‚¨áÔ∏è Import
              </button>
            </div>
          ))}
        </div>

        {/* No Results */}
        {filteredModels.length === 0 && (
          <div style={{
            textAlign: 'center',
            padding: '60px 20px',
            color: '#aaa'
          }}>
            <div style={{ fontSize: '64px', marginBottom: '20px' }}>üîç</div>
            <h3 style={{ fontSize: '24px', marginBottom: '10px' }}>No models found</h3>
            <p>Try adjusting your search or filters</p>
          </div>
        )}

        {/* Model Preview Modal */}
        {selectedModel && (
          <div
            style={{
              position: 'fixed',
              top: 0,
              left: 0,
              right: 0,
              bottom: 0,
              background: 'rgba(0, 0, 0, 0.9)',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              zIndex: 1000,
              padding: '40px'
            }}
            onClick={() => setSelectedModel(null)}
          >
            <div
              style={{
                background: 'rgba(26, 26, 46, 0.95)',
                border: '2px solid rgba(255, 255, 255, 0.2)',
                borderRadius: '20px',
                padding: '40px',
                maxWidth: '800px',
                width: '100%'
              }}
              onClick={(e) => e.stopPropagation()}
            >
              <div style={{
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'flex-start',
                marginBottom: '30px'
              }}>
                <div>
                  <h2 style={{ fontSize: '28px', marginBottom: '10px' }}>
                    {selectedModel.name}
                  </h2>
                  <div style={{ display: 'flex', gap: '10px' }}>
                    {selectedModel.tags.map(tag => (
                      <span
                        key={tag}
                        style={{
                          padding: '6px 12px',
                          background: 'rgba(102, 126, 234, 0.2)',
                          border: '1px solid rgba(102, 126, 234, 0.4)',
                          borderRadius: '15px',
                          fontSize: '12px',
                          color: '#aaa'
                        }}
                      >
                        {tag}
                      </span>
                    ))}
                  </div>
                </div>
                <button
                  onClick={() => setSelectedModel(null)}
                  style={{
                    background: 'rgba(255, 255, 255, 0.1)',
                    border: 'none',
                    color: 'white',
                    padding: '10px 15px',
                    borderRadius: '10px',
                    cursor: 'pointer',
                    fontSize: '20px'
                  }}
                >
                  ‚úï
                </button>
              </div>

              <div style={{
                width: '100%',
                height: '400px',
                background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                borderRadius: '15px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '120px',
                marginBottom: '30px'
              }}>
                {selectedModel.thumbnail}
              </div>

              <div style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(3, 1fr)',
                gap: '20px',
                marginBottom: '30px'
              }}>
                <div>
                  <div style={{ fontSize: '12px', color: '#aaa', marginBottom: '5px' }}>
                    Format
                  </div>
                  <div style={{ fontSize: '18px', fontWeight: '600' }}>
                    {selectedModel.format}
                  </div>
                </div>
                <div>
                  <div style={{ fontSize: '12px', color: '#aaa', marginBottom: '5px' }}>
                    File Size
                  </div>
                  <div style={{ fontSize: '18px', fontWeight: '600' }}>
                    {selectedModel.size}
                  </div>
                </div>
                <div>
                  <div style={{ fontSize: '12px', color: '#aaa', marginBottom: '5px' }}>
                    Polygons
                  </div>
                  <div style={{ fontSize: '18px', fontWeight: '600' }}>
                    {selectedModel.polygons}
                  </div>
                </div>
              </div>

              <button
                onClick={() => handleImport(selectedModel)}
                style={{
                  width: '100%',
                  background: '#667eea',
                  color: 'white',
                  border: 'none',
                  padding: '18px',
                  borderRadius: '12px',
                  fontSize: '16px',
                  fontWeight: '700',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease'
                }}
              >
                ‚¨áÔ∏è Import to VR/AR Studio
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import { isOwner } from '../utils/ownerAuth';

export default function ModerationDashboard() {
  const [isOwnerUser, setIsOwnerUser] = useState(false);
  const [reports, setReports] = useState([
    { id: 1, type: 'spam', content: 'Suspicious content reported', reporter: 'user123', status: 'pending', date: '2025-11-15' },
    { id: 2, type: 'abuse', content: 'Inappropriate behavior', reporter: 'user456', status: 'pending', date: '2025-11-14' },
    { id: 3, type: 'copyright', content: 'Copyright violation claim', reporter: 'user789', status: 'resolved', date: '2025-11-13' }
  ]);

  useEffect(() => {
    const checkOwner = async () => {
      const ownerStatus = await isOwner();
      setIsOwnerUser(ownerStatus);
    };
    checkOwner();
  }, []);

  if (!isOwnerUser) {
    return (
      <div style={{ padding: '24px', textAlign: 'center' }}>
        <h2 style={{ fontSize: '1.5rem', marginBottom: '16px' }}>üîí Owner Access Only</h2>
        <p style={{ color: '#6b7280' }}>This dashboard is restricted to the platform owner.</p>
      </div>
    );
  }

  const handleAction = (reportId, action) => {
    console.log(`Action ${action} on report ${reportId}`);
    setReports(reports.map(r =>
      r.id === reportId ? { ...r, status: action === 'approve' ? 'resolved' : 'dismissed' } : r
    ));
  };

  const pendingReports = reports.filter(r => r.status === 'pending');

  return (
    <div style={{ padding: '24px' }}>
      <div style={{
        background: 'linear-gradient(135deg, #f59e0b 0%, #d97706 100%)',
        padding: '24px',
        borderRadius: '12px',
        color: 'white',
        marginBottom: '24px'
      }}>
        <h1 style={{ fontSize: '2rem', fontWeight: '700', marginBottom: '8px' }}>
          üõ°Ô∏è Moderation Dashboard
        </h1>
        <p style={{ opacity: 0.9 }}>
          Review and manage reported content
        </p>
      </div>

      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
        gap: '16px',
        marginBottom: '24px'
      }}>
        <div style={{
          background: 'white',
          padding: '20px',
          borderRadius: '12px',
          border: '1px solid #e5e7eb'
        }}>
          <div style={{ fontSize: '2rem', fontWeight: '700', color: '#ef4444' }}>
            {pendingReports.length}
          </div>
          <div style={{ color: '#6b7280', marginTop: '4px' }}>Pending Reports</div>
        </div>

        <div style={{
          background: 'white',
          padding: '20px',
          borderRadius: '12px',
          border: '1px solid #e5e7eb'
        }}>
          <div style={{ fontSize: '2rem', fontWeight: '700', color: '#10b981' }}>
            {reports.filter(r => r.status === 'resolved').length}
          </div>
          <div style={{ color: '#6b7280', marginTop: '4px' }}>Resolved</div>
        </div>

        <div style={{
          background: 'white',
          padding: '20px',
          borderRadius: '12px',
          border: '1px solid #e5e7eb'
        }}>
          <div style={{ fontSize: '2rem', fontWeight: '700', color: '#667eea' }}>
            {reports.length}
          </div>
          <div style={{ color: '#6b7280', marginTop: '4px' }}>Total Reports</div>
        </div>
      </div>

      <div style={{ background: 'white', borderRadius: '12px', border: '1px solid #e5e7eb', overflow: 'hidden' }}>
        <div style={{ padding: '16px', borderBottom: '1px solid #e5e7eb' }}>
          <h2 style={{ fontSize: '1.25rem', fontWeight: '700' }}>Recent Reports</h2>
        </div>

        {reports.map(report => (
          <div
            key={report.id}
            style={{
              padding: '16px',
              borderBottom: '1px solid #f3f4f6',
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              background: report.status === 'pending' ? '#fef3c7' : 'white'
            }}
          >
            <div style={{ flex: 1 }}>
              <div style={{ display: 'flex', alignItems: 'center', gap: '8px', marginBottom: '4px' }}>
                <span style={{
                  padding: '2px 8px',
                  borderRadius: '12px',
                  fontSize: '0.75rem',
                  fontWeight: '600',
                  background: report.type === 'spam' ? '#fee2e2' :
                    report.type === 'abuse' ? '#fecaca' : '#dbeafe',
                  color: report.type === 'spam' ? '#991b1b' :
                    report.type === 'abuse' ? '#7f1d1d' : '#1e40af'
                }}>
                  {report.type}
                </span>
                <span style={{
                  padding: '2px 8px',
                  borderRadius: '12px',
                  fontSize: '0.75rem',
                  fontWeight: '600',
                  background: report.status === 'pending' ? '#fef3c7' : '#d1fae5',
                  color: report.status === 'pending' ? '#92400e' : '#065f46'
                }}>
                  {report.status}
                </span>
              </div>
              <div style={{ fontWeight: '600', marginBottom: '4px' }}>{report.content}</div>
              <div style={{ fontSize: '0.875rem', color: '#6b7280' }}>
                Reported by: {report.reporter} ‚Ä¢ {report.date}
              </div>
            </div>

            {report.status === 'pending' && (
              <div style={{ display: 'flex', gap: '8px' }}>
                <button
                  onClick={() => handleAction(report.id, 'approve')}
                  style={{
                    padding: '8px 16px',
                    background: '#10b981',
                    color: 'white',
                    border: 'none',
                    borderRadius: '6px',
                    cursor: 'pointer',
                    fontWeight: '600',
                    fontSize: '0.875rem'
                  }}
                >
                  Resolve
                </button>
                <button
                  onClick={() => handleAction(report.id, 'dismiss')}
                  style={{
                    padding: '8px 16px',
                    background: '#ef4444',
                    color: 'white',
                    border: 'none',
                    borderRadius: '6px',
                    cursor: 'pointer',
                    fontWeight: '600',
                    fontSize: '0.875rem'
                  }}
                >
                  Dismiss
                </button>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

