import React, { useState, useEffect } from 'react';
import api from '../utils/backendApi';
import { FeatureBlocker } from './FeatureDisabledBanner';
import featureDetector from '../utils/featureDetection';
import './NotificationCenter.css';

export default function NotificationCenter() {
  const [notifications, setNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [features, setFeatures] = useState(featureDetector.getFeatures());

  useEffect(() => {
    const unsubscribe = featureDetector.subscribe(setFeatures);
    featureDetector.checkFeatures();
    return unsubscribe;
  }, []);

  useEffect(() => {
    if (features.socialMedia) {
      loadNotifications();
      loadUnreadCount();

      // Poll for new notifications every 30 seconds
      const interval = setInterval(() => {
        loadUnreadCount();
      }, 30000);

      return () => clearInterval(interval);
    }
  }, [features.socialMedia]);

  const loadNotifications = async () => {
    try {
      setLoading(true);
      const data = await api.notifications.getAll();
      setNotifications(data.notifications || []);
      setError(null);
    } catch (err) {
      console.error('Failed to load notifications:', err);
      setError('Failed to load notifications');
      setNotifications([]);
    } finally {
      setLoading(false);
    }
  };

  const loadUnreadCount = async () => {
    try {
      const data = await api.notifications.getUnreadCount();
      setUnreadCount(data.count || 0);
    } catch (err) {
      console.error('Failed to load unread count:', err);
    }
  };

  const handleMarkAsRead = async (notificationId) => {
    try {
      await api.notifications.markAsRead(notificationId);
      setNotifications(notifications.map(n =>
        n.id === notificationId ? { ...n, read: true } : n
      ));
      setUnreadCount(Math.max(0, unreadCount - 1));
    } catch (err) {
      console.error('Failed to mark as read:', err);
    }
  };

  const handleMarkAllRead = async () => {
    try {
      await api.notifications.markAllAsRead();
      setNotifications(notifications.map(n => ({ ...n, read: true })));
      setUnreadCount(0);
    } catch (err) {
      console.error('Failed to mark all as read:', err);
    }
  };

  const handleDelete = async (notificationId) => {
    try {
      await api.notifications.delete(notificationId);
      setNotifications(notifications.filter(n => n.id !== notificationId));
      const deleted = notifications.find(n => n.id === notificationId);
      if (deleted && !deleted.read) {
        setUnreadCount(Math.max(0, unreadCount - 1));
      }
    } catch (err) {
      console.error('Failed to delete notification:', err);
    }
  };

  const getNotificationIcon = (type) => {
    switch (type) {
      case 'LIKE': return '‚ù§Ô∏è';
      case 'COMMENT': return 'üí¨';
      case 'FOLLOW': return 'üëÅÔ∏è';
      case 'FRIEND_REQUEST': return 'üë•';
      case 'MESSAGE': return '‚úâÔ∏è';
      case 'MENTION': return 'üîî';
      case 'SUBSCRIPTION': return 'üíé';
      default: return 'üîî';
    }
  };

  return (
    <FeatureBlocker feature="socialMedia" features={features}>
      <div className="notification-center">
        <div className="notification-header">
          <h2>üîî Notifications</h2>
          {unreadCount > 0 && (
            <div className="unread-badge">{unreadCount} unread</div>
          )}
          {notifications.length > 0 && (
            <button className="mark-all-read-btn" onClick={handleMarkAllRead}>
              Mark all as read
            </button>
          )}
        </div>

        {error && (
          <div className="notification-error">{error}</div>
        )}

        {loading ? (
          <div className="notification-loading">Loading notifications...</div>
        ) : notifications.length === 0 ? (
          <div className="notification-empty">
            <div className="empty-icon">üîî</div>
            <h3>No notifications yet</h3>
            <p>When you get likes, comments, or messages, they'll show up here.</p>
          </div>
        ) : (
          <div className="notification-list">
            {notifications.map(notification => (
              <div
                key={notification.id}
                className={`notification-item ${!notification.read ? 'unread' : ''}`}
                onClick={() => !notification.read && handleMarkAsRead(notification.id)}
              >
                <div className="notification-icon">
                  {getNotificationIcon(notification.type)}
                </div>
                <div className="notification-content">
                  <div className="notification-text">
                    <strong>{notification.actor?.username || 'Someone'}</strong>
                    {' '}
                    {notification.message || 'interacted with your content'}
                  </div>
                  <div className="notification-time">
                    {new Date(notification.createdAt).toLocaleString()}
                  </div>
                </div>
                {!notification.read && (
                  <div className="notification-unread-dot"></div>
                )}
                <button
                  className="notification-delete"
                  onClick={(e) => {
                    e.stopPropagation();
                    handleDelete(notification.id);
                  }}
                >
                  ‚úï
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
    </FeatureBlocker>
  );
}

// Unread count badge for nav
export function NotificationBadge() {
  const [unreadCount, setUnreadCount] = useState(0);
  const [features, setFeatures] = useState(featureDetector.getFeatures());

  useEffect(() => {
    const unsubscribe = featureDetector.subscribe(setFeatures);
    featureDetector.checkFeatures();
    return unsubscribe;
  }, []);

  useEffect(() => {
    if (!features.socialMedia) return;

    const loadCount = async () => {
      try {
        const data = await api.notifications.getUnreadCount();
        setUnreadCount(data.count || 0);
      } catch (err) {
        console.error('Failed to load unread count:', err);
      }
    };

    loadCount();
    const interval = setInterval(loadCount, 30000); // Poll every 30s

    return () => clearInterval(interval);
  }, [features.socialMedia]);

  if (!features.socialMedia || unreadCount === 0) return null;

  return (
    <div className="notification-badge">{unreadCount}</div>
  );
}

import React, { useState, useEffect } from 'react';
import './OnboardingTour.css';

const tourSteps = [
  {
    id: 'welcome',
    title: 'üëã Welcome to ForTheWeebs!',
    description: 'Let\'s take a quick tour of your new creator dashboard.',
    target: null,
    position: 'center'
  },
  {
    id: 'tools',
    title: 'üé® Creator Tools',
    description: 'Access all your creative tools here: Audio Studio, Comic Creator, Graphic Design, and more!',
    target: '.creator-tools',
    position: 'bottom'
  },
  {
    id: 'projects',
    title: 'üìÅ Your Projects',
    description: 'View and manage all your projects in one place. Projects auto-save every 30 seconds!',
    target: '.projects-section',
    position: 'top'
  },
  {
    id: 'shortcuts',
    title: '‚å®Ô∏è Keyboard Shortcuts',
    description: 'Use Ctrl+S to save, Ctrl+E to export, and Ctrl+K for quick search.',
    target: null,
    position: 'center'
  },
  {
    id: 'theme',
    title: 'üåì Theme Toggle',
    description: 'Switch between light and dark mode with the button in the top right.',
    target: '.theme-toggle',
    position: 'left'
  },
  {
    id: 'complete',
    title: 'üéâ You\'re All Set!',
    description: 'Start creating amazing content for the anime community!',
    target: null,
    position: 'center'
  }
];

export function OnboardingTour({ onComplete }) {
  const [currentStep, setCurrentStep] = useState(0);
  const [isActive, setIsActive] = useState(() => {
    return !localStorage.getItem('onboardingCompleted');
  });

  useEffect(() => {
    if (isActive && currentStep < tourSteps.length) {
      const target = tourSteps[currentStep].target;
      if (target) {
        const element = document.querySelector(target);
        if (element) {
          element.scrollIntoView({ behavior: 'smooth', block: 'center' });
          element.classList.add('tour-highlight');
          return () => element.classList.remove('tour-highlight');
        }
      }
    }
  }, [currentStep, isActive]);

  const handleNext = () => {
    if (currentStep < tourSteps.length - 1) {
      setCurrentStep(currentStep + 1);
    } else {
      handleComplete();
    }
  };

  const handleSkip = () => {
    handleComplete();
  };

  const handleComplete = () => {
    localStorage.setItem('onboardingCompleted', 'true');
    setIsActive(false);
    if (onComplete) onComplete();
  };

  if (!isActive) return null;

  const step = tourSteps[currentStep];

  return (
    <>
      <div className="tour-overlay" onClick={handleSkip}></div>
      <div className={`tour-tooltip tour-position-${step.position}`}>
        <div className="tour-header">
          <h3>{step.title}</h3>
          <button onClick={handleSkip} className="tour-close">√ó</button>
        </div>
        <p className="tour-description">{step.description}</p>
        <div className="tour-footer">
          <span className="tour-progress">
            {currentStep + 1} of {tourSteps.length}
          </span>
          <div className="tour-actions">
            <button onClick={handleSkip} className="tour-skip">Skip</button>
            <button onClick={handleNext} className="tour-next">
              {currentStep === tourSteps.length - 1 ? 'Finish' : 'Next'} ‚Üí
            </button>
          </div>
        </div>
      </div>
    </>
  );
}

