                                <div className="affiliate-stats">
                                    <span>üë• {program.total_affiliates || 0} affiliates</span>
                                    <span>üíµ Avg. monthly: ${program.average_monthly_commission?.toFixed(0) || 0}</span>
                                </div>
                                <button 
                                    className="join-btn"
                                    onClick={() => handleJoinAffiliate(program.id)}
