import React from 'react';
import './ProgressBar.css';

export function ProgressBar({ progress = 0, showPercentage = true, color = '#8b5cf6' }) {
    const clampedProgress = Math.min(Math.max(progress, 0), 100);

    return (
        <div className="progress-bar-container">
            <div className="progress-bar-track">
                <div
                    className="progress-bar-fill"
                    style={{
                        width: `${clampedProgress}%`,
                        backgroundColor: color
                    }}
                >
                    {showPercentage && clampedProgress > 10 && (
                        <span className="progress-percentage">{Math.round(clampedProgress)}%</span>
                    )}
                </div>
            </div>
            {showPercentage && clampedProgress <= 10 && (
                <span className="progress-percentage-external">{Math.round(clampedProgress)}%</span>
            )}
        </div>
    );
}

export function CircularProgress({ size = 40, strokeWidth = 4, progress = 0 }) {
    const radius = (size - strokeWidth) / 2;
    const circumference = radius * 2 * Math.PI;
    const offset = circumference - (progress / 100) * circumference;

    return (
        <svg width={size} height={size} className="circular-progress">
            <circle
                cx={size / 2}
                cy={size / 2}
                r={radius}
                stroke="#e5e7eb"
                strokeWidth={strokeWidth}
                fill="none"
            />
            <circle
                cx={size / 2}
                cy={size / 2}
                r={radius}
                stroke="#8b5cf6"
                strokeWidth={strokeWidth}
                fill="none"
                strokeDasharray={circumference}
                strokeDashoffset={offset}
                strokeLinecap="round"
                style={{ transition: 'stroke-dashoffset 0.3s ease' }}
                transform={`rotate(-90 ${size / 2} ${size / 2})`}
            />
        </svg>
    );
}

import React, { useState, useEffect } from 'react';
import { proofOfCreation } from '../lib/proofOfCreation';
import './ProofOfCreationPanel.css';

export default function ProofOfCreationPanel({ artifactId, creatorId, file }) {
  const [proof, setProof] = useState(null);
  const [certificate, setCertificate] = useState(null);
  const [isGenerating, setIsGenerating] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);

  useEffect(() => {
    // Check if proof already exists
    const existingProofs = proofOfCreation.getCreatorProofs(creatorId);
    const existingProof = existingProofs.find(p => p.artifactId === artifactId);
    if (existingProof) {
      setProof(existingProof);
    }
  }, [artifactId, creatorId]);

  const generateProof = async () => {
    if (!file) {
      alert('No file provided');
      return;
    }

    setIsGenerating(true);
    try {
      const newProof = await proofOfCreation.generateProof(
        artifactId,
        creatorId,
        file,
        {
          filename: file.name,
          dimensions: await getImageDimensions(file),
        }
      );
      
      setProof(newProof);
      setShowSuccess(true);
      setTimeout(() => setShowSuccess(false), 3000);
    } catch (error) {
      console.error('Failed to generate proof:', error);
      alert('Failed to generate proof');
    } finally {
      setIsGenerating(false);
    }
  };

  const generateCert = async () => {
    if (!proof) return;

    try {
      const cert = await proofOfCreation.generateCertificate(artifactId);
      setCertificate(cert);
    } catch (error) {
      console.error('Failed to generate certificate:', error);
      alert('Failed to generate certificate');
    }
  };

  const exportProof = () => {
    const json = proofOfCreation.exportProof(artifactId);
    const blob = new Blob([json], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `proof-of-creation-${artifactId}.json`;
    a.click();
    URL.revokeObjectURL(url);
  };

  const copyVerificationUrl = () => {
    if (!certificate) return;
    navigator.clipboard.writeText(certificate.verificationUrl);
    alert('Verification URL copied to clipboard!');
  };

  const getImageDimensions = (file) => {
    return new Promise((resolve) => {
      const img = new Image();
      img.onload = () => {
        resolve({ width: img.width, height: img.height });
        URL.revokeObjectURL(img.src);
      };
      img.src = URL.createObjectURL(file);
    });
  };

  if (!proof) {
    return (
      <div className="proof-panel empty">
        <div className="proof-icon">üîê</div>
        <h3>Proof of Creation</h3>
        <p>Generate cryptographic proof that you created this content.</p>
        <ul className="benefits-list">
          <li>‚úì Timestamped ownership record</li>
          <li>‚úì Content fingerprint verification</li>
          <li>‚úì DMCA takedown assistance</li>
          <li>‚úì Blockchain-ready (coming soon)</li>
        </ul>
        <button 
          className="btn-primary btn-generate"
          onClick={generateProof}
          disabled={isGenerating || !file}
        >
          {isGenerating ? '‚è≥ Generating...' : 'üîê Generate Proof'}
        </button>
      </div>
    );
  }

  const verification = proofOfCreation.verifyProof(proof);

  return (
    <div className="proof-panel">
      <div className="panel-header">
        <h3>
          <span className="proof-icon">üîê</span>
          Proof of Creation
        </h3>
        {verification.valid ? (
          <span className="status-badge valid">‚úì Valid</span>
        ) : (
          <span className="status-badge invalid">‚úó Invalid</span>
        )}
      </div>

      {showSuccess && (
        <div className="success-banner">
          ‚úÖ Proof of Creation generated successfully!
        </div>
      )}

      <div className="proof-details">
        <div className="detail-item">
          <span className="detail-label">Content Hash</span>
          <span className="detail-value hash">
            {proof.contentHash.substring(0, 16)}...
          </span>
        </div>

        <div className="detail-item">
          <span className="detail-label">Created</span>
          <span className="detail-value">
            {new Date(proof.timestamp).toLocaleString()}
          </span>
        </div>

        <div className="detail-item">
          <span className="detail-label">File Size</span>
          <span className="detail-value">
            {proof.metadata.filesize 
              ? `${(proof.metadata.filesize / 1024).toFixed(1)} KB`
              : 'N/A'}
          </span>
        </div>

        {proof.metadata.dimensions && (
          <div className="detail-item">
            <span className="detail-label">Dimensions</span>
            <span className="detail-value">
              {proof.metadata.dimensions.width} √ó {proof.metadata.dimensions.height}
            </span>
          </div>
        )}

        <div className="detail-item">
          <span className="detail-label">Signature</span>
          <span className="detail-value hash">
            {proof.signature.substring(0, 20)}...
          </span>
        </div>
      </div>

      {proof.blockchainTx && (
        <div className="blockchain-badge">
          ‚õìÔ∏è Blockchain Verified
          <span className="tx-hash">{proof.blockchainTx.substring(0, 10)}...</span>
        </div>
      )}

      <div className="action-buttons">
        <button 
          className="btn-primary"
          onClick={generateCert}
        >
          üìú Generate Certificate
        </button>
        
        <button 
          className="btn-secondary"
          onClick={exportProof}
        >
          üì• Export Proof
        </button>
      </div>

      {certificate && (
        <div className="certificate-section">
          <h4>üéñÔ∏è Ownership Certificate</h4>
          <div className="certificate-card">
            <div className="cert-id">
              Certificate ID: {certificate.certificateId.substring(0, 16)}...
            </div>
            <div className="cert-issued">
              Issued: {new Date(certificate.issuedAt).toLocaleDateString()}
            </div>
            <div className="cert-verification">
              <label>Verification URL:</label>
              <div className="url-display">
                <input 
                  type="text" 
                  value={certificate.verificationUrl} 
                  readOnly 
                />
                <button onClick={copyVerificationUrl} className="btn-copy">
                  üìã Copy
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      <div className="protection-tools">
        <h4>üõ°Ô∏è Protection Tools</h4>
        <button className="tool-button">
          üîç Check for Re-uploads
        </button>
        <button className="tool-button">
          ‚öñÔ∏è Generate DMCA Takedown
        </button>
      </div>
    </div>
  );
}

