import React, { useState, useEffect, useRef } from 'react';
import './RealTimeActivityFeed.css';

/**
 * Real-Time Activity Feed
 * Live updates using Server-Sent Events (SSE) or WebSocket
 */
export const RealTimeActivityFeed = ({ userId }) => {
  const [activities, setActivities] = useState([]);
  const [isConnected, setIsConnected] = useState(false);
  const [filter, setFilter] = useState('all'); // all, posts, comments, likes, subs
  const eventSourceRef = useRef(null);

  useEffect(() => {
    connectToActivityStream();
    return () => disconnectFromActivityStream();
  }, []);

  const connectToActivityStream = () => {
    try {
      // Use Server-Sent Events for real-time updates
      const eventSource = new EventSource(`/api/activity/stream?userId=${userId}`);
      
      eventSource.onopen = () => {
        console.log('‚úÖ Activity stream connected');
        setIsConnected(true);
      };

      eventSource.onmessage = (event) => {
        try {
          const activity = JSON.parse(event.data);
          handleNewActivity(activity);
        } catch (err) {
          console.error('Failed to parse activity:', err);
        }
      };

      eventSource.onerror = () => {
        console.error('‚ùå Activity stream disconnected');
        setIsConnected(false);
        
        // Reconnect after 5 seconds
        setTimeout(() => {
          connectToActivityStream();
        }, 5000);
      };

      eventSourceRef.current = eventSource;
    } catch (error) {
      console.error('Failed to connect to activity stream:', error);
      
      // Fallback to polling
      startPolling();
    }
  };

  const disconnectFromActivityStream = () => {
    if (eventSourceRef.current) {
      eventSourceRef.current.close();
      eventSourceRef.current = null;
    }
  };

  const handleNewActivity = (activity) => {
    setActivities(prev => {
      // Add animation flag
      const newActivity = { ...activity, isNew: true };
      
      // Remove animation flag after 3 seconds
      setTimeout(() => {
        setActivities(current => 
          current.map(a => a.id === activity.id ? { ...a, isNew: false } : a)
        );
      }, 3000);

      // Keep only last 50 activities
      const updated = [newActivity, ...prev].slice(0, 50);
      return updated;
    });

    // Show notification for important events
    if (activity.type === 'subscription' || activity.type === 'tip') {
      showNotification(activity);
    }
  };

  const showNotification = (activity) => {
    if ('Notification' in window && Notification.permission === 'granted') {
      new Notification('ForTheWeebs', {
        body: getActivityText(activity),
        icon: '/icon-192.png',
        badge: '/badge-72.png'
      });
    }
  };

  const startPolling = () => {
    // Fallback: Poll every 10 seconds
    const pollInterval = setInterval(async () => {
      try {
        const response = await fetch(`/api/activity/recent?userId=${userId}&limit=10`);
        const data = await response.json();
        
        if (data.activities) {
          data.activities.forEach(activity => {
            // Only add if not already in list
            setActivities(prev => {
              const exists = prev.some(a => a.id === activity.id);
              return exists ? prev : [activity, ...prev].slice(0, 50);
            });
          });
        }
      } catch (error) {
        console.error('Polling failed:', error);
      }
    }, 10000);

    return () => clearInterval(pollInterval);
  };

  const getActivityIcon = (type) => {
    const icons = {
      post: 'üìù',
      comment: 'üí¨',
      like: '‚ù§Ô∏è',
      follow: 'üë•',
      subscription: 'üíé',
      tip: 'üí∞',
      share: 'üîÑ',
      mention: '@',
      milestone: 'üéâ'
    };
    return icons[type] || 'üìå';
  };

  const getActivityText = (activity) => {
    const { type, user, target, data } = activity;
    
    switch (type) {
      case 'post':
        return `${user.name} created a new post`;
      case 'comment':
        return `${user.name} commented on ${target.title || 'a post'}`;
      case 'like':
        return `${user.name} liked your ${target.type}`;
      case 'follow':
        return `${user.name} started following you`;
      case 'subscription':
        return `${user.name} subscribed to your channel!`;
      case 'tip':
        return `${user.name} sent you a $${data.amount} tip!`;
      case 'share':
        return `${user.name} shared your post`;
      case 'mention':
        return `${user.name} mentioned you in a ${target.type}`;
      case 'milestone':
        return `üéâ You reached ${data.milestone}!`;
      default:
        return 'New activity';
    }
  };

  const getActivityLink = (activity) => {
    const { type, target } = activity;
    
    switch (type) {
      case 'post':
      case 'comment':
      case 'like':
        return `/post/${target.postId}`;
      case 'follow':
      case 'subscription':
        return `/profile/${activity.user.id}`;
      default:
        return null;
    }
  };

  const filteredActivities = activities.filter(activity => {
    if (filter === 'all') return true;
    return activity.type === filter;
  });

  const formatTimeAgo = (timestamp) => {
    const seconds = Math.floor((Date.now() - new Date(timestamp)) / 1000);
    
    if (seconds < 60) return 'just now';
    if (seconds < 3600) return `${Math.floor(seconds / 60)}m ago`;
    if (seconds < 86400) return `${Math.floor(seconds / 3600)}h ago`;
    return `${Math.floor(seconds / 86400)}d ago`;
  };

  return (
    <div className="realtime-activity-feed">
      <div className="feed-header">
        <h2>‚ö° Live Activity</h2>
        <div className="connection-status">
          <span className={`status-dot ${isConnected ? 'connected' : 'disconnected'}`}></span>
          <span>{isConnected ? 'Live' : 'Connecting...'}</span>
        </div>
      </div>

      <div className="activity-filters">
        <button 
          className={filter === 'all' ? 'active' : ''} 
          onClick={() => setFilter('all')}
        >
          All
        </button>
        <button 
          className={filter === 'post' ? 'active' : ''} 
          onClick={() => setFilter('post')}
        >
          üìù Posts
        </button>
        <button 
          className={filter === 'comment' ? 'active' : ''} 
          onClick={() => setFilter('comment')}
        >
          üí¨ Comments
        </button>
        <button 
          className={filter === 'like' ? 'active' : ''} 
          onClick={() => setFilter('like')}
        >
          ‚ù§Ô∏è Likes
        </button>
        <button 
          className={filter === 'subscription' ? 'active' : ''} 
          onClick={() => setFilter('subscription')}
        >
          üíé Subs
        </button>
      </div>

      <div className="activity-list">
        {filteredActivities.length === 0 ? (
          <div className="empty-activity">
            <p>No recent activity</p>
            <span>Check back soon!</span>
          </div>
        ) : (
          filteredActivities.map((activity, idx) => (
            <ActivityItem
              key={`${activity.id}-${idx}`}
              activity={activity}
              icon={getActivityIcon(activity.type)}
              text={getActivityText(activity)}
              link={getActivityLink(activity)}
              timeAgo={formatTimeAgo(activity.timestamp)}
            />
          ))
        )}
      </div>

      {/* Live Stats */}
      <div className="live-stats">
        <div className="stat">
          <span className="stat-icon">üë•</span>
          <span className="stat-value">{activities.filter(a => a.type === 'follow').length}</span>
          <span className="stat-label">New Followers</span>
        </div>
        <div className="stat">
          <span className="stat-icon">üíé</span>
          <span className="stat-value">{activities.filter(a => a.type === 'subscription').length}</span>
          <span className="stat-label">New Subs</span>
        </div>
        <div className="stat">
          <span className="stat-icon">‚ù§Ô∏è</span>
          <span className="stat-value">{activities.filter(a => a.type === 'like').length}</span>
          <span className="stat-label">Likes</span>
        </div>
      </div>
    </div>
  );
};

/**
 * Activity Item Component
 */
const ActivityItem = ({ activity, icon, text, link, timeAgo }) => {
  const handleClick = () => {
    if (link) {
      window.location.href = link;
    }
  };

  return (
    <div 
      className={`activity-item ${activity.isNew ? 'new-activity' : ''} ${link ? 'clickable' : ''}`}
      onClick={handleClick}
    >
      <div className="activity-icon">{icon}</div>
      <div className="activity-content">
        <div className="activity-text">{text}</div>
        <div className="activity-time">{timeAgo}</div>
      </div>
      {activity.data?.amount && (
        <div className="activity-badge">${activity.data.amount}</div>
      )}
    </div>
  );
};

export default RealTimeActivityFeed;

// TypeScript interfaces for RemixBadge and RemixLineageTimelineProps

/**
 * @typedef {Object} RemixBadge
 * @property {string} creatorId
 * @property {string} tier
 * @property {number} timestamp
 * @property {string} lineageHash
 * @property {{ label: string, color: string, icon: string }} badge
 */

/**
 * @param {{ badges: RemixBadge[] }} props
 */

import React from "react";
import "./RemixLineageTimeline.css";

export const RemixLineageTimeline = ({ badges }) => {
  return (
  <div className="lineage-container">
      {badges
        .sort((a, b) => b.timestamp - a.timestamp)
        .map((badge) => (
          <div
            key={badge.lineageHash}
            className="lineage-item"
            style={{ backgroundColor: badge.badge.color }}
          >
            <div className="lineage-icon">{badge.badge.icon} {badge.badge.label}</div>
            <div className="lineage-desc">{new Date(badge.timestamp).toLocaleString()}</div>
            <div className="lineage-id"><strong>Hash:</strong> {badge.lineageHash}</div>
          </div>
        ))}
    </div>
  );
};

/**
 * @typedef {Object} RemixBadge
 * @property {string} creatorId
 * @property {string} tier
 * @property {number} timestamp
 * @property {string} lineageHash
 * @property {{ label: string, color: string, icon: string }} badge
 */

/**
 * @param {{ badges: RemixBadge[] }} props
 */
import React from "react";

export const RemixMap = ({ badges }) => {
  const radius = 120;
  const center = { x: 150, y: 150 };
  const angleStep = (2 * Math.PI) / badges.length;

  return (
    <svg width={300} height={300} className="remix-map-svg">
      <circle cx={center.x} cy={center.y} r={4} fill="#fff" />
      {badges.map((badge, i) => {
        const angle = i * angleStep;
        const x = center.x + radius * Math.cos(angle);
        const y = center.y + radius * Math.sin(angle);

        return (
          <g key={badge.lineageHash}>
            <line
              x1={center.x}
              y1={center.y}
              x2={x}
              y2={y}
              stroke="#555"
              strokeDasharray="2,2"
            />
            <circle cx={x} cy={y} r={10} fill={badge.badge.color} />
            <text
              x={x}
              y={y - 14}
              fill="#fff"
              fontSize="0.7rem"
              textAnchor="middle"
            >
              {badge.badge.icon}
            </text>
          </g>
        );
      })}
    </svg>
  );
};

// src/components/ReportBug.jsx - User bug report form
import React, { useState } from 'react';
import safeFetch from '../lib/safeFetch';

const ReportBug = () => {
  const [message, setMessage] = useState('');
  const [severity, setSeverity] = useState('low');
  const [submitted, setSubmitted] = useState(false);
  const [loading, setLoading] = useState(false);

  const apiUrl = globalThis.window?.__VITE_API_URL__ || 'http://localhost:3001';

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await safeFetch(`${apiUrl}/userfix/feedback/report`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          report_type: 'bug',
          message,
          page_url: globalThis.location.href,
          severity,
          metadata: {
            userAgent: navigator.userAgent,
            screenSize: `${window.innerWidth}x${window.innerHeight}`,
          },
        }),
      });

      if (response.ok) {
        setSubmitted(true);
        setMessage('');
      }
    } catch (error) {
      console.error('[ReportBug] Failed:', error);
      alert('Failed to submit report. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  if (submitted) {
    return (
      <div style={{ padding: '2rem', textAlign: 'center' }}>
        <h2>‚úÖ Thank you!</h2>
        <p>Your report has been submitted. We'll look into it!</p>
        <button onClick={() => setSubmitted(false)}>Report Another Issue</button>
      </div>
    );
  }

  return (
    <div style={{ padding: '2rem', maxWidth: '600px', margin: '0 auto' }}>
      <h2>üêõ Report a Bug</h2>
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: '1rem' }}>
          <label htmlFor="bug-description">
            Describe the issue:
            <textarea
              id="bug-description"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              required
              rows={5}
              style={{ width: '100%', padding: '0.5rem', marginTop: '0.5rem' }}
            />
          </label>
        </div>

        <div style={{ marginBottom: '1rem' }}>
          <label>
            Severity:
            <select
              value={severity}
              onChange={(e) => setSeverity(e.target.value)}
              style={{ marginLeft: '1rem', padding: '0.5rem' }}
            >
              <option value="low">Low</option>
              <option value="medium">Medium</option>
              <option value="high">High</option>
              <option value="critical">Critical</option>
            </select>
          </label>
        </div>

        <button
          type="submit"
          disabled={loading}
          style={{
            padding: '0.75rem 1.5rem',
            backgroundColor: '#0066cc',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: loading ? 'not-allowed' : 'pointer',
          }}
        >
          {loading ? 'Submitting...' : 'Submit Report'}
        </button>
      </form>
    </div>
  );
};

export default ReportBug;

import React, { useState, useEffect } from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';
import './Login.css';

export default function ResetPassword() {
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);
  const [tokenValid, setTokenValid] = useState(null);

  useEffect(() => {
    // Check if we have required recovery tokens
    const accessToken = searchParams.get('access_token');
    const type = searchParams.get('type');

    if (!accessToken || type !== 'recovery') {
      setTokenValid(false);
      setError('Invalid or expired reset link. Please request a new one.');
    } else {
      setTokenValid(true);
    }
  }, [searchParams]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    // Validation
    if (password.length < 8) {
      setError('Password must be at least 8 characters');
      return;
    }

    if (password !== confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    setLoading(true);

    try {
      const { supabase } = await import('../lib/supabase');

      // Update the user's password
      const { error: updateError } = await supabase.auth.updateUser({
        password: password
      });

      if (updateError) {
        setError(updateError.message);
        setLoading(false);
        return;
      }

      setSuccess(true);
      setLoading(false);

      // Redirect to login after 3 seconds
      setTimeout(() => {
        navigate('/login');
      }, 3000);

    } catch (err) {
      console.error('Password reset error:', err);
      setError('Failed to reset password. Please try again.');
      setLoading(false);
    }
  };

  if (tokenValid === false) {
    return (
      <div className="login-container">
        <div className="login-box">
          <div className="login-header">
            <div className="login-icon">‚ùå</div>
            <h2>Invalid Reset Link</h2>
            <p>This password reset link is invalid or has expired</p>
          </div>

          <div style={{ padding: '20px', textAlign: 'center' }}>
            <p style={{ opacity: 0.8, marginBottom: '20px' }}>
              Password reset links expire after 1 hour for security.
            </p>
            <button
              onClick={() => navigate('/login')}
              className="login-btn"
            >
              ‚Üê Back to login
            </button>
            <p style={{ marginTop: '15px', fontSize: '0.9rem', opacity: 0.7 }}>
              Need a new link? Click "Forgot password?" on the login page.
            </p>
          </div>
        </div>
      </div>
    );
  }

  if (success) {
    return (
      <div className="login-container">
        <div className="login-box">
          <div className="login-header">
            <div className="login-icon">‚úÖ</div>
            <h2>Password Reset Successfully!</h2>
            <p>Your password has been updated</p>
          </div>

          <div style={{ padding: '20px', textAlign: 'center' }}>
            <p style={{ opacity: 0.8, marginBottom: '20px' }}>
              You can now log in with your new password.
            </p>
            <div className="spinner" style={{ margin: '20px auto' }}></div>
            <p style={{ fontSize: '0.9rem', opacity: 0.7 }}>
              Redirecting to login...
            </p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="login-container">
      <div className="login-box">
        <div className="login-header">
          <div className="login-icon">üîê</div>
          <h2>Reset Your Password</h2>
          <p>Enter your new password below</p>
        </div>

        <form onSubmit={handleSubmit} className="login-form">
          <div className="form-group">
            <label htmlFor="password">New Password</label>
            <input
              id="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter new password"
              required
              minLength={8}
              autoFocus
            />
            <p style={{ fontSize: '0.85rem', opacity: 0.7, marginTop: '5px' }}>
              Must be at least 8 characters
            </p>
          </div>

          <div className="form-group">
            <label htmlFor="confirmPassword">Confirm Password</label>
            <input
              id="confirmPassword"
              type="password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              placeholder="Confirm new password"
              required
              minLength={8}
            />
          </div>

          {/* Password strength indicator */}
          {password && (
            <div style={{ marginBottom: '15px' }}>
              <div style={{
                fontSize: '0.85rem',
                marginBottom: '5px',
                opacity: 0.8
              }}>
                Password strength:
              </div>
              <div style={{
                height: '4px',
                background: '#e5e7eb',
                borderRadius: '2px',
                overflow: 'hidden'
              }}>
                <div style={{
                  height: '100%',
                  background: password.length < 8 ? '#ef4444' :
                            password.length < 12 ? '#f59e0b' :
                            password.length < 16 ? '#3b82f6' : '#10b981',
                  width: password.length < 8 ? '33%' :
                        password.length < 12 ? '66%' : '100%',
                  transition: 'all 0.3s ease'
                }}></div>
              </div>
              <div style={{
                fontSize: '0.75rem',
                marginTop: '5px',
                opacity: 0.7,
                color: password.length < 8 ? '#ef4444' :
                      password.length < 12 ? '#f59e0b' :
                      password.length < 16 ? '#3b82f6' : '#10b981'
              }}>
                {password.length < 8 ? 'Too short' :
                 password.length < 12 ? 'Fair' :
                 password.length < 16 ? 'Good' : 'Excellent'}
              </div>
            </div>
          )}

          {error && (
            <div className="error-message">
              <span className="error-icon">‚ö†Ô∏è</span>
              {error}
            </div>
          )}

          <button type="submit" className="login-btn" disabled={loading}>
            {loading ? (
              <>
                <span className="spinner"></span>
                Resetting password...
              </>
            ) : (
              <>
                üîê Reset password
              </>
            )}
          </button>

          <button
            type="button"
            onClick={() => navigate('/login')}
            style={{
              marginTop: '10px',
              background: 'transparent',
              color: '#667eea',
              border: 'none',
              cursor: 'pointer'
            }}
          >
            ‚Üê Back to login
          </button>
        </form>
      </div>
    </div>
  );
}

