import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import './Search.css';

function Search({ onSearch }) {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchType, setSearchType] = useState('all'); // all, creators, artworks, tags
  const [filters, setFilters] = useState({
    nsfw: 'all', // all, sfw, nsfw
    sortBy: 'relevance', // relevance, popular, recent
    category: 'all', // all, anime, manga, original, fanart, commission
  });
  const [results, setResults] = useState({
    creators: [],
    artworks: [],
    tags: [],
  });
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    if (searchQuery.length >= 2) {
      performSearch();
    } else {
      setResults({ creators: [], artworks: [], tags: [] });
    }
  }, [searchQuery, searchType, filters]);

  const performSearch = async () => {
    setIsLoading(true);

    // Firestore queries can be implemented here for live search
    // For now, using mock data

    await new Promise(resolve => setTimeout(resolve, 300)); // Simulate API delay

    const mockCreators = [
      {
        id: '1',
        username: 'SakuraArtist',
        displayName: 'Sakura',
        avatar: null,
        followers: 15200,
        artworkCount: 342,
        verified: true,
        badges: ['verified', 'pro']
      },
      {
        id: '2',
        username: 'MangaMaster',
        displayName: 'Manga Master',
        avatar: null,
        followers: 8900,
        artworkCount: 189,
        verified: false,
        badges: ['top_seller']
      },
    ];

    const mockArtworks = [
      {
        id: '1',
        title: 'Sunset Dreams',
        imageUrl: null,
        creator: 'SakuraArtist',
        creatorId: '1',
        likes: 1234,
        views: 45678,
        nsfw: false,
        tags: ['anime', 'original', 'sunset'],
        category: 'anime'
      },
      {
        id: '2',
        title: 'Character Study',
        imageUrl: null,
        creator: 'MangaMaster',
        creatorId: '2',
        likes: 892,
        views: 12340,
        nsfw: false,
        tags: ['manga', 'sketch', 'character'],
        category: 'manga'
      },
    ];

    const mockTags = [
      { name: 'anime', count: 12450 },
      { name: 'manga', count: 8930 },
      { name: 'original', count: 6720 },
      { name: 'fanart', count: 15200 },
    ];

    // Filter by search query
    const filteredCreators = mockCreators.filter(c =>
      c.username.toLowerCase().includes(searchQuery.toLowerCase()) ||
      c.displayName.toLowerCase().includes(searchQuery.toLowerCase())
    );

    const filteredArtworks = mockArtworks.filter(a =>
      a.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      a.tags.some(tag => tag.toLowerCase().includes(searchQuery.toLowerCase()))
    );

    const filteredTags = mockTags.filter(t =>
      t.name.toLowerCase().includes(searchQuery.toLowerCase())
    );

    // Apply NSFW filter
    const nsfwFilteredArtworks = filters.nsfw === 'all'
      ? filteredArtworks
      : filteredArtworks.filter(a =>
        filters.nsfw === 'nsfw' ? a.nsfw : !a.nsfw
      );

    // Apply category filter
    const categoryFilteredArtworks = filters.category === 'all'
      ? nsfwFilteredArtworks
      : nsfwFilteredArtworks.filter(a => a.category === filters.category);

    // Apply sorting
    let sortedArtworks = [...categoryFilteredArtworks];
    if (filters.sortBy === 'popular') {
      sortedArtworks.sort((a, b) => b.likes - a.likes);
    } else if (filters.sortBy === 'recent') {
      sortedArtworks.sort((a, b) => b.id - a.id); // Assuming higher ID = newer
    }

    setResults({
      creators: filteredCreators,
      artworks: sortedArtworks,
      tags: filteredTags,
    });

    setIsLoading(false);

    if (onSearch) {
      onSearch({
        query: searchQuery,
        type: searchType,
        results: {
          creators: filteredCreators,
          artworks: sortedArtworks,
          tags: filteredTags,
        }
      });
    }
  };

  const handleFilterChange = (filterKey, value) => {
    setFilters(prev => ({
      ...prev,
      [filterKey]: value
    }));
  };

  const clearSearch = () => {
    setSearchQuery('');
    setResults({ creators: [], artworks: [], tags: [] });
  };

  const totalResults = results.creators.length + results.artworks.length + results.tags.length;

  return (
    <div className="search-container">
      <div className="search-header">
        <div className="search-input-wrapper">
          <input
            type="text"
            className="search-input"
            placeholder="Search creators, artworks, tags..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          {searchQuery && (
            <button className="clear-search" onClick={clearSearch}>
              ‚úï
            </button>
          )}
          <button className="search-button" onClick={performSearch}>
            üîç
          </button>
        </div>

        <div className="search-type-selector">
          <button
            className={searchType === 'all' ? 'active' : ''}
            onClick={() => setSearchType('all')}
          >
            All
          </button>
          <button
            className={searchType === 'creators' ? 'active' : ''}
            onClick={() => setSearchType('creators')}
          >
            Creators ({results.creators.length})
          </button>
          <button
            className={searchType === 'artworks' ? 'active' : ''}
            onClick={() => setSearchType('artworks')}
          >
            Artworks ({results.artworks.length})
          </button>
          <button
            className={searchType === 'tags' ? 'active' : ''}
            onClick={() => setSearchType('tags')}
          >
            Tags ({results.tags.length})
          </button>
        </div>
      </div>

      <div className="search-filters">
        <div className="filter-group">
          <label htmlFor="filter-content">Content:</label>
          <select
            id="filter-content"
            value={filters.nsfw}
            onChange={(e) => handleFilterChange('nsfw', e.target.value)}
          >
            <option value="all">All Content</option>
            <option value="sfw">SFW Only</option>
            <option value="nsfw">NSFW Only</option>
          </select>
        </div>

        <div className="filter-group">
          <label htmlFor="filter-category">Category:</label>
          <select
            id="filter-category"
            value={filters.category}
            onChange={(e) => handleFilterChange('category', e.target.value)}
          >
            <option value="all">All Categories</option>
            <option value="anime">Anime</option>
            <option value="manga">Manga</option>
            <option value="original">Original</option>
            <option value="fanart">Fanart</option>
            <option value="commission">Commission</option>
          </select>
        </div>

        <div className="filter-group">
          <label htmlFor="filter-sort">Sort By:</label>
          <select
            id="filter-sort"
            value={filters.sortBy}
            onChange={(e) => handleFilterChange('sortBy', e.target.value)}
          >
            <option value="relevance">Relevance</option>
            <option value="popular">Most Popular</option>
            <option value="recent">Most Recent</option>
          </select>
        </div>
      </div>

      {isLoading && (
        <div className="search-loading">
          <div className="loading-spinner"></div>
          <p>Searching...</p>
        </div>
      )}

      {!isLoading && searchQuery && (
        <div className="search-results">
          <div className="results-header">
            <h2>
              {totalResults} result{totalResults === 1 ? '' : 's'}
              {searchQuery && ` for "${searchQuery}"`}
            </h2>
          </div>

          {(searchType === 'all' || searchType === 'creators') && results.creators.length > 0 && (
            <div className="results-section">
              <h3>Creators</h3>
              <div className="creator-results">
                {results.creators.map(creator => (
                  <div key={creator.id} className="creator-card">
                    <div className="creator-avatar">
                      {creator.avatar ? (
                        <img src={creator.avatar} alt={creator.username} />
                      ) : (
                        <div className="avatar-placeholder">
                          {creator.username[0].toUpperCase()}
                        </div>
                      )}
                    </div>
                    <div className="creator-info">
                      <div className="creator-name">
                        {creator.displayName}
                        {creator.verified && <span className="verified-badge">‚úì</span>}
                      </div>
                      <div className="creator-username">@{creator.username}</div>
                      <div className="creator-stats">
                        <span>üë• {creator.followers.toLocaleString()} followers</span>
                        <span>üé® {creator.artworkCount} artworks</span>
                      </div>
                      {creator.badges && creator.badges.length > 0 && (
                        <div className="creator-badges">
                          {creator.badges.map(badge => (
                            <span key={badge} className={`badge ${badge}`}>
                              {badge === 'verified' && '‚úì'}
                              {badge === 'pro' && '‚≠ê'}
                              {badge === 'top_seller' && 'üèÜ'}
                            </span>
                          ))}
                        </div>
                      )}
                    </div>
                    <button className="view-profile-button">View Profile</button>
                  </div>
                ))}
              </div>
            </div>
          )}

          {(searchType === 'all' || searchType === 'artworks') && results.artworks.length > 0 && (
            <div className="results-section">
              <h3>Artworks</h3>
              <div className="artwork-results">
                {results.artworks.map(artwork => (
                  <div key={artwork.id} className="artwork-card">
                    <div className="artwork-image">
                      {artwork.imageUrl ? (
                        <img src={artwork.imageUrl} alt={artwork.title} />
                      ) : (
                        <div className="image-placeholder">üñºÔ∏è</div>
                      )}
                      {artwork.nsfw && <div className="nsfw-badge">üîû NSFW</div>}
                    </div>
                    <div className="artwork-info">
                      <h4>{artwork.title}</h4>
                      <p className="artwork-creator">by {artwork.creator}</p>
                      <div className="artwork-stats">
                        <span>‚ù§Ô∏è {artwork.likes.toLocaleString()}</span>
                        <span>üëÅÔ∏è {artwork.views.toLocaleString()}</span>
                      </div>
                      <div className="artwork-tags">
                        {artwork.tags.slice(0, 3).map(tag => (
                          <span key={tag} className="tag">#{tag}</span>
                        ))}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {(searchType === 'all' || searchType === 'tags') && results.tags.length > 0 && (
            <div className="results-section">
              <h3>Tags</h3>
              <div className="tag-results">
                {results.tags.map(tag => (
                  <div key={tag.name} className="tag-card">
                    <span className="tag-name">#{tag.name}</span>
                    <span className="tag-count">{tag.count.toLocaleString()} posts</span>
                  </div>
                ))}
              </div>
            </div>
          )}

          {totalResults === 0 && (
            <div className="no-results">
              <p>No results found for "{searchQuery}"</p>
              <p>Try different keywords or check your filters</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

Search.propTypes = {
  onSearch: PropTypes.func.isRequired
};

export default Search;

import React, { useState } from 'react';
import { useAuth } from './AuthSupabase.jsx';
import './SecurityChallenge.css';

/**
 * Security Challenge - Re-authentication prompt
 * Shows even when user is already logged in
 * Use this to protect sensitive actions/pages
 */
export function SecurityChallenge({ onSuccess, onCancel, reason = "verify your identity" }) {
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [stayLoggedIn, setStayLoggedIn] = useState(false);
  const { user, login } = useAuth();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      // Re-authenticate with current user's email and provided password
      if (!user?.email) {
        throw new Error('No user session found');
      }

      await login(user.email, password);

      // Store security verification timestamp
      const now = Date.now().toString();

      if (stayLoggedIn) {
        // Store in localStorage for persistent verification (30 days)
        localStorage.setItem('security_verified_at', now);
        localStorage.setItem('security_stay_logged_in', 'true');
      } else {
        // Store in sessionStorage (expires when browser closes or after 15 min)
        sessionStorage.setItem('security_verified_at', now);
      }

      if (onSuccess) onSuccess();
    } catch (err) {
      setError('Incorrect password. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="security-challenge-overlay">
      <div className="security-challenge-modal">
        <div className="security-challenge-header">
          <div className="security-icon">üîí</div>
          <h2>Security Verification Required</h2>
          <p>Please re-enter your password to {reason}</p>
        </div>

        {error && <div className="security-error">{error}</div>}

        <form onSubmit={handleSubmit} className="security-form">
          <div className="security-user-info">
            <div className="user-email-display">
              <span className="email-label">Verifying as:</span>
              <span className="email-value">{user?.email || 'Unknown'}</span>
            </div>
          </div>

          <div className="security-input-group">
            <label htmlFor="security-password">Password</label>
            <input
              id="security-password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              autoFocus
              required
              disabled={loading}
            />
          </div>

          <div className="security-checkbox-group">
            <label className="security-checkbox-label">
              <input
                type="checkbox"
                checked={stayLoggedIn}
                onChange={(e) => setStayLoggedIn(e.target.checked)}
                disabled={loading}
              />
              <span>Stay logged in for 30 days</span>
            </label>
            <small className="security-checkbox-help">
              Don't check this on shared devices
            </small>
          </div>

          <div className="security-actions">
            <button
              type="button"
              onClick={onCancel}
              className="security-btn security-btn-cancel"
              disabled={loading}
            >
              Cancel
            </button>
            <button
              type="submit"
              className="security-btn security-btn-verify"
              disabled={loading}
            >
              {loading ? 'Verifying...' : 'Verify Identity'}
            </button>
          </div>
        </form>

        <div className="security-footer">
          <small>This verification helps keep your account secure</small>
        </div>
      </div>
    </div>
  );
}

/**
 * Hook to check if security verification is still valid
 * Session verification expires after 15 minutes
 * Persistent verification expires after 30 days
 */
export function useSecurityVerification() {
  const isVerified = () => {
    // Check for persistent "stay logged in" verification first
    const persistentVerifiedAt = localStorage.getItem('security_verified_at');
    const stayLoggedIn = localStorage.getItem('security_stay_logged_in') === 'true';

    if (stayLoggedIn && persistentVerifiedAt) {
      const thirtyDays = 30 * 24 * 60 * 60 * 1000;
      const timeSinceVerification = Date.now() - parseInt(persistentVerifiedAt);

      if (timeSinceVerification < thirtyDays) {
        return true;
      } else {
        // Expired, clear it
        localStorage.removeItem('security_verified_at');
        localStorage.removeItem('security_stay_logged_in');
      }
    }

    // Check session verification (15 minutes)
    const sessionVerifiedAt = sessionStorage.getItem('security_verified_at');
    if (!sessionVerifiedAt) return false;

    const fifteenMinutes = 15 * 60 * 1000;
    const timeSinceVerification = Date.now() - parseInt(sessionVerifiedAt);

    return timeSinceVerification < fifteenMinutes;
  };

  const clearVerification = () => {
    sessionStorage.removeItem('security_verified_at');
    localStorage.removeItem('security_verified_at');
    localStorage.removeItem('security_stay_logged_in');
  };

  return { isVerified, clearVerification };
}

/**
 * HOC to wrap components that need security verification
 * Usage: export default withSecurityChallenge(YourComponent)
 */
export function withSecurityChallenge(Component, reason) {
  return function ProtectedComponent(props) {
    const [showChallenge, setShowChallenge] = useState(false);
    const [isVerified, setIsVerified] = useState(false);
    const { isVerified: checkVerified } = useSecurityVerification();

    React.useEffect(() => {
      // Check if already verified
      if (checkVerified()) {
        setIsVerified(true);
      } else {
        setShowChallenge(true);
      }
    }, []);

    if (showChallenge && !isVerified) {
      return (
        <SecurityChallenge
          reason={reason}
          onSuccess={() => {
            setIsVerified(true);
            setShowChallenge(false);
          }}
          onCancel={() => {
            // Redirect back or show error
            window.history.back();
          }}
        />
      );
    }

    if (!isVerified) {
      return (
        <div style={{ padding: '40px', textAlign: 'center' }}>
          <div className="loading-spinner"></div>
          <p>Checking security verification...</p>
        </div>
      );
    }

    return <Component {...props} />;
  };
}

export default SecurityChallenge;

