          />
          <StatCard
            title="Low Risk"
            value={fraudStats?.byRiskLevel?.low || 0}
            color="green"
            icon="‚úÖ"
          />
          <StatCard
            title="Medium Risk"
            value={fraudStats?.byRiskLevel?.medium || 0}
            color="yellow"
            icon="‚ö†Ô∏è"
          />
          <StatCard
            title="High Risk"
            value={fraudStats?.byRiskLevel?.high || 0}
            color="orange"
            icon="üî∏"
          />
          <StatCard
            title="Critical"
            value={fraudStats?.byRiskLevel?.critical || 0}
            color="red"
            icon="üö®"
          />
          <StatCard
            title="Blocked"
            value={fraudStats?.blocked || 0}
