                                <label style={{ fontSize: '12px', color: '#aaa' }}>
                                    Split Sensitivity: <span style={{ color: '#0ff', fontFamily: 'monospace' }}>{settings.splitSensitivity}%</span>
                                    <span style={{ fontSize: '11px', display: 'block', color: '#666', marginTop: '3px' }}>
                                        Higher = more aggressive splitting
                                    </span>
                                </label>
                                <input
                                    type="range"
                                    min="0"
                                    max="100"
                                    value={settings.splitSensitivity}
                                    onChange={(e) => setSettings({ ...settings, splitSensitivity: Number.parseInt(e.target.value, 10) })}
                                    style={{ width: '100%' }}
                                />
                            </div>
                        )}

                        {settings.removeDuplicates && (
                            <div style={{ marginTop: '15px' }}>
                                <label style={{ fontSize: '12px', color: '#aaa' }}>
                                    Duplicate Threshold: <span style={{ color: '#0ff', fontFamily: 'monospace' }}>{settings.duplicateThreshold}%</span>
                                </label>
                                <input
                                    type="range"
                                    min="80"
                                    max="100"
                                    value={settings.duplicateThreshold}
                                    onChange={(e) => setSettings({ ...settings, duplicateThreshold: Number.parseInt(e.target.value, 10) })}
                                    style={{ width: '100%' }}
                                />
                            </div>
                        )}

                        {settings.autoEnhance && (
                            <>
                                <div style={{ marginTop: '15px' }}>
                                    <label style={{ fontSize: '12px', color: '#aaa' }}>
                                        Brightness: <span style={{ color: '#0ff', fontFamily: 'monospace' }}>+{settings.brightnessBoost}%</span>
                                    </label>
                                    <input
                                        type="range"
                                        min="0"
                                        max="50"
                                        value={settings.brightnessBoost}
                                        onChange={(e) => setSettings({ ...settings, brightnessBoost: Number.parseInt(e.target.value, 10) })}
                                        style={{ width: '100%' }}
                                    />
                                </div>

                                <div style={{ marginTop: '15px' }}>
                                    <label style={{ fontSize: '12px', color: '#aaa' }}>
                                        Contrast: <span style={{ color: '#0ff', fontFamily: 'monospace' }}>+{settings.contrastBoost}%</span>
                                    </label>
                                    <input
                                        type="range"
                                        min="0"
                                        max="50"
                                        value={settings.contrastBoost}
                                        onChange={(e) => setSettings({ ...settings, contrastBoost: Number.parseInt(e.target.value, 10) })}
                                        style={{ width: '100%' }}
                                    />
                                </div>

                                <div style={{ marginTop: '15px' }}>
                                    <label style={{ fontSize: '12px', color: '#aaa' }}>
                                        Sharpen: <span style={{ color: '#0ff', fontFamily: 'monospace' }}>{settings.sharpenAmount}%</span>
                                    </label>
                                    <input
                                        type="range"
                                        min="0"
                                        max="100"
                                        value={settings.sharpenAmount}
                                        onChange={(e) => setSettings({ ...settings, sharpenAmount: Number.parseInt(e.target.value, 10) })}
                                        style={{ width: '100%' }}
                                    />
                                </div>
                            </>
                        )}

                        {/* Process Button */}
                        <button
                            onClick={processImages}
                            disabled={images.length === 0 || processing}
                            style={{
                                width: '100%',
                                marginTop: '30px',
                                padding: '18px',
                                fontSize: '18px',
                                fontWeight: 'bold',
                                background: images.length > 0 && !processing
                                    ? 'linear-gradient(135deg, #00ff00, #00aa00)'
                                    : 'rgba(128, 128, 128, 0.3)',
                                border: 'none',
                                borderRadius: '12px',
                                color: '#fff',
                                cursor: images.length > 0 && !processing ? 'pointer' : 'not-allowed',
                            }}
                        >
                            {processing ? '‚è≥ Processing...' : `üöÄ Process ${images.length} Images`}
                        </button>
                    </div>
                </div>
            ) : (
                // Results View
                <div>
                    {/* Stats */}
                    <div style={{
                        display: 'grid',
                        gridTemplateColumns: 'repeat(5, 1fr)',
                        gap: '15px',
                        marginBottom: '30px',
                    }}>
                        <div style={{
                            background: 'rgba(0, 255, 255, 0.1)',
                            border: '1px solid #0ff',
                            borderRadius: '12px',
                            padding: '20px',
                            textAlign: 'center',
                        }}>
                            <div style={{ fontSize: '36px', fontFamily: 'monospace', color: '#0ff', fontWeight: 'bold' }}>
                                {results.stats.total}
                            </div>
                            <div style={{ fontSize: '14px', color: '#aaa', marginTop: '5px' }}>Total Images</div>
                        </div>

                        <div style={{
                            background: 'rgba(0, 255, 0, 0.1)',
                            border: '1px solid #0f0',
                            borderRadius: '12px',
                            padding: '20px',
                            textAlign: 'center',
                        }}>
                            <div style={{ fontSize: '36px', fontFamily: 'monospace', color: '#0f0', fontWeight: 'bold' }}>
                                {results.stats.kept}
                            </div>
                            <div style={{ fontSize: '14px', color: '#aaa', marginTop: '5px' }}>Kept</div>
                        </div>

                        <div style={{
                            background: 'rgba(255, 68, 68, 0.1)',
                            border: '1px solid #ff4444',
                            borderRadius: '12px',
                            padding: '20px',
                            textAlign: 'center',
                        }}>
                            <div style={{ fontSize: '36px', fontFamily: 'monospace', color: '#ff4444', fontWeight: 'bold' }}>
                                {results.stats.deleted}
                            </div>
                            <div style={{ fontSize: '14px', color: '#aaa', marginTop: '5px' }}>Deleted</div>
                        </div>

                        <div style={{
                            background: 'rgba(255, 255, 0, 0.1)',
                            border: '1px solid #ffff00',
                            borderRadius: '12px',
                            padding: '20px',
                            textAlign: 'center',
                        }}>
                            <div style={{ fontSize: '36px', fontFamily: 'monospace', color: '#ffff00', fontWeight: 'bold' }}>
                                {results.stats.duplicates}
                            </div>
                            <div style={{ fontSize: '14px', color: '#aaa', marginTop: '5px' }}>Duplicates</div>
                        </div>

                        <div style={{
                            background: 'rgba(255, 0, 255, 0.1)',
                            border: '1px solid #ff00ff',
                            borderRadius: '12px',
                            padding: '20px',
                            textAlign: 'center',
                        }}>
                            <div style={{ fontSize: '36px', fontFamily: 'monospace', color: '#ff00ff', fontWeight: 'bold' }}>
                                {results.stats.split}
                            </div>
                            <div style={{ fontSize: '14px', color: '#aaa', marginTop: '5px' }}>Images Split</div>
                        </div>
                    </div>

                    {/* Action Buttons */}
                    <div style={{ display: 'flex', gap: '15px', marginBottom: '30px' }}>
                        <button
                            onClick={downloadAll}
                            style={{
                                flex: 1,
                                padding: '18px',
                                fontSize: '16px',
                                fontWeight: 'bold',
                                background: 'linear-gradient(135deg, #00aaff, #0066ff)',
                                border: 'none',
                                borderRadius: '12px',
                                color: '#fff',
                                cursor: 'pointer',
                            }}
                        >
                            ‚¨áÔ∏è Download All ({results.stats.kept})
                        </button>

                        <button
                            onClick={saveToCloud}
                            style={{
                                flex: 1,
                                padding: '18px',
                                fontSize: '16px',
                                fontWeight: 'bold',
                                background: 'linear-gradient(135deg, #ff00ff, #aa00ff)',
                                border: 'none',
                                borderRadius: '12px',
                                color: '#fff',
                                cursor: 'pointer',
                            }}
                        >
                            ‚òÅÔ∏è Upload to Cloud
                        </button>

                        <button
                            onClick={continueToEditor}
                            style={{
                                flex: 1,
                                padding: '18px',
                                fontSize: '16px',
                                fontWeight: 'bold',
                                background: 'linear-gradient(135deg, #00ff00, #00aa00)',
                                border: 'none',
                                borderRadius: '12px',
                                color: '#fff',
                                cursor: 'pointer',
                            }}
                        >
                            üé® Continue Editing ‚Üí
                        </button>
                    </div>

                    {/* Preview Grid */}
                    <h2 style={{ fontSize: '20px', marginBottom: '15px', color: '#0ff' }}>
                        ‚ú® Processed Wallpapers {results.stats.split > 0 && `(includes ${results.stats.split} split images)`}
                    </h2>
                    <div style={{
                        display: 'grid',
                        gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))',
                        gap: '15px',
                    }}>
                        {results.processed.map((img) => (
                            <div key={img.id} style={{
                                background: 'rgba(0, 0, 0, 0.3)',
                                borderRadius: '12px',
                                overflow: 'hidden',
                                border: img.isSplit ? '2px solid rgba(255, 0, 255, 0.5)' : '2px solid rgba(0, 255, 255, 0.3)',
                            }}>
                                <img src={img.processedSrc} alt="" style={{ width: '100%', height: '200px', objectFit: 'cover' }} />
                                <div style={{ padding: '10px', fontSize: '12px', color: '#888' }}>
                                    {img.newWidth}√ó{img.newHeight} ‚Ä¢ Enhanced
                                    {img.isSplit && (
                                        <span style={{
                                            marginLeft: '10px',
                                            color: '#ff00ff',
                                            fontWeight: 'bold'
                                        }}>
                                            ‚úÇÔ∏è Split {img.splitIndex}
                                        </span>
                                    )}
                                </div>
                            </div>
                        ))}
                    </div>

                    {/* Start Over */}
                    <button
                        onClick={() => {
                            setResults(null);
                            setImages([]);
                            setUnwantedExamples([]);
                        }}
                        style={{
                            marginTop: '30px',
                            padding: '12px 24px',
                            background: 'rgba(255, 255, 255, 0.1)',
                            border: '1px solid rgba(255, 255, 255, 0.2)',
                            borderRadius: '8px',
                            color: '#fff',
                            cursor: 'pointer',
                        }}
                    >
                        üîÑ Process Another Batch
                    </button>
                </div>
            )}
        </div>
    );
}

/* eslint-disable react-hooks/exhaustive-deps */
import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import './SocialFeed.css';
import { checkTierAccess } from '../utils/tierAccess';
import api from '../utils/backendApi';
import featureDetector from '../utils/featureDetection';
import { FeatureBlocker } from './FeatureDisabledBanner';

/**
 * Social Feed - Main content feed for all users
 * 
 * Tier Structure:
 * - Owner + VIPs: FREE everything, admin powers, all features
 * - $1000: Admin powers, all features, pay creator fees
 * - $500: All features except admin, pay creator fees
 * - $250: No VR/AR, pay creator fees
 * - $100: Basic features
 * - $50: Minimal features
 * - $15+$5: Adult content only
 * - FREE: Family friendly only
 */
export const SocialFeed = ({ userId, userTier }) => {
  const [posts, setPosts] = useState([]);
  const [newPostContent, setNewPostContent] = useState('');
  const [activeTab, setActiveTab] = useState('feed'); // feed, discover, search
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [discoverCreators] = useState([]);
  const [showCGITools, setShowCGITools] = useState(false);
  const [showMonetizeDialog, setShowMonetizeDialog] = useState(false);
  const [friends, setFriends] = useState([]);
  const [followers, setFollowers] = useState([]);
  const [subscriptions, setSubscriptions] = useState([]);
  const [contentVisibility, setContentVisibility] = useState('PUBLIC'); // PUBLIC, FRIENDS, SUBSCRIBERS, CUSTOM
  const [isPaidContent, setIsPaidContent] = useState(false);
  const [priceCents, setPriceCents] = useState(500); // Default $5.00
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showCommentsForPost, setShowCommentsForPost] = useState(null);
  const [commentsMap, setCommentsMap] = useState({});
  const [newCommentText, setNewCommentText] = useState('');
  const [features, setFeatures] = useState(featureDetector.getFeatures());
  const [hasMore, setHasMore] = useState(true);
  const [offset, setOffset] = useState(0);
  const [likedPosts, setLikedPosts] = useState(new Set());
  const [savedPosts, setSavedPosts] = useState(new Set());
  const [friendSuggestions, setFriendSuggestions] = useState([
    { id: 1, name: 'AnimeArtist99', avatar: 'üé®', mutualFriends: 5 },
    { id: 2, name: 'CosplayQueen', avatar: 'üëó', mutualFriends: 3 },
    { id: 3, name: 'GameDevPro', avatar: 'üéÆ', mutualFriends: 7 },
    { id: 4, name: 'MangaLover', avatar: 'üìö', mutualFriends: 2 }
  ]);

  // Get user's tier access
  const userEmail = localStorage.getItem('ownerEmail') || localStorage.getItem('userEmail');
  const access = checkTierAccess(userId, userTier, userEmail);

  // Subscribe to feature changes
  useEffect(() => {
    const unsubscribe = featureDetector.subscribe(setFeatures);
    featureDetector.checkFeatures();
    return unsubscribe;
  }, []);

  useEffect(() => {
    const loadFeed = async () => {
      try {
        setLoading(true);
        
        // Load posts from backend API with pagination
        const apiUrl = import.meta.env.VITE_API_URL || globalThis.location.origin;
        const response = await fetch(`${apiUrl}/api/social/feed?limit=20&offset=0`);
        if (response.ok) {
          const feedData = await response.json();
          setPosts(feedData.posts || []);
          setHasMore(feedData.posts?.length >= 20);
        } else {
          setPosts([]);
          setHasMore(false);
        }

        setFriends([]);
        setFollowers([]);
        setSubscriptions([]);
        setError(null);
      } catch (err) {
        console.error('Feed load error:', err);
        setPosts([]);
        setError(null);
      } finally {
        setLoading(false);
      }
    };

    loadFeed();
  }, []);

  // Infinite scroll handler
  const loadMorePosts = async () => {
    if (loading || !hasMore) return;
    
    try {
      setLoading(true);
      const newOffset = offset + 20;
      const apiUrl = import.meta.env.VITE_API_URL || globalThis.location.origin;
      const response = await fetch(`${apiUrl}/api/social/feed?limit=20&offset=${newOffset}`);
      
      if (response.ok) {
        const feedData = await response.json();
        const newPosts = feedData.posts || [];
        setPosts([...posts, ...newPosts]);
        setOffset(newOffset);
        setHasMore(newPosts.length >= 20);
      }
    } catch (err) {
      console.error('Load more error:', err);
    } finally {
      setLoading(false);
    }
  };

  // Scroll detection for infinite scroll
  useEffect(() => {
    const handleScroll = () => {
      if (activeTab !== 'feed') return;
      
      const scrollTop = window.pageYOffset || document.documentElement.scrollTop;
      const scrollHeight = document.documentElement.scrollHeight;
      const clientHeight = document.documentElement.clientHeight;
      
      if (scrollTop + clientHeight >= scrollHeight - 500 && !loading && hasMore) {
        loadMorePosts();
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [activeTab, loading, hasMore, offset, posts]);

  const createPost = async () => {
    if (!newPostContent.trim()) return;

    try {
      setLoading(true);
      setError(null);

      // Get user ID from localStorage or generate real UUID
      let currentUserId = localStorage.getItem('userId');
      if (!currentUserId) {
        // Generate a real UUID v4
        currentUserId = crypto.randomUUID();
        localStorage.setItem('userId', currentUserId);
      }
      const userName = localStorage.getItem('currentUserName') || localStorage.getItem('displayName') || 'Anonymous';
      const userAvatar = localStorage.getItem('userAvatar') || 'üë§';

      // Call the correct API endpoint
      const apiUrl = import.meta.env.VITE_API_URL || globalThis.location.origin;
      const response = await fetch(`${apiUrl}/api/social/post`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          userId: currentUserId,
          content: newPostContent,
          visibility: contentVisibility.toUpperCase(),
          mediaUrl: null
        })
      });

      if (!response.ok) {
        throw new Error('Failed to create post');
      }

      const data = await response.json();

      // Format post for display
      const newPost = {
        id: data.post.id,
        userId: data.post.userId,
        userName: userName,
        avatar: userAvatar,
        content: data.post.content,
        visibility: data.post.visibility,
        timestamp: data.post.timestamp,
        likes: data.post.likes || 0,
        commentsCount: data.post.commentsCount || 0,
        shares: data.post.shares || 0,
        isPaidContent: isPaidContent,
        priceCents: isPaidContent ? priceCents : 0
      };

      setPosts([newPost, ...posts]);
      setNewPostContent('');
      setShowMonetizeDialog(false);
      setIsPaidContent(false);
      setPriceCents(500);
    } catch (err) {
      console.error('Failed to create post:', err);
      setError('‚ùå Failed to create post: ' + err.message);

      // Still show the post even if API fails
      const newPost = {
        id: Date.now(),
        userId: localStorage.getItem('userId') || `user_${Date.now()}`,
        userName: localStorage.getItem('currentUserName') || 'Anonymous',
        avatar: localStorage.getItem('userAvatar') || 'üë§',
        content: newPostContent,
        visibility: contentVisibility.toLowerCase(),
        timestamp: new Date().toISOString(),
        likes: 0,
        commentsCount: 0,
        shares: 0,
        isPaidContent: isPaidContent,
        priceCents: isPaidContent ? priceCents : 0
      };

      setPosts([newPost, ...posts]);
      setNewPostContent('');
      setShowMonetizeDialog(false);
    } finally {
      setLoading(false);
    }
  };

  const toggleComments = async (postId) => {
    if (showCommentsForPost === postId) {
      // Close comments
      setShowCommentsForPost(null);
    } else {
      // Open comments and load them
      setShowCommentsForPost(postId);
      if (!commentsMap[postId]) {
        try {
          const commentsData = await api.comments.getComments(postId, 50, 0);
          setCommentsMap({ ...commentsMap, [postId]: commentsData.comments || [] });
        } catch (err) {
          console.error('Failed to load comments:', err);
          setCommentsMap({ ...commentsMap, [postId]: [] });
        }
      }
    }
  };

  const handleAddComment = async (postId) => {
    if (!newCommentText.trim()) return;

    try {
      const comment = await api.comments.create(postId, newCommentText);

      // Add to comments map
      const currentComments = commentsMap[postId] || [];
      setCommentsMap({ ...commentsMap, [postId]: [comment, ...currentComments] });

      // Update post comments count
      setPosts(posts.map(p =>
        p.id === postId ? { ...p, commentsCount: (p.commentsCount || 0) + 1 } : p
      ));

      setNewCommentText('');
    } catch (err) {
      console.error('Failed to add comment:', err);
      setError('Failed to add comment');
    }
  };

  const likePost = async (postId) => {
    try {
      // Optimistic update
      const wasLiked = likedPosts.has(postId);
      const updatedPosts = posts.map(post => 
        post.id === postId ? { 
          ...post, 
          likes: wasLiked ? (post.likes || 0) - 1 : (post.likes || 0) + 1,
          isLiked: !wasLiked
        } : post
      );
      setPosts(updatedPosts);
      
      // Update liked state
      const newLiked = new Set(likedPosts);
      if (wasLiked) {
        newLiked.delete(postId);
      } else {
        newLiked.add(postId);
      }
      setLikedPosts(newLiked);
      localStorage.setItem('likedPosts', JSON.stringify([...newLiked]));

      // Send to backend
      const apiUrl = import.meta.env.VITE_API_URL || globalThis.location.origin;
      await fetch(`${apiUrl}/api/social/post/${postId}/like`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' }
      });
    } catch (err) {
      console.error('Like error:', err);
    }
  };

  const sharePost = async (postId) => {
    try {
      const post = posts.find(p => p.id === postId);
      if (navigator.share) {
        await navigator.share({
          title: `Post by @${post.author?.username || 'creator'}`,
          text: post.content?.substring(0, 100),
          url: `${globalThis.location.origin}/post/${postId}`
        });
      } else {
        // Fallback: copy link
        await navigator.clipboard.writeText(`${globalThis.location.origin}/post/${postId}`);
        alert('‚úÖ Link copied to clipboard!');
      }
      
      // Track share
      const updatedPosts = posts.map(p => 
        p.id === postId ? { ...p, shares: (p.shares || 0) + 1 } : p
      );
      setPosts(updatedPosts);
      
      const apiUrl = import.meta.env.VITE_API_URL || globalThis.location.origin;
      await fetch(`${apiUrl}/api/social/post/${postId}/share`, {
        method: 'POST'
      });
    } catch (err) {
      console.error('Share error:', err);
    }
  };

  const savePost = (postId) => {
    const wasSaved = savedPosts.has(postId);
    const newSaved = new Set(savedPosts);

    if (wasSaved) {
      newSaved.delete(postId);
      alert('üóëÔ∏è Removed from saved');
    } else {
      newSaved.add(postId);
      alert('üíæ Post saved!');
    }

    setSavedPosts(newSaved);
    localStorage.setItem('savedPosts', JSON.stringify([...newSaved]));
  };

  // Social interaction functions
  const followUser = async (targetUserId, targetUsername) => {
    try {
      const apiUrl = import.meta.env.VITE_API_URL || globalThis.location.origin;
      await fetch(`${apiUrl}/api/social/follow/${targetUserId}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId: localStorage.getItem('userId') || userId })
      });
      alert(`‚úÖ Now following @${targetUsername}!`);
    } catch (err) {
      console.error('Follow error:', err);
      alert('‚ö†Ô∏è Follow action recorded locally');
    }
  };

  const addFriend = async (targetUserId, targetUsername) => {
    try {
      const apiUrl = import.meta.env.VITE_API_URL || globalThis.location.origin;
      await fetch(`${apiUrl}/api/social/friend/${targetUserId}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId: localStorage.getItem('userId') || userId })
      });
      alert(`‚úÖ Friend request sent to @${targetUsername}!`);
    } catch (err) {
      console.error('Add friend error:', err);
      alert('‚ö†Ô∏è Friend request recorded locally');
    }
  };

  const subscribeToUser = async (targetUserId, targetUsername) => {
    try {
      const apiUrl = import.meta.env.VITE_API_URL || globalThis.location.origin;
      await fetch(`${apiUrl}/api/social/subscribe/${targetUserId}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId: localStorage.getItem('userId') || userId })
      });
      alert(`‚úÖ Subscribed to @${targetUsername}!`);
    } catch (err) {
      console.error('Subscribe error:', err);
      alert('‚ö†Ô∏è Subscription recorded locally');
    }
  };

  const deletePost = async (postId) => {
    if (!confirm('Are you sure you want to delete this post?')) return;
    
    try {
      const apiUrl = import.meta.env.VITE_API_URL || globalThis.location.origin;
      const response = await fetch(`${apiUrl}/api/social/post/${postId}`, {
        method: 'DELETE',
        headers: { 'Content-Type': 'application/json' }
      });
      
      if (response.ok) {
        setPosts(posts.filter(p => p.id !== postId));
        alert('‚úÖ Post deleted!');
      } else {
        alert('‚ùå Failed to delete post');
      }
    } catch (err) {
      console.error('Delete error:', err);
      // Remove from UI anyway
      setPosts(posts.filter(p => p.id !== postId));
      alert('‚ö†Ô∏è Post removed from view');
    }
  };

  const [editingPostId, setEditingPostId] = useState(null);
  const [editContent, setEditContent] = useState('');

  const startEditPost = (postId, currentContent) => {
    setEditingPostId(postId);
    setEditContent(currentContent);
  };

  const cancelEdit = () => {
    setEditingPostId(null);
    setEditContent('');
  };

  const saveEdit = async (postId) => {
    if (!editContent.trim()) return;
    
    try {
      const apiUrl = import.meta.env.VITE_API_URL || globalThis.location.origin;
      const response = await fetch(`${apiUrl}/api/social/post/${postId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ content: editContent })
      });
      
      if (response.ok) {
        setPosts(posts.map(p => p.id === postId ? { ...p, content: editContent } : p));
        setEditingPostId(null);
        setEditContent('');
        alert('‚úÖ Post updated!');
      } else {
        alert('‚ùå Failed to update post');
      }
    } catch (err) {
      console.error('Edit error:', err);
      // Update UI anyway
      setPosts(posts.map(p => p.id === postId ? { ...p, content: editContent } : p));
      setEditingPostId(null);
      setEditContent('');
      alert('‚ö†Ô∏è Post updated locally');
    }
  };

  return (
    <div className="social-feed-container">
      {/* Left Sidebar - Quick Actions */}
      <div className="feed-sidebar-left">
        <div className="sidebar-section">
          <button className="sidebar-item" onClick={() => setActiveTab('feed')}>
            <span className="sidebar-icon">üè†</span>
            <span className="sidebar-text">Home</span>
          </button>
          <button className="sidebar-item" onClick={() => setActiveTab('discover')}>
            <span className="sidebar-icon">üîç</span>
            <span className="sidebar-text">Discover Creators</span>
          </button>
          <button className="sidebar-item" onClick={() => setActiveTab('friends')}>
            <span className="sidebar-icon">üë•</span>
            <span className="sidebar-text">Find Friends</span>
          </button>
          <button className="sidebar-item" onClick={() => setActiveTab('messages')}>
            <span className="sidebar-icon">üí¨</span>
            <span className="sidebar-text">Messages</span>
          </button>
          <button className="sidebar-item" onClick={() => setActiveTab('subscriptions')}>
            <span className="sidebar-icon">üíé</span>
            <span className="sidebar-text">My Subscriptions</span>
          </button>
          <button className="sidebar-item" onClick={() => setActiveTab('saved')}>
            <span className="sidebar-icon">üìï</span>
            <span className="sidebar-text">Saved</span>
          </button>
        </div>
      </div>

      {/* Main Feed Area */}
      <div className="feed-main">
        {/* Navigation Tabs */}
        <div className="feed-nav">
          <button 
            className={`feed-nav-btn ${activeTab === 'feed' ? 'active' : ''}`}
            onClick={() => setActiveTab('feed')}
          >
            üè† Feed
          </button>
          <button 
            className={`feed-nav-btn ${activeTab === 'discover' ? 'active' : ''}`}
            onClick={() => setActiveTab('discover')}
          >
            üîç Discover
          </button>
          <button 
            className={`feed-nav-btn ${activeTab === 'friends' ? 'active' : ''}`}
            onClick={() => setActiveTab('friends')}
          >
            üë• Friends
          </button>
        </div>

      {/* Feed Tab */}
      {activeTab === 'feed' && (
        <FeatureBlocker feature="socialMedia" features={features}>
        <div className="feed-content">
          {/* Post Creator */}
          <div className="post-creator">
            <textarea
              placeholder="What's on your mind?"
              value={newPostContent}
              onChange={(e) => setNewPostContent(e.target.value)}
              rows={3}
              disabled={loading}
            />
            
            {/* Content Visibility Selector */}
            <div className="visibility-selector">
              <label htmlFor="visibility-select">üëÅÔ∏è Who can see this:</label>
              <select id="visibility-select" value={contentVisibility} onChange={(e) => setContentVisibility(e.target.value)}>
                <option value="PUBLIC">üåç Public (Everyone)</option>
                <option value="FRIENDS">üë• Friends Only</option>
                <option value="SUBSCRIBERS">üíé Subscribers Only</option>
                <option value="CUSTOM">‚öôÔ∏è Custom List</option>
              </select>
            </div>

            {error && <div className="error-message">{error}</div>}

            <div className="post-creator-actions">
              <div className="post-options">
                <button className="post-option">üì∑ Photo</button>
                <button className="post-option">üé• Video</button>
                {access.hasCGI.basic && (
                  <button 
                    className="post-option premium"
                    onClick={() => setShowCGITools(!showCGITools)}
                  >
                    ‚ú® CGI Effects
                  </button>
                )}
              </div>
              <button 
                className="post-btn" 
                onClick={() => {
                  // Show monetization dialog before posting
                  if (newPostContent.trim()) {
                    setShowMonetizeDialog(true);
                  } else {
                    createPost();
                  }
                }}
              >
                Post
              </button>
            </div>

            {/* Monetization Dialog - shown BEFORE posting */}
            {showMonetizeDialog && (
              <div className="monetize-dialog">
                <div className="monetize-dialog-content">
                  <h3>Ready to share?</h3>
                  <p>Choose how you want to share this post:</p>
                  
                  <label className="monetize-option" aria-label="Post to feed for free">
                    <input
                      type="radio"
                      name="monetize"
                      checked={!isPaidContent}
                      onChange={() => setIsPaidContent(false)}
                    />
                    <div>
                      <strong>üåü Post to Feed</strong>
                      <small>Share with your audience for free</small>
                    </div>
                  </label>

                  <label className="monetize-option" aria-label="Monetized content">
                    <input
                      type="radio"
                      name="monetize"
                      checked={isPaidContent}
                      onChange={() => setIsPaidContent(true)}
                    />
                    <div>
                      <strong>üí∞ Monetized Content</strong>
                      <small>Charge subscribers to view (VIPs see for free)</small>
                    </div>
                  </label>

                  {isPaidContent && (
                    <div className="price-input">
                      <label htmlFor="price-input">Set Price:</label>
                      <input
                        id="price-input"
                        type="number"
                        value={priceCents / 100}
                        onChange={(e) => setPriceCents(Math.round(Number.parseFloat(e.target.value) * 100))}
                        min="1"
                        step="0.01"
                        placeholder="0.00"
                      />
                    </div>
                  )}

                  <div className="monetize-actions">
                    <button onClick={() => setShowMonetizeDialog(false)}>Cancel</button>
                    <button className="primary" onClick={() => { setShowMonetizeDialog(false); createPost(); }}>
                      {isPaidContent ? `Post for $${(priceCents / 100).toFixed(2)}` : 'Post Now'}
                    </button>
                  </div>
                </div>
              </div>
            )}

            {showCGITools && access.hasCGI.basic && (
              <div className="cgi-tools-panel">
                <h4>üé® CGI Tools & Effects</h4>
                <div className="cgi-categories">
                  <div className="effect-category">
                    <h5>üåà Background Effects</h5>
                    <div className="cgi-options">
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [Effect: Starry Night Sky]')}>‚≠ê Starry Night</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [Effect: Cherry Blossom]')}>üå∏ Cherry Blossom</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [Effect: Neon City]')}>üåÉ Neon City</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [Effect: Beach Sunset]')}>üèñÔ∏è Beach Sunset</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [Effect: Northern Lights]')}>‚ú® Northern Lights</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [Effect: Space Galaxy]')}>üåå Space Galaxy</button>
                    </div>
                  </div>
                  
                  <div className="effect-category">
                    <h5>‚ú® Filters & Color Grading</h5>
                    <div className="cgi-options">
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [Filter: Anime Style]')}>üéå Anime Style</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [Filter: Vintage Film]')}>üìΩÔ∏è Vintage Film</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [Filter: Cyberpunk Neon]')}>‚ö° Cyberpunk</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [Filter: Soft Glow]')}>üí´ Soft Glow</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [Filter: High Contrast]')}>üéØ High Contrast</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [Filter: Black & White]')}>‚ö™ B&W</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [Filter: Sepia Tone]')}>üü´ Sepia</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [Filter: Retro Waves]')}>üåä Retro Waves</button>
                    </div>
                  </div>

                  <div className="effect-category">
                    <h5>üëª AR Stickers & Elements</h5>
                    <div className="cgi-options">
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [AR: Cute Cat Ears]')}>üê± Cat Ears</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [AR: Sparkles]')}>‚ú® Sparkles</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [AR: Hearts]')}>üíï Hearts</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [AR: Stars]')}>‚≠ê Stars</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [AR: Butterfly Wings]')}>ü¶ã Butterfly Wings</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [AR: Angel Halo]')}>üòá Angel Halo</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [AR: Devil Horns]')}>üòà Devil Horns</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [AR: Crown]')}>üëë Crown</button>
                    </div>
                  </div>

                  <div className="effect-category">
                    <h5>üé≠ Face Filters</h5>
                    <div className="cgi-options">
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [Face: Beauty Filter]')}>üíÖ Beauty Filter</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [Face: Big Eyes]')}>üëÅÔ∏è Big Eyes</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [Face: Smooth Skin]')}>‚ú® Smooth Skin</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [Face: Rosy Cheeks]')}>üåπ Rosy Cheeks</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [Face: Glitter Makeup]')}>üíé Glitter Makeup</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [Face: Fantasy Makeup]')}>üé® Fantasy Makeup</button>
                    </div>
                  </div>

                  <div className="effect-category">
                    <h5>üåü Special Effects</h5>
                    <div className="cgi-options">
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [Effect: Light Rays]')}>‚òÄÔ∏è Light Rays</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [Effect: Particle Burst]')}>üí• Particle Burst</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [Effect: Motion Blur]')}>üí® Motion Blur</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [Effect: Hologram]')}>üî∑ Hologram</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [Effect: Glitch]')}>üì∫ Glitch</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [Effect: Bokeh]')}>üîÜ Bokeh</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [Effect: Lens Flare]')}>üåü Lens Flare</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [Effect: Rain]')}>üåßÔ∏è Rain</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [Effect: Snow]')}>‚ùÑÔ∏è Snow</button>
                      <button className="effect-btn" onClick={() => setNewPostContent(newPostContent + ' [Effect: Fire]')}>üî• Fire</button>
                    </div>
                  </div>

                  {access.hasCGI.full && (
                    <div className="effect-category premium-category">
                      <h5>üíé Premium CGI (Your Tier Only)</h5>
                      <div className="cgi-options">
                        <button className="effect-btn premium" onClick={() => setNewPostContent(newPostContent + ' [Premium: 3D Model Import]')}>üé≤ 3D Model Import</button>
                        <button className="effect-btn premium" onClick={() => setNewPostContent(newPostContent + ' [Premium: Custom Shader]')}>‚ö° Custom Shader</button>
                        <button className="effect-btn premium" onClick={() => setNewPostContent(newPostContent + ' [Premium: AI Enhancement]')}>ü§ñ AI Enhancement</button>
                        <button className="effect-btn premium" onClick={() => setNewPostContent(newPostContent + ' [Premium: Real-time Tracking]')}>üìç Real-time Tracking</button>
                        <button className="effect-btn premium" onClick={() => setNewPostContent(newPostContent + ' [Premium: Scene Composition]')}>üé¨ Scene Composition</button>
                      </div>
                    </div>
                  )}
                </div>
              </div>
            )}
          </div>

          {/* Posts Feed */}
          <div className="posts-feed">
            {posts.length === 0 && (
              <div className="empty-feed">
                <h3>Welcome to ForTheWeebs! üéå</h3>
                <p>Be the first to post something awesome</p>
              </div>
            )}
            {posts.map((post, index) => {
              // Check if user can view this paid content
              const canViewPaidContent = !post.isPaidContent || 
                                        access.hasFreeContentAccess || 
                                        post.userId === userId ||
                                        subscriptions.some(sub => sub.creatorId === post.userId);
              
              // Insert creator recommendation card every 4 posts (OnlyFans-style blending)
              const shouldShowCreatorCard = (index + 1) % 4 === 0 && index < posts.length - 1;
              const creatorToShow = discoverCreators[Math.floor(index / 4) % discoverCreators.length];
              
              // Insert friend suggestion card every 6 posts
              const shouldShowFriendCard = (index + 1) % 6 === 0 && index < posts.length - 1 && friendSuggestions.length > 0;
              const friendToShow = friendSuggestions[Math.floor(index / 6) % friendSuggestions.length];
              
              return (
              <React.Fragment key={post.id}>
              <div className="post-card">
                <div className="post-header">
                  <span className="post-avatar">{post.avatar}</span>
                  <div className="post-meta">
                    <strong>{post.userName}</strong>
                    <span className="post-time">
                      {new Date(post.timestamp).toLocaleString()}
                    </span>
                    {post.visibility !== 'public' && (
                      <span className="visibility-badge">
                        {post.visibility === 'friends' && 'üë• Friends'}
                        {post.visibility === 'subscribers' && 'üíé Subscribers'}
                        {post.visibility === 'custom' && '‚öôÔ∏è Custom'}
                      </span>
                    )}
                    {post.isPaidContent && (
                      <span className="paid-badge">
                        üí∞ ${(post.priceCents / 100).toFixed(2)}
                      </span>
                    )}
                  </div>
                  <div className="post-actions-menu">
                    <button className="follow-btn" onClick={() => followUser(post.userId, post.userName)}>
                      ‚ûï Follow
                    </button>
                    <button className="friend-btn" onClick={() => addFriend(post.userId, post.userName)}>
                      üë• Add Friend
                    </button>
                    {post.isPaidContent && !canViewPaidContent && (
                      <button className="subscribe-btn" onClick={() => subscribeToUser(post.userId, post.userName)}>
                        üíé Subscribe for ${(post.priceCents / 100).toFixed(2)}
                      </button>
                    )}
                  </div>
                </div>
                
                {/* Show content if user has access, otherwise show lock */}
                {(() => {
                  if (!canViewPaidContent) {
                    return (
                      <div className="post-content locked">
                        <div className="locked-overlay">
                          üîí 
                          <p>Subscribe to view this content</p>
                          <button onClick={() => subscribeToUser(post.userId, post.userName)}>
                            Subscribe for ${(post.priceCents / 100).toFixed(2)}
                          </button>
                        </div>
                      </div>
                    );
                  }
                  
                  if (editingPostId === post.id) {
                    return (
                      <div className="post-content editing">
                        <textarea
                          value={editContent}
                          onChange={(e) => setEditContent(e.target.value)}
                          className="edit-textarea"
                        />
                        <div className="edit-actions">
                          <button className="cancel-edit-btn" onClick={cancelEdit}>Cancel</button>
                          <button className="save-edit-btn" onClick={() => saveEdit(post.id)}>Save</button>
                        </div>
                      </div>
                    );
                  }
                  
                  return <div className="post-content">{post.content}</div>;
                })()}
                
                <div className="post-actions">
                  <button 
                    onClick={() => likePost(post.id)}
                    className={`action-btn like-btn ${likedPosts.has(post.id) ? 'liked' : ''}`}
                  >
                    {likedPosts.has(post.id) ? '‚ù§Ô∏è' : 'ü§ç'} {post.likes || 0}
                  </button>
                  <button onClick={() => toggleComments(post.id)} className="action-btn comment-btn">
                    üí¨ {post.commentsCount || 0}
                  </button>
                  <button onClick={() => sharePost(post.id)} className="action-btn share-btn">
                    üîÅ {post.shares || 0}
                  </button>
                  <button 
                    onClick={() => savePost(post.id)}
                    className={`action-btn save-btn ${savedPosts.has(post.id) ? 'saved' : ''}`}
                  >
                    {savedPosts.has(post.id) ? 'üìï' : 'üìñ'} Save
                  </button>
                  {post.userId === (localStorage.getItem('userId') || userId) && (
                    <>
                      <button onClick={() => startEditPost(post.id, post.content)} className="action-btn edit-btn">
                        ‚úèÔ∏è Edit
                      </button>
                      <button onClick={() => deletePost(post.id)} className="action-btn delete-btn">
                        üóëÔ∏è Delete
                      </button>
                    </>
                  )}
                </div>

                {/* Comments Section */}
                {showCommentsForPost === post.id && (
                  <div className="comments-section">
                    <div className="add-comment">
                      <input
                        type="text"
                        value={newCommentText}
                        onChange={(e) => setNewCommentText(e.target.value)}
                        placeholder="Write a comment..."
                        onKeyPress={(e) => e.key === 'Enter' && handleAddComment(post.id)}
                      />
                      <button onClick={() => handleAddComment(post.id)}>Post</button>
                    </div>
                    <div className="comments-list">
                      {(commentsMap[post.id] || []).map(comment => (
                        <div key={comment.id} className="comment">
