        </div>

        {/* Premium Upsell */}
        {!access.hasCGI.full && (
          <div className="sidebar-widget premium-widget">
            <h3>üíé Unlock Premium</h3>
            <p>Get CGI tools, video effects, and more!</p>
            <button className="premium-upgrade-btn">Upgrade Now</button>
          </div>
        )}
