export const TermsOfService = ({ onAccept }) => {
  const [accepted, setAccepted] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleCheckboxChange = (e) => {
    e.stopPropagation();
    setAccepted(e.target.checked);
  };

  const handleCheckboxClick = (e) => {
    e.stopPropagation();
  };

  const handleButtonClick = async (e) => {
    e.stopPropagation();
    if (!accepted || isSubmitting) return;
    
    setIsSubmitting(true);
    
    try {
      // Get current user
      const { data: { user } } = await supabase.auth.getUser();
      
      if (!user) {
        console.error('No user found');
        setIsSubmitting(false);
        return;
      }

      // Get user's IP address
      const ipAddress = await fetch('https://api.ipify.org?format=json')
        .then(r => r.json())
        .then(data => data.ip)
        .catch(() => 'unknown');

      // Call legal receipts API
      const response = await fetch(`${import.meta.env.VITE_API_URL}/api/legal-receipts`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userId: user.id,
          email: user.email,
          termsContent: TERMS_TEXT,
          privacyContent: PRIVACY_POLICY_TEXT,
          ipAddress
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to create legal receipt');
      }

      const receipt = await response.json();
      console.log('‚úÖ Legal receipt created:', receipt.receiptId);
      
      onAccept();
    } catch (error) {
      console.error('Error creating legal receipt:', error);
      alert('Failed to process acceptance. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
  <div className="terms-container">
      <h2>Terms of Service</h2>
      <div className="terms-content">
        <pre className="terms-pre">{TERMS_TEXT}</pre>
      </div>
  <label 
        className="terms-label"
        onClick={handleCheckboxClick}
      >
        <input 
          type="checkbox" 
          checked={accepted} 
          onChange={handleCheckboxChange}
          onClick={handleCheckboxClick}
        />
        I accept the Terms of Service
      </label>
      <button
        disabled={!accepted || isSubmitting}
        className={`terms-button${(!accepted || isSubmitting) ? " disabled" : ""}`}
        onClick={handleButtonClick}
      >
        {isSubmitting ? 'Processing...' : 'Continue'}
      </button>
    </div>
  );
};

import React, { createContext, useContext, useEffect, useState } from 'react';
import './ThemeToggle.css';

const ThemeContext = createContext();

export function ThemeProvider({ children }) {
  const [theme, setTheme] = useState(() => {
    return localStorage.getItem('theme') || 'dark';
  });

  useEffect(() => {
    document.documentElement.setAttribute('data-theme', theme);
    localStorage.setItem('theme', theme);
  }, [theme]);

  const toggleTheme = () => {
    setTheme(prev => prev === 'dark' ? 'light' : 'dark');
  };

  return (
    <ThemeContext.Provider value={{ theme, toggleTheme }}>
      {children}
    </ThemeContext.Provider>
  );
}

export function useTheme() {
  const context = useContext(ThemeContext);
  if (!context) {
    throw new Error('useTheme must be used within ThemeProvider');
  }
  return context;
}

export function ThemeToggle() {
  const { theme, toggleTheme } = useTheme();

  return (
    <button
      className="theme-toggle"
      onClick={toggleTheme}
      aria-label="Toggle theme"
      title={`Switch to ${theme === 'dark' ? 'light' : 'dark'} mode`}
    >
      {theme === 'dark' ? '‚òÄÔ∏è' : 'üåô'}
    </button>
  );
}

