      useCase: 'E-commerce'
    },
    { 
      id: 'furniture', 
      name: 'üõãÔ∏è Furniture Placement', 
      description: 'Try before you buy - furniture edition',
      useCase: 'Interior Design'
    },
    { 
      id: 'character', 
      name: 'üé≠ Character Showcase', 
