                <button>üåô Night</button>
                <button>üèôÔ∏è City</button>
                <button>üåÖ Sunset</button>
              </div>
            </div>

            {/* Center - 3D Viewport */}
            <div className="center-viewport">
              <div className="viewport-canvas">
                <div className="canvas-placeholder">
                  <div className="grid-overlay"></div>
                  <p>3D Viewport</p>
                  <p className="hint">
                    {(() => {
                      if (objects.length === 0) return 'Add objects from the left panel';
                      const plural = objects.length > 1 ? 's' : '';
                      return `${objects.length} object${plural} in scene`;
                    })()}
                  </p>
                  
                  {/* Object List */}
                  {objects.length > 0 && (
                    <div className="scene-objects">
                      {objects.map(obj => (
                        <div 
                          key={obj.id}
                          className={`object-item ${selectedObject === obj.id ? 'selected' : ''}`}
                          onClick={() => setSelectedObject(obj.id)}
                          onKeyDown={(e) => e.key === 'Enter' && setSelectedObject(obj.id)}
                          role="button"
                          tabIndex={0}
                        >
                          {obj.name}
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              </div>

              <div className="viewport-controls">
                <select className="camera-view">
                  <option>Perspective</option>
                  <option>Top View</option>
                  <option>Front View</option>
                  <option>Side View</option>
                </select>
              </div>
            </div>

            {/* Right Sidebar - Properties */}
            <div className="right-sidebar">
              <h3>‚öôÔ∏è Properties</h3>
              
              {selectedObject ? (
                <div className="properties-panel">
                  {objects
                    .filter(obj => obj.id === selectedObject)
                    .map(obj => (
                      <div key={obj.id} className="property-groups">
                        <div className="property-group">
                          <label htmlFor={`object-name-${obj.id}`}>Name</label>
                          <input
                            id={`object-name-${obj.id}`}
                            type="text"
                            value={obj.name}
                            onChange={(e) => updateObject(obj.id, { name: e.target.value })}
                          />
                        </div>

                        <div className="property-group">
                          <label htmlFor={`object-position-${obj.id}`}>Position</label>
                          <div className="vector-inputs">
                            <input
                              id={`object-position-${obj.id}`}
                              type="number"
                              value={obj.position.x}
                              onChange={(e) => updateObject(obj.id, { 
                                position: { ...obj.position, x: Number.parseFloat(e.target.value) }
                              })}
                              placeholder="X"
                            />
                            <input
                              type="number"
                              value={obj.position.y}
                              onChange={(e) => updateObject(obj.id, { 
                                position: { ...obj.position, y: Number.parseFloat(e.target.value) }
                              })}
                              placeholder="Y"
                            />
                            <input
                              type="number"
                              value={obj.position.z}
                              onChange={(e) => updateObject(obj.id, { 
                                position: { ...obj.position, z: Number.parseFloat(e.target.value) }
                              })}
                              placeholder="Z"
                            />
                          </div>
                        </div>

                        <div className="property-group">
                          <label htmlFor={`object-scale-${obj.id}`}>Scale</label>
                          <div className="vector-inputs">
                            <input
                              id={`object-scale-${obj.id}`}
                              type="number"
                              value={obj.scale.x}
                              onChange={(e) => updateObject(obj.id, { 
                                scale: { ...obj.scale, x: Number.parseFloat(e.target.value) }
                              })}
                              step="0.1"
                              placeholder="X"
                            />
                            <input
                              type="number"
                              value={obj.scale.y}
                              onChange={(e) => updateObject(obj.id, { 
                                scale: { ...obj.scale, y: Number.parseFloat(e.target.value) }
                              })}
                              step="0.1"
                              placeholder="Y"
                            />
                            <input
                              type="number"
                              value={obj.scale.z}
                              onChange={(e) => updateObject(obj.id, { 
                                scale: { ...obj.scale, z: Number.parseFloat(e.target.value) }
                              })}
                              step="0.1"
                              placeholder="Z"
                            />
                          </div>
                        </div>

                        <div className="property-group">
                          <label htmlFor={`object-color-${obj.id}`}>Color</label>
                          <input
                            id={`object-color-${obj.id}`}
                            type="color"
                            value={obj.color}
                            onChange={(e) => updateObject(obj.id, { color: e.target.value })}
                          />
                        </div>

                        <button onClick={deleteObject} className="btn-delete">
                          üóëÔ∏è Delete Object
                        </button>
                      </div>
                    ))}
                </div>
              ) : (
                <div className="no-selection">
                  <p>Select an object to edit properties</p>
                </div>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

import React, { useState, useRef } from 'react';
import { saveFileWithDialog, FILE_TYPES } from '../utils/fileSaveDialog';

/**
 * VRRecordingStudio - Record and stream VR/AR content
 * 
 * Features:
 * - Screen capture from VR headset
 * - Stream to Twitch/YouTube
 * - 360¬∞ video export
 * - Recording controls accessible in VR
 * - Multi-camera angles
 * - Audio mixing with commentary
 */
export default function VRRecordingStudio() {
  const [isRecording, setIsRecording] = useState(false);
  const [isStreaming, setIsStreaming] = useState(false);
  const [recordingTime, setRecordingTime] = useState(0);
  const [streamPlatform, setStreamPlatform] = useState('none');
  const [quality, setQuality] = useState('1080p');
  const [captureMode, setCaptureMode] = useState('mixed-reality');
  const mediaRecorderRef = useRef(null);

  const recordingModes = [
    {
      id: 'mixed-reality',
      name: 'Mixed Reality',
      icon: 'üé≠',
      description: 'Show yourself in VR with green screen'
    },
    {
      id: 'first-person',
      name: 'First Person',
      icon: 'üëÅÔ∏è',
      description: 'Record exactly what you see'
    },
    {
      id: '360-video',
      name: '360¬∞ Video',
      icon: 'üåê',
      description: 'Immersive 360¬∞ capture'
    },
    {
      id: 'spectator',
      name: 'Spectator View',
      icon: 'üìπ',
      description: 'Third-person camera angle'
    }
  ];

  const streamPlatforms = [
    { id: 'twitch', name: 'Twitch', icon: 'üíú', color: '#9146FF' },
    { id: 'youtube', name: 'YouTube', icon: '‚ñ∂Ô∏è', color: '#FF0000' },
    { id: 'discord', name: 'Discord', icon: 'üí¨', color: '#5865F2' },
    { id: 'custom', name: 'Custom RTMP', icon: 'üîó', color: '#667eea' }
  ];

  const startRecording = async () => {
    try {
      // Request display capture with high quality
      const stream = await navigator.mediaDevices.getDisplayMedia({
        video: {
          width: { ideal: quality === '4K' ? 3840 : quality === '1440p' ? 2560 : 1920 },
          height: { ideal: quality === '4K' ? 2160 : quality === '1440p' ? 1440 : 1080 },
          frameRate: { ideal: 60 }
        },
        audio: true
      });

      // Create MediaRecorder
      const options = { mimeType: 'video/webm;codecs=vp9' };
      mediaRecorderRef.current = new MediaRecorder(stream, options);

      const chunks = [];
      mediaRecorderRef.current.ondataavailable = (e) => {
        if (e.data.size > 0) {
          chunks.push(e.data);
        }
      };

      mediaRecorderRef.current.onstop = async () => {
        const blob = new Blob(chunks, { type: 'video/webm' });
        const suggestedName = `vr-recording-${Date.now()}.webm`;
        await saveFileWithDialog(blob, suggestedName, { types: [FILE_TYPES.VIDEO] });
      };

      mediaRecorderRef.current.start();
      setIsRecording(true);

      // Update timer
      const interval = setInterval(() => {
        setRecordingTime(prev => prev + 1);
      }, 1000);

      // Store interval for cleanup
      mediaRecorderRef.current.interval = interval;
    } catch (error) {
      console.error('Recording error:', error);
      alert('Failed to start recording. Make sure you have screen capture permissions.');
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      clearInterval(mediaRecorderRef.current.interval);
      
      // Stop all tracks
      mediaRecorderRef.current.stream.getTracks().forEach(track => track.stop());
      
      setIsRecording(false);
      setRecordingTime(0);
    }
  };

  const startStreaming = async () => {
    if (streamPlatform === 'none') {
      alert('Please select a streaming platform first!');
      return;
    }

    // RTMP streaming can be implemented here
    alert(`Starting stream to ${streamPlatform}...\n\nYou'll need to:\n1. Get your stream key from ${streamPlatform}\n2. Enter it in settings\n3. Start streaming`);
    setIsStreaming(true);
  };

  const stopStreaming = () => {
    setIsStreaming(false);
  };

  const formatTime = (seconds) => {
    const hrs = Math.floor(seconds / 3600);
    const mins = Math.floor((seconds % 3600) / 60);
    const secs = seconds % 60;
    return `${hrs.toString().padStart(2, '0')}:${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div style={{
      background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a2e 100%)',
      minHeight: '100vh',
      padding: '40px 20px',
      color: 'white'
