    setTimeout(() => {
      alert(`Subscribed to ${creator.name} for $${creator.fee}/month!`);
      setSelectedCreators(prev => [...prev, creator.id]);
      setProcessing(false);
    }, 2000);
  };

  const handleUnsubscribe = async (creatorId) => {
    setProcessing(true);
    setTimeout(() => {
      alert('Unsubscribed successfully');
      setSelectedCreators(prev => prev.filter(id => id !== creatorId));
      setProcessing(false);
    }, 1000);
  };

  return (
    <div style={{
      padding: '40px',
      maxWidth: '1400px',
      margin: '0 auto',
      background: 'linear-gradient(135deg, #1e3c72 0%, #2a5298 100%)',
      borderRadius: '20px',
      color: '#fff',
      minHeight: '80vh'
    }}>
      {/* Exempt User Banner */}
      {isExempt && (
        <div style={{
          background: 'linear-gradient(135deg, #FFD700 0%, #FFA500 100%)',
          padding: '20px 40px',
          borderRadius: '15px',
          marginBottom: '30px',
          textAlign: 'center',
          color: '#000',
          fontWeight: '700',
          fontSize: '1.2rem',
          boxShadow: '0 6px 20px rgba(255, 215, 0, 0.4)'
        }}>
          üëë VIP ACCESS - You have FREE access to ALL content and ALL creators! üëë
        </div>
      )}

      {/* Header */}
      <div style={{ textAlign: 'center', marginBottom: '40px' }}>
        <h1 style={{
          fontSize: '2.8rem',
          fontWeight: '900',
          marginBottom: '10px',
          background: 'linear-gradient(135deg, #00d4ff 0%, #0099ff 100%)',
          WebkitBackgroundClip: 'text',
          WebkitTextFillColor: 'transparent'
        }}>
