        .tier-card {
          background: white;
          border-radius: 20px;
          padding: 36px 28px;
          box-shadow: 0 10px 40px rgba(0,0,0,0.15);
          position: relative;
          transition: transform 0.3s, box-shadow 0.3s;
          display: flex;
          flex-direction: column;
