    }), { status: 200, headers: { 'Content-Type': 'application/json' } });
  }

  // Store suggestion
  const { data: suggestionRecord, error: dbError } = await supabase
    .from('suggestions')
    .insert({
      user_id: userId,
      email: email,
      tier: tier,
      suggestion: sanitizeInput(suggestion),
