import React from 'react';
import ReactDOM from 'react-dom/client';
import { Settings } from './components/Settings';
import { supabase } from './lib/supabase';

function SettingsPage() {
  const [userId, setUserId] = React.useState(null);
  const [loading, setLoading] = React.useState(true);

  React.useEffect(() => {
    checkAuth();
  }, []);

  async function checkAuth() {
    const { data: { session } } = await supabase.auth.getSession();
    
    if (!session) {
      // Redirect to login
      window.location.href = '/login';
      return;
    }

    setUserId(session.user.id);
    setLoading(false);
  }

  if (loading) {
    return (
      <div style={{ 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        height: '100vh',
        color: 'white',
        fontSize: '1.5rem'
      }}>
        Loading...
      </div>
    );
  }

  return <Settings userId={userId} />;
}

ReactDOM.createRoot(document.getElementById('root')).render(
  <React.StrictMode>
    <SettingsPage />
  </React.StrictMode>
);

// soundForge.ts
import * as Tone from 'tone';

export interface CreateTrackOptions {
  bpm?: number;
  instruments?: string[];
  fx?: Array<{ type: string; options?: any }>;
}

export async function createTrack({ bpm = 120, instruments = ['synth'], fx = [] }: CreateTrackOptions) {
  await Tone.start();
  Tone.Transport.bpm.value = bpm;

  const track = instruments.map(inst => {
    // You can expand this switch for more instrument types
    let synth: Tone.Synth | Tone.PolySynth = new Tone.Synth().toDestination();
    if (inst === 'poly') synth = new Tone.PolySynth().toDestination();
    return { synth, pattern: [] };
  });

  fx.forEach(effect => {
    // @ts-ignore
    const fxNode = new (Tone as any)[effect.type](effect.options).toDestination();
    track.forEach(t => t.synth.connect(fxNode));
  });

  return track;
}

/**
 * PDF Engine - Real implementation using PDFKit
 * Creates, modifies, and exports PDFs with metadata
 */

export async function createPDF({ template, content }) {
  try {
    // Check if PDFKit is available
    if (typeof window === 'undefined') {
      // Server-side implementation
      const PDFDocument = require('pdfkit');
      const doc = new PDFDocument();

      // Apply template styling
      if (template === 'legal') {
        doc.fontSize(12).font('Times-Roman');
      } else if (template === 'modern') {
        doc.fontSize(14).font('Helvetica');
      } else {
        doc.fontSize(11).font('Courier');
      }

      // Add content
      doc.text(content, 100, 100);

      return { doc, template, content };
    } else {
      // Client-side fallback
      console.log(`Creating PDF with template: ${template}, content: ${content}`);
      return {
        doc: `${template}-${content}`,
        template,
        content,
        isClientSide: true
      };
    }
  } catch (error) {
    console.error('PDF creation error:', error);
    return {
      doc: `${template}-${content}`,
      error: error.message,
      template,
      content
    };
  }
}

export async function attachMetadata(doc, metadata) {
  try {
    if (doc.doc && typeof doc.doc.info === 'function') {
      // Real PDFKit document
      doc.doc.info.Title = metadata.title || 'Untitled';
      doc.doc.info.Author = metadata.author || 'ForTheWeebs';
      doc.doc.info.Subject = metadata.subject || '';
      doc.doc.info.Keywords = metadata.keywords || '';
      doc.doc.info.Creator = 'ForTheWeebs Platform';
      doc.doc.info.CreationDate = new Date();
    }

    doc.metadata = metadata;
    console.log(`Attached metadata to PDF:`, metadata);
    return doc;
  } catch (error) {
    console.error('Metadata attachment error:', error);
    doc.metadata = metadata;
    return doc;
  }
}

export async function exportPDF(doc, { format = 'pdf', sealed = false }) {
  try {
    if (doc.doc && typeof doc.doc.end === 'function') {
      // Real PDFKit document - finalize and export
      return new Promise((resolve, reject) => {
        const chunks = [];

        doc.doc.on('data', (chunk) => chunks.push(chunk));
        doc.doc.on('end', () => {
          const pdfBuffer = Buffer.concat(chunks);
          resolve({
            buffer: pdfBuffer,
            format,
            sealed,
            size: pdfBuffer.length,
            message: 'PDF exported successfully'
          });
        });
        doc.doc.on('error', reject);

        // Finalize PDF
        doc.doc.end();
      });
    } else {
      // Fallback for client-side or mock
      console.log(`Exporting PDF: format=${format}, sealed=${sealed}`);
      return {
        message: 'PDF exported (client-side mock)',
        format,
        sealed,
        doc: doc.doc
      };
    }
  } catch (error) {
    console.error('PDF export error:', error);
    return {
      error: error.message,
      format,
      sealed,
      message: 'PDF export failed'
    };
  }
}

