// memory.js - Memory monitoring and heap snapshot
const { checkMemory, writeArtifact } = require('./server-safety');

// Take heap snapshot (lightweight version)
async function takeHeapSnapshot() {
  const memory = checkMemory();
  const snapshot = {
    timestamp: new Date().toISOString(),
    memory,
    uptime: process.uptime(),
    platform: process.platform,
    nodeVersion: process.version,
  };
  
  await writeArtifact('heapSnapshot', snapshot);
  return snapshot;
}

// Monitor memory periodically
let memoryMonitorTimer = null;
function startMemoryMonitor(intervalMs = 60000) {
  console.log('[Memory] Starting memory monitor...');
  
  memoryMonitorTimer = setInterval(async () => {
    const memory = checkMemory();
    
    if (!memory.isHealthy) {
      console.warn(`[Memory] WARNING: Heap usage ${memory.heapUsedMB}MB exceeds threshold ${memory.threshold}MB`);
      await takeHeapSnapshot();
    }
  }, intervalMs);
  
  // Cleanup on exit
  process.on('SIGTERM', () => { if (memoryMonitorTimer) clearInterval(memoryMonitorTimer); });
  process.on('SIGINT', () => { if (memoryMonitorTimer) clearInterval(memoryMonitorTimer); });
}

module.exports = {
  takeHeapSnapshot,
  startMemoryMonitor,
};

// utils/networkProtection.js - IP allowlist, CSP, HSTS, WAF
const { writeArtifact } = require('./server-safety');

// Admin IP allowlist
const ADMIN_IPS = (process.env.ADMIN_IPS || '').split(',').filter(Boolean);

function ipAllowlist(req, res, next) {
  const clientIp = req.ip || req.connection.remoteAddress;
  
  if (ADMIN_IPS.length === 0) {
    // No allowlist configured - allow all (dev mode)
    console.warn('[IPAllowlist] No ADMIN_IPS configured - allowing all IPs');
    return next();
  }
  
  if (!ADMIN_IPS.includes(clientIp)) {
    writeArtifact('ipBlocked', {
      ip: clientIp,
      path: req.path,
      timestamp: new Date().toISOString(),
    });
    
    return res.status(403).json({ error: 'Forbidden: IP not authorized' });
  }
  
  next();
}

// CSP and security headers
function securityHeaders(req, res, next) {
  // Content Security Policy
  res.setHeader('Content-Security-Policy', [
    "default-src 'self'",
    "script-src 'self'",
    "style-src 'self' 'unsafe-inline'", // Allow inline styles for React
    "img-src 'self' data: https:",
    "font-src 'self' data:",
    "connect-src 'self' https://fortheweebs-production.up.railway.app https://iqipomerawkvtojbtvom.supabase.co wss://iqipomerawkvtojbtvom.supabase.co",
    "frame-ancestors 'none'",
    "base-uri 'self'",
    "form-action 'self'",
  ].join('; '));
  
  // HSTS (force HTTPS)
  res.setHeader('Strict-Transport-Security', 'max-age=31536000; includeSubDomains; preload');
  
  // Additional security headers
  res.setHeader('X-Frame-Options', 'DENY');
  res.setHeader('X-Content-Type-Options', 'nosniff');
  res.setHeader('X-XSS-Protection', '1; mode=block');
  res.setHeader('Referrer-Policy', 'strict-origin-when-cross-origin');
  res.setHeader('Permissions-Policy', 'geolocation=(), microphone=(), camera=()');
  
  next();
}

