  // SQL Injection
  /(\b(union|select|insert|update|delete|drop|create|alter|exec|execute|script|javascript|eval)\b)/i,
  /(['";]|--|\*|\/\*|\*\/)/,

  // XSS
  /(<script|<iframe|<object|<embed|<applet|javascript:|vbscript:|onload=|onerror=)/i,

  // Path traversal
  /(\.\.\/|\.\.\\|\/etc\/passwd|\/proc\/|\/sys\/)/i,

  // Command injection
  /(\||&|;|\$\(|`|>|<)/,
];

function wafFilter(req, res, next) {
  const inputs = [
    JSON.stringify(req.body),
    JSON.stringify(req.query),
    req.path,
  ];
  
  for (const input of inputs) {
    for (const pattern of ATTACK_PATTERNS) {
      if (pattern.test(input)) {
        writeArtifact('wafBlocked', {
          ip: req.ip,
          path: req.path,
          method: req.method,
          pattern: pattern.toString(),
          blocked: input.substring(0, 100),
          timestamp: new Date().toISOString(),
        });
        
        return res.status(400).json({ error: 'Malicious input detected' });
      }
    }
  }
  
  next();
}

module.exports = {
  ipAllowlist,
  securityHeaders,
  wafFilter,
};

// observability.js - Metrics collection for Prometheus
const { checkMemory } = require('./server-safety');

class MetricsCollector {
  requestCount = 0;
  errorCount = 0;
  requestDurations = [];
  startTime = Date.now();
  
  recordRequest(duration) {
    this.requestCount++;
    this.requestDurations.push(duration);
    // Keep only last 1000 durations to avoid memory bloat
    if (this.requestDurations.length > 1000) {
      this.requestDurations.shift();
    }
  }
  
  recordError() {
    this.errorCount++;
  }
  
  getMetrics() {
    const uptime = Math.floor((Date.now() - this.startTime) / 1000);
    const memory = checkMemory();
    
    // Calculate percentiles
    const sorted = [...this.requestDurations].sort((a, b) => a - b);
    const p50 = sorted[Math.floor(sorted.length * 0.5)] || 0;
    const p95 = sorted[Math.floor(sorted.length * 0.95)] || 0;
    const p99 = sorted[Math.floor(sorted.length * 0.99)] || 0;
    
    return {
      uptime,
      requests: {
        total: this.requestCount,
        errors: this.errorCount,
        errorRate: this.requestCount > 0 ? (this.errorCount / this.requestCount * 100).toFixed(2) : 0,
      },
      latency: {
        p50: Math.round(p50),
        p95: Math.round(p95),
        p99: Math.round(p99),
      },
      memory: {
        heapUsedMB: memory.heapUsedMB,
        heapTotalMB: memory.heapTotalMB,
        rssMB: memory.rssMB,
        isHealthy: memory.isHealthy,
      },
    };
  }
  
  // Prometheus format
  toPrometheus() {
    const metrics = this.getMetrics();
    return `# HELP app_uptime_seconds Application uptime in seconds
# TYPE app_uptime_seconds counter
app_uptime_seconds ${metrics.uptime}

# HELP http_requests_total Total HTTP requests
# TYPE http_requests_total counter
http_requests_total ${metrics.requests.total}

# HELP http_errors_total Total HTTP errors
# TYPE http_errors_total counter
http_errors_total ${metrics.requests.errors}

# HELP http_request_duration_ms HTTP request duration in milliseconds
# TYPE http_request_duration_ms summary
http_request_duration_ms{quantile="0.5"} ${metrics.latency.p50}
http_request_duration_ms{quantile="0.95"} ${metrics.latency.p95}
http_request_duration_ms{quantile="0.99"} ${metrics.latency.p99}

# HELP memory_heap_used_mb Memory heap used in MB
# TYPE memory_heap_used_mb gauge
memory_heap_used_mb ${metrics.memory.heapUsedMB}

# HELP memory_heap_total_mb Memory heap total in MB
# TYPE memory_heap_total_mb gauge
memory_heap_total_mb ${metrics.memory.heapTotalMB}

# HELP memory_rss_mb Memory RSS in MB
# TYPE memory_rss_mb gauge
memory_rss_mb ${metrics.memory.rssMB}
`;
  }
}

const metricsCollector = new MetricsCollector();

// Middleware to track requests
function metricsMiddleware(req, res, next) {
  const start = Date.now();
  
  res.on('finish', () => {
    const duration = Date.now() - start;
    metricsCollector.recordRequest(duration);
    
    if (res.statusCode >= 500) {
      metricsCollector.recordError();
    }
  });
  
  next();
}

module.exports = {
  metricsCollector,
  metricsMiddleware,
};

// utils/rateLimit.js - Rate limiting and IP reputation
const rateLimit = require('express-rate-limit');
const { writeArtifact } = require('./server-safety');

// Soft ban cache (IP -> expiry timestamp)
const softBans = new Map();

// Check if IP is soft-banned
function isSoftBanned(ip) {
  const banExpiry = softBans.get(ip);
  if (!banExpiry) return false;
  
  if (Date.now() > banExpiry) {
    softBans.delete(ip);
    return false;
  }
  
  return true;
}

// Apply soft ban
function softBan(ip, durationMs = 60 * 60 * 1000) {
  const expiry = Date.now() + durationMs;
  softBans.set(ip, expiry);
  
  writeArtifact('softBan', {
    ip,
    duration: durationMs,
    expiry: new Date(expiry).toISOString(),
  });
}

// Rate limiter for public endpoints
const publicLimiter = rateLimit({
  windowMs: 15 * 60 * 1000, // 15 minutes
  max: 100, // 100 requests per window
  message: 'Too many requests from this IP',
  standardHeaders: true,
  legacyHeaders: false,
});

// Rate limiter for suggestion endpoints (stricter)
const suggestionLimiter = rateLimit({
  windowMs: 60 * 60 * 1000, // 1 hour
  max: 10, // 10 suggestions per hour
  message: 'Too many suggestions from this IP',
  standardHeaders: true,
  legacyHeaders: false,
  handler: (req, res) => {
    // Soft ban on excessive suggestions
    softBan(req.ip, 60 * 60 * 1000);
    res.status(429).json({ error: 'Rate limit exceeded. Soft-banned for 1 hour.' });
  },
});

// Middleware to check soft bans
function checkSoftBan(req, res, next) {
  if (isSoftBanned(req.ip)) {
    return res.status(403).json({ error: 'IP temporarily banned due to excessive requests' });
  }
  next();
}

module.exports = {
  publicLimiter,
  suggestionLimiter,
  checkSoftBan,
  softBan,
  isSoftBanned,
};

// utils/security.js - HMAC authentication and request signing
const crypto = require('node:crypto');

const ADMIN_SECRET = process.env.BUGFIXER_TOKEN || 'not_configured';
const MAX_REQUEST_AGE_MS = 5 * 60 * 1000; // 5 minutes
const nonceCache = new Set();

// Generate HMAC signature
function generateHMAC(payload, timestamp, nonce) {
  const message = `${timestamp}:${nonce}:${JSON.stringify(payload)}`;
  return crypto.createHmac('sha256', ADMIN_SECRET).update(message).digest('hex');
}

// Verify HMAC signature (timing-safe)
function verifyHMAC(req, res, next) {
  const signature = req.headers['x-bugfixer-signature'];
  const timestamp = req.headers['x-bugfixer-timestamp'];
  const nonce = req.headers['x-bugfixer-nonce'];
  const token = req.headers['x-bugfixer-token'];
  
  // Check token first
  if (!token || token !== ADMIN_SECRET) {
    return res.status(401).json({ error: 'Invalid or missing token' });
  }
  
  // Check required headers
  if (!signature || !timestamp || !nonce) {
    return res.status(401).json({ error: 'Missing signature headers' });
  }
  
  // Check timestamp (prevent replay attacks)
  const requestTime = Number.parseInt(timestamp, 10);
  const now = Date.now();
  
  if (Number.isNaN(requestTime) || Math.abs(now - requestTime) > MAX_REQUEST_AGE_MS) {
    return res.status(401).json({ error: 'Request too old or invalid timestamp' });
  }
  
  // Check nonce (prevent duplicate requests)
  if (nonceCache.has(nonce)) {
    return res.status(401).json({ error: 'Nonce already used' });
  }
  
  // Compute expected signature
  const expectedSignature = generateHMAC(req.body || {}, timestamp, nonce);
  
  // Timing-safe comparison
  const isValid = crypto.timingSafeEqual(
    Buffer.from(signature, 'hex'),
    Buffer.from(expectedSignature, 'hex')
  );
  
  if (!isValid) {
    const { writeArtifact } = require('./server-safety');
    writeArtifact('tamperAttempt', {
      ip: req.ip,
      path: req.path,
      timestamp: new Date().toISOString(),
    });
    return res.status(401).json({ error: 'Invalid signature' });
  }
  
  // Store nonce (with TTL cleanup)
  nonceCache.add(nonce);
  setTimeout(() => nonceCache.delete(nonce), MAX_REQUEST_AGE_MS);
  
  next();
}

module.exports = {
  generateHMAC,
  verifyHMAC,
};

