/* eslint-disable no-unused-vars, @typescript-eslint/no-explicit-any */
/**
 * Apply Supabase RLS Policy Fixes
 * Fixes 406 errors by updating Row Level Security policies
 */

require('dotenv').config();
const { createClient } = require('@supabase/supabase-js');
const fs = require('node:fs');
const path = require('node:path');

const supabaseUrl = process.env.SUPABASE_URL;
const supabaseServiceKey = process.env.SUPABASE_SERVICE_KEY;

if (!supabaseUrl || !supabaseServiceKey) {
  console.error('‚ùå Missing SUPABASE_URL or SUPABASE_SERVICE_KEY in .env');
  process.exit(1);
}

const supabase = createClient(supabaseUrl, supabaseServiceKey);

async function applyRLSFixes() {
  console.log('üîß Applying RLS policy fixes to Supabase...\n');

  const sqlFile = path.join(__dirname, '../supabase/migrations/fix_rls_policies.sql');
  const sql = fs.readFileSync(sqlFile, 'utf8');

  // Split SQL into individual statements
  const statements = sql
    .split(';')
    .map(s => s.trim())
    .filter(s => s && !s.startsWith('--') && s !== '');

  console.log(`üìù Found ${statements.length} SQL statements to execute\n`);

  for (let i = 0; i < statements.length; i++) {
    const statement = statements[i];
    console.log(`[${i + 1}/${statements.length}] Executing...`);
    
    try {
      // Use raw SQL execution
      const { error } = await supabase.rpc('exec_sql', { 
        sql_query: statement + ';' 
      });

      if (error) {
        console.error(`   ‚ùå Error: ${error.message}`);
      } else {
        console.log(`   ‚úÖ Success`);
      }
    } catch (err) {
      console.error(`   ‚ùå Exception: ${err.message}`);
    }
  }

  console.log('\nüéâ RLS policy fixes applied!');
  console.log('\nüìã Next steps:');
  console.log('1. Go to Supabase Dashboard');
  console.log('2. Authentication > Policies');
  console.log('3. Verify "Users can read their own data" policy exists');
  console.log('4. Test the frontend - 406 errors should be gone!\n');
}

await applyRLSFixes();

/* eslint-disable @typescript-eslint/no-explicit-any, no-console */
// Tier API Endpoint for Fortheweebs
const express = require('express');
const router = express.Router();
const { createClient } = require('@supabase/supabase-js');

const supabase = createClient(
  process.env.SUPABASE_URL,
  process.env.SUPABASE_SERVICE_KEY
);

// POST /api/storefront/tier
router.post('/', async (req, res) => {
  try {
    const { userId, newTier } = req.body;
    
    if (!userId || !newTier) {
      return res.status(400).json({ error: 'Missing userId or newTier' });
    }

    // Update user tier in database
    const { error } = await supabase
      .from('profiles')
      .update({ tier: newTier, updated_at: new Date().toISOString() })
      .eq('user_id', userId)
      .select()
      .single();

    if (error) {
      console.error('Error updating tier:', error);
      return res.status(500).json({ error: 'Failed to update tier' });
    }

    res.json({ success: true, userId, newTier, message: 'Tier updated successfully.' });
  } catch (error) {
    console.error('Tier update error:', error);
    res.status(500).json({ error: 'Internal server error' });
  }
});

module.exports = router;

