  useEffect(() => {
    // Get initial session
    supabase.auth.getSession().then(({ data: { session } }) => {
      const user = session?.user ?? null;
      setUser(user);

      // Grant VIP access if applicable
      if (user) {
        grantVIPAccess(user);
      }

      setLoading(false);
    });

    // Listen for auth changes
    const { data: { subscription } } = supabase.auth.onAuthStateChange((_event, session) => {
      const user = session?.user ?? null;
      setUser(user);

      // Grant VIP access if applicable
      if (user) {
        grantVIPAccess(user);
      }

      setLoading(false);
    });

    return () => subscription.unsubscribe();
  }, []);

  const signup = async (email, password, displayName) => {
    const { data, error } = await supabase.auth.signUp({
      email,
      password,
      options: {
        data: {
          display_name: displayName
        }
      }
    });

    if (error) throw error;

    // Create user profile in database
    if (data.user) {
      // Check total user count for first 100 ELITE promotion
      const { count } = await supabase
        .from('users')
        .select('*', { count: 'exact', head: true });
      
      // First 100 users get ELITE tier automatically ($1000 value)
      const tier = (count !== null && count < 100) ? 'elite' : 'free';
      
      await supabase.from('users').insert([{
        id: data.user.id,
        email,
        display_name: displayName,
        tier,
        balance: 0,
        created_at: new Date().toISOString()
      }]);

      // If they got Elite, store it in localStorage
      if (tier === 'elite') {
        localStorage.setItem('userTier', 'ELITE');
        console.log(`üéâ User ${count + 1}/100 - ELITE tier granted ($1000 value)!`);
      }
    }

    return data;
  };

  const login = async (email, password) => {
    const { data, error } = await supabase.auth.signInWithPassword({
      email,
      password
    });

    if (error) throw error;

    // Grant VIP access if applicable
    if (data.user) {
      grantVIPAccess(data.user);
    }

    return data;
  };

  const loginWithGoogle = async () => {
    const { data, error } = await supabase.auth.signInWithOAuth({
      provider: 'google',
      options: {
        redirectTo: `${globalThis.location.origin}/auth/callback`
      }
    });

    if (error) throw error;

    // Grant VIP access if applicable (OAuth will be checked on callback)
    if (data.user) {
      grantVIPAccess(data.user);
    }

    return data;
  };

  const logout = async () => {
    const { error } = await supabase.auth.signOut();
    if (error) throw error;
  };

  const resetPassword = async (email) => {
    const { error } = await supabase.auth.resetPasswordForEmail(email, {
      redirectTo: `${globalThis.location.origin}/reset-password`
    });

    if (error) throw error;
  };

  const value = React.useMemo(() => ({
    user,
    loading,
    signup,
    login,
    loginWithGoogle,
    logout,
    resetPassword
  }), [user, loading]);

  return (
    <AuthContext.Provider value={value}>
      {!loading && children}
    </AuthContext.Provider>
  );
}

AuthProviderComponent.propTypes = {
  children: PropTypes.node
};

export const AuthProvider = AuthProviderComponent;

// Login Form Component
function LoginFormComponent({ onSuccess, onSwitchToSignup }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { login, loginWithGoogle } = useAuth();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      await login(email, password);
      if (onSuccess) onSuccess();
    } catch (err) {
      setError(err.message || 'Failed to login');
    } finally {
      setLoading(false);
    }
  };

  const handleGoogleLogin = async () => {
    setError('');
    setLoading(true);

    try {
      await loginWithGoogle();
      // OAuth will redirect, so no need to call onSuccess
    } catch (err) {
      setError(err.message || 'Failed to login with Google');
      setLoading(false);
    }
  };

  return (
    <div className="auth-form">
      <h2>Welcome Back!</h2>
      {error && <div className="auth-error">{error}</div>}

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="login-email">Email</label>
          <input
            id="login-email"
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            placeholder="your@email.com"
          />
        </div>

        <div className="form-group">
          <label htmlFor="login-password">Password</label>
          <input
            id="login-password"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
          />
        </div>

        <button type="submit" className="auth-button" disabled={loading}>
          {loading ? 'Logging in...' : 'Login'}
        </button>
      </form>

      <div className="auth-divider">
        <span>OR</span>
      </div>

      <button
        className="google-auth-button"
        onClick={handleGoogleLogin}
        disabled={loading}
      >
        <span className="google-icon">üîê</span>Continue with Google
      </button>

      <div className="auth-footer">
        <p>
          Don't have an account?{' '}
          <button onClick={onSwitchToSignup} className="auth-link">
            Sign up
          </button>
        </p>
      </div>
    </div>
  );
}

LoginFormComponent.propTypes = {
  onSuccess: PropTypes.func,
  onSwitchToSignup: PropTypes.func
};

export const LoginForm = LoginFormComponent;

// Signup Form Component
