          }} onClick={(e) => e.stopPropagation()} onKeyDown={(e) => e.stopPropagation()} role="dialog" tabIndex={-1}>
            <div style={{
              position: 'sticky',
              top: 0,
              background: '#1a1a2e',
              padding: '20px',
              borderBottom: '1px solid rgba(255,255,255,0.1)',
