        body: JSON.stringify({ userId, tier, email })
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to create subscription');
      }

      // Confirm payment with Stripe
      const { error } = await stripe.confirmCardPayment(data.clientSecret);

      if (error) {
        alert(`‚ùå Subscription failed: ${error.message}`);
        return;
      }

      alert(`üéâ Subscription activated!\n\nTier: ${tier}\nYou now have full access to ${tier === 'adult' ? 'adult content' : tier === 'unlimited' ? 'unlimited features' : 'super admin perks'}!`);

      // Reload to show updated tier
      setTimeout(() => window.location.reload(), 1500);

    } catch (error) {
      console.error('Subscription error:', error);
      alert(`‚ùå Subscription failed: ${error.message}\n\nPlease try again.`);
    } finally {
      setProcessing(false);
    }
  };

  return (
    <div className="premium-subscription">
      <div className="premium-header">
        <h1>ÔøΩ Unlock Tools & Features</h1>
        <p className="premium-subtitle">
          Earn money on ForTheWeebs, then unlock tools from your balance. No credit card needed!
        </p>
        <div className="user-balance">
          üí∞ Your Balance: <strong>${userBalance.toFixed(2)}</strong>
        </div>
      </div>

      {/* TOOL UNLOCKS - Individual purchases */}
      <div className="tool-unlocks-section">
        <h2>üõ†Ô∏è Unlock Tools Individually</h2>
        <p className="section-subtitle">Pay once, keep forever. Use your earnings!</p>
        <div className="tool-unlocks-grid">
          {toolUnlocks.map(tool => (
