import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './SubscriptionSuccess.css';

export const SubscriptionSuccess = () => {

  const navigate = useNavigate();
  const [countdown, setCountdown] = useState(5);

  useEffect(() => {
    const timer = setInterval(() => {
      setCountdown(prev => {
        if (prev <= 1) {
          navigate('/dashboard');
          return 0;
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, [navigate]);

  return (
    <div className="subscription-success">
      <div className="success-card">
        <div className="success-icon">‚úÖ</div>
        <h1>Subscription Activated!</h1>
        <p className="success-message">
          Welcome to the $1,000 Lifetime VIP Tier! üéâ
        </p>
        
        <div className="benefits-list">
          <h2>Your Premium Benefits:</h2>
          <ul>
            <li>‚ú® Access to all exclusive subscriber-only content</li>
            <li>üë• Create 3 additional creator profiles</li>
            <li>üí∞ 0% platform fees - Keep 100% of earnings (only Stripe ~2.9%)</li>
            <li>üé® CGI effects and AR filters for posts, calls, and streams</li>
            <li>üîê Admin superpowers and moderation tools</li>
            <li>üéØ Priority support and feature access</li>
            <li>üíé VIP badge across all platforms</li>
            <li>‚ôæÔ∏è Lifetime access - no recurring payments</li>
          </ul>
        </div>

        <div className="countdown">
          Redirecting to dashboard in {countdown} seconds...
        </div>

        <button onClick={() => navigate('/dashboard')} className="dashboard-btn">
          Go to Dashboard Now ‚Üí
        </button>
      </div>
    </div>
  );
};

export const SubscriptionCanceled = () => {
